INSERT INTO `broadcast_text` VALUES (1985, 'Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon.', '', 0, 0, 0, 0, 0, 0, 0, 33, 1, 0, 0, 0, 26124);
