INSERT INTO `broadcast_text` VALUES (10164, 'Hello!  And welcome!  I\'m afraid my booth is not open... many repairs are needed...$B$BBut I am still bound by blood oath to put a smile on your face.  So please, smile, or I must cut off my hand and consume no living flesh until it regenerates...', '', 1, 6, 0, 0, 1000, 0, 0, 0, 1, 0, 0, 0, 26124);
