INSERT INTO `broadcast_text` VALUES (41593, '%s gasps for air and shivers in the cold.', '%s gasps for air and shivers in the cold.', 0, 0, 0, 0, 0, 0, 20, 0, 1, 0, 0, 0, 26124);
