INSERT INTO `broadcast_text` VALUES (50040, 'That\'s some nice work there!', '', 0, 0, 0, 0, 0, 0, 432, 1, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (50041, 'Hmm... interesting choice of parts.', '', 0, 0, 0, 0, 0, 0, 432, 1, 0, 0, 0, 0, 26124);
