INSERT INTO `broadcast_text` VALUES (50987, 'My ssssserpents, dey already ssssslither beneath every rock an\' shrub of Ssssstranglethorn.', '', 0, 0, 0, 0, 0, 0, 397, 0, 1, 0, 24327, 0, 26124);
