INSERT INTO `broadcast_text` VALUES (60490, 'Look, look! Weird, bumpy carrot!', '', 0, 0, 0, 0, 0, 0, 0, 0, 1, 16275, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (60491, 'We so lucky.', '', 0, 0, 0, 0, 0, 0, 0, 0, 1, 16275, 0, 0, 26124);
