INSERT INTO `broadcast_text` VALUES (61205, 'At least we know that Anduin is alive. Well done, $n. Now go speak to Elder Lusshan. He has been asking for you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 1, 16544, 0, 0, 26124);
