INSERT INTO `broadcast_text` VALUES (67320, 'You have done much today, $n. Rest and return tomorrow.', 'You have done much today, $n. Rest and return tomorrow.', 0, 0, 0, 0, 0, 0, 0, 0, 1, 18593, 0, 0, 26124);
