INSERT INTO `broadcast_text` VALUES (71267, 'Here comes the final assault. For Domination Point! BUTCHER THEM ALL!', '', 0, 0, 0, 0, 0, 0, 5, 0, 1, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (71268, 'Weapons up, you magnificent monsters! They\'re about to charge.', '', 0, 0, 0, 0, 0, 0, 5, 0, 1, 0, 0, 0, 26124);
