INSERT INTO `broadcast_text` VALUES (71529, 'How could the mogu be capable of such atrocity? Thank goodness you arrived when you did.', 'How could the mogu be capable of such atrocity? Thank goodness you arrived when you did.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 923, 0, 0, 26124);
