INSERT INTO `broadcast_text` VALUES (73744, '$gHe\'s:She\'s; one of our veteran Rank 9 brawlers here in the Brawlpub. It\'s $n!', '$gHe\'s:She\'s; one of our veteran Rank 9 brawlers here in Brawl\'gar. It\'s $n!', 0, 0, 0, 0, 0, 0, 22, 0, 1, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (73745, 'Nothing can stop our Rank 10 champion brawler, $N!', 'Nothing can stop our Rank 10 champion brawler, $N!', 0, 0, 0, 0, 0, 0, 22, 0, 1, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (73746, 'To do what is right, no matter the cost. That is wisdom.', '', 0, 0, 0, 0, 0, 0, 1, 0, 7, 0, 37450, 0, 26124);
INSERT INTO `broadcast_text` VALUES (73747, 'When is it ever wise not to act for the greater good?', '', 0, 0, 0, 0, 0, 0, 1, 0, 7, 0, 37451, 0, 26124);
INSERT INTO `broadcast_text` VALUES (73748, '', 'Ah, the dragon child and the hero. Together you will achieve great things.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37364, 26124);
INSERT INTO `broadcast_text` VALUES (73749, '', 'Tell me, dragon child: What is the nature of wisdom?', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37365, 26124);
INSERT INTO `broadcast_text` VALUES (73750, '', 'Spoken like a true child of the Aspects. Always seeking to change the world.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37366, 26124);
INSERT INTO `broadcast_text` VALUES (73751, '', 'But true wisdom comes from KNOWING what is right. And sometimes, DOING nothing at all.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37367, 26124);
INSERT INTO `broadcast_text` VALUES (73752, '', 'To learn THAT is to learn true wisdom.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37368, 26124);
INSERT INTO `broadcast_text` VALUES (73753, '', 'Come forward! I will give you my blessing.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37369, 26124);
INSERT INTO `broadcast_text` VALUES (73754, '', 'My challenge, if you accept it, will test your cunning and footwork even in the midst of life-threatening danger.', 0, 0, 0, 0, 0, 0, 2, 0, 1, 0, 0, 37524, 26124);
