INSERT INTO `broadcast_text` VALUES (86786, '', 'The battle is finished. We\'ve pushed the Iron Horde army back to the gates.', 0, 0, 0, 0, 0, 0, 0, 0, 4, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (86788, 'Lay down your weapons, Grommash! Look around you. You are lost.', '', 0, 0, 0, 0, 0, 0, 603, 0, 4, 0, 48917, 0, 26124);
