INSERT INTO `broadcast_text` VALUES (88973, '', 'I won\'t go down easily.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88974, '', 'For my grandfather.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88975, '', 'I will earn my place.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88976, '', 'You will not break us.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
