INSERT INTO `broadcast_text` VALUES (88978, '', 'You cannot stand against the Light!', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88979, '', 'We fight as one.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88980, '', 'I will never be broken.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
