INSERT INTO `broadcast_text` VALUES (88985, '', 'I\'ve killed one!', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88986, '', 'We will prevail!', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88987, '', 'Our defenses hold.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88988, '', 'I stand for another day.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88989, '', 'May the Light protect us.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88990, '', 'I will stand by your side, commander.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88991, '', 'May the Light bless you, commander.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
INSERT INTO `broadcast_text` VALUES (88992, '', 'Lead the way.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
