INSERT INTO `broadcast_text` VALUES (96480, 'Finally we get some help around here. That is, if you\'re willing to put aside past differences, for now.', '', 603, 0, 0, 15, 0, 0, 0, 0, 0, 0, 0, 0, 26124);
