INSERT INTO `broadcast_text` VALUES (104904, 'The draenei sought to devise a ritual that could turn the naaru back to the Light. To aid their quest, I called together many priesthoods to work in unison.', '', 0, 0, 0, 0, 0, 0, 1, 0, 4, 0, 63413, 0, 26124);
