INSERT INTO `broadcast_text` VALUES (112269, 'If the ward protecting the Fangs fails, we\'ll have more to worry about than a few cuts and bruises. I\'ll guard the weapon, even if you won\'t!', '', 0, 0, 0, 0, 0, 0, 603, 0, 0, 0, 62266, 0, 26124);
