INSERT INTO `broadcast_text` VALUES (112641, 'This ain\'t like anything I\'ve felt before.', 'This ain\'t like anything I\'ve felt before.', 0, 0, 0, 0, 0, 0, 396, 0, 0, 0, 0, 0, 26124);
