INSERT INTO `broadcast_text_locale` VALUES (3, 'enUS', 'Company.. HALT!  ... Fall IN!', 'Company.. HALT!  ... Fall IN!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (4, 'enUS', 'Forward.. MARCH!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (5, 'enUS', 'Come, let us engage in fisticuffs, like civilized folk.', 'Come, let us engage in fisticuffs, like civilized folk.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (7, 'enUS', 'Thank you very much!', 'Thank you very much!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9, 'enUS', 'Follow me, $n. I\'ll take you to the Defias hideout. But you better protect me or I am as good as dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10, 'enUS', 'The entrance is hidden here in Moonbrook.  Keep your eyes peeled for thieves.  They want me dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11, 'enUS', 'You can go tell Stoutmantle this is where the Defias Gang is holed up, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13, 'enUS', 'Work scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14, 'enUS', 'Grrrr! Idiots!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (15, 'enUS', 'Good for nothing Kobolds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (16, 'enUS', 'All right people, look alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (17, 'enUS', 'All right, you guys! Time to get up', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (19, 'enUS', 'Hey, follow me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (22, 'enUS', 'Another herd brought in successfully!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (23, 'enUS', 'Well I\'m here.  And I\'m ready to rejoin...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (24, 'enUS', 'You\'re not joining anyone, Thorsen..', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (25, 'enUS', 'My wounds are grave.  Forgive my slow pace but my injuries won\'t allow me to walk any faster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (26, 'enUS', 'Ah, fresh air at last! I need a moment to rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (27, 'enUS', 'The Blackrock infestation is thick in these parts.  I will do my best to keep the pace.  Let\'s go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (28, 'enUS', 'So close to blessed Lakeshire.  By the Light, let my legs carry me a bit further!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (29, 'enUS', 'Marshal Marris, sir.  Corporal Keeshan of the 12th Sabre Regiment returned from battle and reporting for duty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (30, 'enUS', 'Brave adventurer, thank you for rescuing me! I am sure Marshal Marris will reward your kind deed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (33, 'enUS', 'Antonio Perelli\'s miracle elixirs for sale!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (35, 'enUS', 'Come one! Come all! Perelli\'s elixirs will dazzle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (36, 'enUS', 'Stonemasons. . .errr. . Defias be warned:  The rusty anchor sinks tonight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (37, 'enUS', 'The boss wants all hands on high alert.  The rusty anchor sinks tonight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (38, 'enUS', 'I\'m here for the gold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (39, 'enUS', 'Everywhere I look I see thieves.  We, The People\'s Miltia, must return this land to the farmers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (40, 'enUS', 'The farms must be liberated.  Stormwind may have deserted these people, but I\'ll be damned if I will.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (41, 'enUS', '', 'Defias activity reported on the costal horizon.  All clear on Sentinel Hill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (42, 'enUS', 'Lots of gnoll activity reported in the Gold Coast Quarry and the Jangolode Mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (43, 'enUS', 'All clear on Sentinel Hill, Lord Stoutmantle.  Heavy Defias movement reported in Moonbrook, however.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (44, 'enUS', 'Stormwind\'s desertion shall not be forgiven by the people of Westfall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (45, 'enUS', 'Nothing biting. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (46, 'enUS', 'Here fishy fishy. . . come to papa.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (47, 'enUS', 'Pull up a stool and have a drink!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (48, 'enUS', 'The bottles are dusty but the liquor goes down clean!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (49, 'enUS', '', 'Hopefully the reinforcements from Stormwind show up soon. . . .', 26124);
INSERT INTO `broadcast_text_locale` VALUES (50, 'enUS', 'Come on boys, we\'ve got to get this bridge rebuilt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (51, 'enUS', 'Where are those reinforcements from Stormwind?  Heavy Blackrock activity has been reported in the East.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (52, 'enUS', 'Still no sign of the 2nd Battalion from Stormwind.  Reports are coming in of Shadowhide movement in the Northeast.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53, 'enUS', 'Marshal Marris expects the reserve from Stormwind any time now. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54, 'enUS', '', 'Don\'t worry, Old Blanchy, we\'ll get you something to eat soon. . .', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55, 'enUS', '', 'I never thought the day would come that I\'d leave my homeland of Westfall. . . .', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56, 'enUS', 'Can\'t believe the forsaken wagon broke down.  Ain\'t no luck to be had in this land. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57, 'enUS', 'We\'ll be out of here just as soon as I get this wagon fixed. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58, 'enUS', 'Now how am I supposed to get those mechanical wretches out of my fields?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59, 'enUS', 'A bunch of thugs and thieves aren\'t going to get me to leave my land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (60, 'enUS', 'They might have run all the other farmers off, but the Saldean\'s will never leave Westfall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (61, 'enUS', '', 'Stay on the roads.  The forest is overrun with undead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62, 'enUS', '', 'Fear not.  The Night Watch will rid the land of this foul taint soon enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63, 'enUS', 'Undead are crawling all over the land.  Where is the Stormwind Army?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (64, 'enUS', 'The Night Watch alone cannot protect us forever.  We need the backing of the Stormwind Army.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (65, 'enUS', 'We must not give into the evil which has darkened our land.  The Night Watch must prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (66, 'enUS', 'The forest is crawling with worgen and ghouls.  Something must be done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (67, 'enUS', 'We need better representation from Stormwind.  Our homes are falling to the undead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (68, 'enUS', 'The Council of Duskwood must take action.  Evil lingers in the air.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (69, 'enUS', 'The people of Darkshire expect more from the Council.  We cannot let them suffer from this unholy wrath which plagues us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (70, 'enUS', 'Constant bickering will get us nowhere.  We need to take action.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (71, 'enUS', 'The news from Stormwind does not bode well. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (72, 'enUS', 'Our cause falls on deaf ears beyond the thick, stone walls of Stormwind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (73, 'enUS', '', 'I sense evil brewing. . . .', 26124);
INSERT INTO `broadcast_text_locale` VALUES (74, 'enUS', '', 'The stars speak to me.  They whisper of the horrors waiting in the night.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (75, 'enUS', '', 'The future holds much death and uncertainty, not unlike the past.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (76, 'enUS', '', 'The spirits whisper in my ear.  They are restless and full of angst.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (77, 'enUS', 'The stars will be in perfect form tonight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79, 'enUS', 'There is something greater than magic at work in the night sky.  One day they\'ll listen to me. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80, 'enUS', 'Psssst.  We just got some Moonshine in. . . .', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81, 'enUS', 'Step right up and wet your whistle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82, 'enUS', 'This dark taint is downright bad for business.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (83, 'enUS', 'Welcome to the Scarlet Raven.  It\'s best to stay indoors these days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84, 'enUS', 'If your glass is full may it be again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (85, 'enUS', 'Welcome to the Lion\'s Pride Inn.  Make yourself at home!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (86, 'enUS', 'So much to do, so much to do!  Where does the time go?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (87, 'enUS', 'Now where was that book on Night Elf customs I was looking for...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (88, 'enUS', 'Oh my!  Look at the state of these books!  Who put them back without alphabetizing them??', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (89, 'enUS', 'Rouse and to arms, citizens of Darkshire! An abomination of the undead approaches along the road!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (90, 'enUS', 'The abomination has overrun the Night Watch camp! Quickly, we must intercept it before it reaches town!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (91, 'enUS', 'It isn\'t enough! Defenders, gather in the center of town. Together we will stand against the undead monster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (92, 'enUS', 'The abomination has come! Forward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (93, 'enUS', 'The beast is slain! All\'s well in Darkshire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (94, 'enUS', 'I didn\'t know he was getting married! I didn\'t even know he was dating! That\'s terrible! He can\'t work nights anymore!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (95, 'enUS', 'Fine! You\'ve outreasoned me, you masters of Engineering! Put the door wherever you want!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (96, 'enUS', 'That\'s right, we\'re stupid. Stupid like a Fox!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (97, 'enUS', 'Where\'s My Bacon?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (98, 'enUS', '', 'You\'ve ruined your own lands, you\'ll not ruin mine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99, 'enUS', '$n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (100, 'enUS', 'Thanks for saving me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (101, 'enUS', 'Now you die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102, 'enUS', 'Time for my patrol.  I\'ll be back shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103, 'enUS', 'Now that the coast is clear...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104, 'enUS', 'Who goes there??', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105, 'enUS', 'I\'ve been gone a long time...I better get back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (106, 'enUS', 'Private Thorsen returning from patrol.  Nothing new to report - it\'s pretty quiet out there...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107, 'enUS', '', 'It\'ll take more than a rotting corpse to stop us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108, 'enUS', '', 'Very good.  You are relieved, private.  Rest, but don\'t get too comfortable.  Kurzen won\'t leave us alone forever.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109, 'enUS', 'What is going on here?  Leave that powder alone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (110, 'enUS', 'Aber?  Is that you...?  Oh...I\'m so hungry, Aber!  SO HUNGRY!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (111, 'enUS', 'Wait...you are not my husband.  But he must have sent you.  And you...look..delicious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (112, 'enUS', 'Let\'s see if I can hit that bullseye this time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (113, 'enUS', 'Looks like I need more practice!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (114, 'enUS', '\'Bout time fer me to make mah rounds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (115, 'enUS', 'Ah I am too busy for a drink now lad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (117, 'enUS', 'Ach! Toss me a cold frosty, Belm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (118, 'enUS', 'Bagh! Back to work for me! Thank you kindly Belm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (120, 'enUS', 'Aye there you go lad, your favorite brew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (121, 'enUS', 'Aye, tell your friend there Tongus if he dont take care of his tab here, i\'ll be making an appearance with old Steelhead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (122, 'enUS', 'Belm says pay your bill or he\'ll break your face.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (123, 'enUS', 'Hoha! I\'d welcome the challenge from old weakbelly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (124, 'enUS', 'I\'ll take whatever ya got cookin\'! And throw in a bottle to wash it down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (125, 'enUS', 'Hey there, Belm!  Give me a mug of Thunder Ale, and one for my good friend $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126, 'enUS', 'How goes the barrel watching...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127, 'enUS', 'Ah, that sure does hit the spot!  I think I\'ll get myself a couple more...can you watch these barrels for me, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128, 'enUS', 'Well, back to business for me.  But it sure was nice taking that short break, and it\'s always nice drinking Thunder Ale!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129, 'enUS', 'I\'m back where I started.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130, 'enUS', 'I\'m now far enough away...so I can\'t see the barrel.  Poison away!.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131, 'enUS', 'Think there are any fish in here?', 'Think there are any fish in here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (132, 'enUS', 'I caught a big one last week, it had three eyes!', 'I caught a big one last week, it had three eyes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133, 'enUS', 'My daddy says that in the ocean, there are fish so big they could swallow a whole ship.', 'My daddy says that in the ocean, there are fish so big they could swallow a whole ship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134, 'enUS', 'My daddy can catch more fish than your daddy!', 'My daddy can catch more fish than your daddy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (135, 'enUS', 'Look! Look! I caught something! Aww....it\'s just a stinky ol\' boot.', 'Look! Look! I caught something! Aww....it\'s just a stinky ol\' boot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136, 'enUS', 'Nuh uh.', 'Nuh uh.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (138, 'enUS', 'Liar!', 'Liar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (139, 'enUS', 'I heard that there are these huge fish that can walk on land to hunt, and eat people!', 'I heard that there are these huge fish that can walk on land to hunt, and eat people!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (140, 'enUS', 'And one time, at camp, I caught a fish that was bigger than I am!!', 'And one time, at camp, I caught a fish that was bigger than I am!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (141, 'enUS', 'I heard a story about this golden fish, and if you caught it you would get three wishes!', 'I heard a story about this golden fish, and if you caught it you would get three wishes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (142, 'enUS', 'If you fished as well as you talked the ocean wouldn\'t have any fish left.', 'If you fished as well as you talked the ocean wouldn\'t have any fish left.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (143, 'enUS', 'Shhh! You\'re scaring the fish away.', 'Shhh! You\'re scaring the fish away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (144, 'enUS', 'You\'re making that up.', 'You\'re making that up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (145, 'enUS', 'If you could catch a fish big enough for your mouth we\'d never be hungry again.', 'If you could catch a fish big enough for your mouth we\'d never be hungry again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (146, 'enUS', 'Maybe we should go to the bridge and fish.', 'Maybe we should go to the bridge and fish.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (147, 'enUS', 'Really?', 'Really?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (149, 'enUS', 'And that\'s how Lothar killed thirty six orcs with his bare hands!', 'And that\'s how Lothar killed thirty six orcs with his bare hands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (150, 'enUS', 'And so the knights stood before the charging Horde and held their ground as a thousand berserk orcs came through the valley.', 'And so the knights stood before the charging Horde and held their ground as a thousand berserk orcs came through the valley.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (151, 'enUS', 'They say he can turn into a raven sometimes.', 'They say he can turn into a raven sometimes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (152, 'enUS', 'You know why orc eyes glow red? It\'s because they drink blood!', 'You know why orc eyes glow red? It\'s because they drink blood!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (153, 'enUS', 'There is no spoon.', 'There is no spoon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (154, 'enUS', 'I swear, people have actually seen them. Pandaren really do exist!', 'I swear, people have actually seen them. Pandaren really do exist!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (155, 'enUS', 'And then the rabbit just bit his head off... I swear.', 'And then the rabbit just bit his head off... I swear.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (156, 'enUS', 'You know there are crocolisks in the Canals. They were brought from the swamp as pets, but got thrown in the canals.', 'You know there are crocolisks in the Canals. They were brought from the swamp as pets, but got thrown in the canals.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (157, 'enUS', 'Wow.', 'Wow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (158, 'enUS', 'Oh yeah, I heard about that.', 'Oh yeah, I heard about that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (159, 'enUS', '', 'Come back, Fluffy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (160, 'enUS', '', 'Why do you run from me, Fluffy?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (161, 'enUS', '', 'C\'mon, Fluffy! I just want to give you a bath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (162, 'enUS', '', 'Fluffy! Fluffy, stop running!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (164, 'enUS', '', 'Carrots every day for a year if you stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (165, 'enUS', '', 'Don\'t make me get my pappy to catch you! He likes to catch bunnies using his rifle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (166, 'enUS', 'Oh c\'mon, that\'s not true.', 'Oh c\'mon, that\'s not true.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (167, 'enUS', 'That\'s neat.', 'That\'s neat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (168, 'enUS', 'My father says that\'s just a story.', 'My father says that\'s just a story.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (169, 'enUS', 'Can you imagine?', 'Can you imagine?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (170, 'enUS', 'Sounds kinda like one of Billy\'s fish stories to me.', 'Sounds kinda like one of Billy\'s fish stories to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (171, 'enUS', 'I thought I heard something.', 'I thought I heard something.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (172, 'enUS', 'I think I see something.', 'I think I see something.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (173, 'enUS', 'Eww... that\'s not a fish!', 'Eww... that\'s not a fish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (174, 'enUS', 'I got worm guts on my shoes.', 'I got worm guts on my shoes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (175, 'enUS', 'Something smells funny.', 'Something smells funny.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (176, 'enUS', 'I hope that was a fish!', 'I hope that was a fish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (177, 'enUS', 'I don\'t think there\'s any fish in these canals.', 'I don\'t think there\'s any fish in these canals.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (178, 'enUS', 'Worm goes on the hook, hook goes in the water. Fish is in the water, our fish.', 'Worm goes on the hook, hook goes in the water. Fish is in the water, our fish.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (179, 'enUS', 'Could ye spare some coin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (181, 'enUS', 'Alms for the poor?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (182, 'enUS', 'I will gladly pay you Tuesday for a hamburger today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (183, 'enUS', 'Help a poor bloke out?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (184, 'enUS', 'Spare some change for a poor blind man? ...What do you mean I\'m not blind? ...I\'M NOT BLIND! I CAN SEE!! It\'s a miracle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (185, 'enUS', 'Shine yer armor for a copper.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (186, 'enUS', 'It\'s all their fault, stupid Alliance army. Just had to build their towers right behind my farm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (187, 'enUS', 'It\'s all their fault, stupid orcs. Had to burn my farm to the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (188, 'enUS', 'You hear that? I think they\'re getting closer...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (189, 'enUS', 'Guard duty, I hate guard duty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (190, 'enUS', 'I heard all the kobolds that were down there are dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (192, 'enUS', 'Sorry, Huldar... But it seems I\'ve misled you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (194, 'enUS', 'Dark Iron activity. Even that some of the shipments have been hijacked--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (195, 'enUS', 'I\'m sure it\'s just incompetence or oversight by some of the workers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (197, 'enUS', 'All hail, $n! Defender of The People!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (198, 'enUS', 'The People of Westfall salute $n, a brave and valiant defender of freedom.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (199, 'enUS', 'Three cheers for $n!  This land shall be ours once again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (200, 'enUS', 'Thanks for the cask of booze, $n.  I think I\'ll open up a bottle right now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (201, 'enUS', '', 'If we reverse the Essence flows perhaps we can alter the polarity.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (203, 'enUS', '', 'So then the array of magics would cascade into a chain reaction of positively charged energies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (204, 'enUS', '', 'The portal will shift slightly, at this point if you cast a binding cantrip you will solidify it in place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (205, 'enUS', '', 'When the positively aligned energies collide with the negatively charged energies, they don\'t negate one another.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (206, 'enUS', '', 'Maginor says that twisting alternating flows of positive energy actually creates a much more stable flow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (207, 'enUS', '', 'The magical wards at that point should be supported enough by the energy flux to contain the entity.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (208, 'enUS', '', 'Why can\'t we just shift the array to compensate for the variance in the flux?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (209, 'enUS', '', 'Putting in twelve centers of focus might allow the magical energies to form more solidly, adding the necessary stability.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (210, 'enUS', '', 'But what if the resulting frequency shift were to send magical feedback up the flows?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (211, 'enUS', '', 'Wouldn\'t that cause the weave to unravel if not properly anchored before starting the casting?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (212, 'enUS', '', 'But wouldn\'t that mean crossing the streams? Isn\'t that really bad?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (213, 'enUS', '', 'The resultant energies could collapse though, and that could cause an energy flux that would give you a migraine for weeks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (214, 'enUS', '', 'But isn\'t that what caused the initial problems with Adept Syleria\'s magical formulae?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (215, 'enUS', '', 'I think not, I don\'t need to be blown up again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (216, 'enUS', '', 'That will unbalance the magical focus, though, and cause a reverse vibration in the ether.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (217, 'enUS', '', 'I suppose that could work, if we had twenty people to cast it with.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (218, 'enUS', '', 'What if we used three focuses in Tyrean pattern? That should solve it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (219, 'enUS', '', 'But if we stabilize it with an anchor thread at the appropriate energy crux then it should work.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (220, 'enUS', '', 'Always so negative. The chances of that happening are between zero and none.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (221, 'enUS', '', 'If we use the appropriate sequence we should be ok. Will just take some serious studying before we start.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (222, 'enUS', '', 'Wow, all of this for a love potion. Hope he\'s worth it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (223, 'enUS', '', 'If we use the Surian theory, then yes, but not if we go with the Y\'serian approach.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (224, 'enUS', '', 'At least we wouldn\'t be around to have to clean it up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (225, 'enUS', '', 'Only if we didn\'t follow the proper initialization procedures.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (226, 'enUS', 'I suppose you\'re right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (227, 'enUS', '', 'Here\'s my list, Keldric. The usual stuff.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (228, 'enUS', '', 'Thank you, Keldric. See you later.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (229, 'enUS', 'Curse you, $n!  One day, vengeance will be mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (230, 'enUS', 'Alright, we\'ve just gotten word that a caravan is leaving from Thelsamar. It\'s time to move! Go go go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (231, 'enUS', 'Alright, we\'re getting close to the excavation site, so we have to proceed carefully. Engage underwater breathing apparatus...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (232, 'enUS', 'Hey Stone, did you hear about the new steam engine old Tonkrend has been runnin on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (233, 'enUS', 'Ah yes. I\'ve heard all about those new flarged things. Armor was removed from the casing to decrease weight. Bargh! Nonsense!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (234, 'enUS', 'Do you ever think about anything other than fighting? I could circle you down on the stretch course!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (235, 'enUS', 'Bah! By the time you got in range of old Trollplow you would be in reduced to rubble!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (236, 'enUS', 'Argh! You\'re hopeless!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (237, 'enUS', 'Hey Fizzer, I ever tell you the time I was riding through the canals and some goblins in shredder suits tried to ambush me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (238, 'enUS', 'Ah, let me see. Yes probably nearing a hundred times now I\'d wager!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (239, 'enUS', 'Brahaha! Tha look on their faces as old Trollplow\'s gun came swiveling around to meet them! Their saws couldn\'t even touch me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (240, 'enUS', 'I would have just dropped some Evershine in my tank and been back home before they realized I\'d gone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (241, 'enUS', 'Bah! That\'s no fun!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (242, 'enUS', 'Hey Stone, let\'s hit the circuit some time. We could run a few laps and see who wins?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (244, 'enUS', 'Aye sure, just as long as we get to hit the arena right after!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (245, 'enUS', 'Nothing you can say will ever get me in the arena against that monstrosity of a machine you call a steam tank!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (246, 'enUS', 'Har! You scared I\'ll hurt your little dainty racer?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (247, 'enUS', 'Ha! I\'m more afraid that those old holes and gashes would burst that thing and I would be showered by the shrapnel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (248, 'enUS', 'Haha! Sturdy old Trollplow has just a few meager battle scars!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (249, 'enUS', 'Nothing beats the sound of a twin-carb, fork-ring steam engine burning pure firestone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (250, 'enUS', 'Ha! I\'d take the sound of a Crater Cannon XT blasting off a shell over that any day!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (251, 'enUS', 'Bah! You wouldn\'t know a great steam engine if it smacked you upside the head!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (252, 'enUS', 'Maybe, but if it did hit me upside the head I\'d know exactly which cannon to use to blow it into the smallest bits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (253, 'enUS', '', 'Corbett, dear.  Harlan needs a load of knitted shirts and pants as soon as we can manage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (254, 'enUS', '', 'Corbett, you there?  Harlan needs another load of knitted goods.  Can you take it to him?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (255, 'enUS', 'My pleasure, sugar drop.  I\'ll be back soon...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (256, 'enUS', 'Business must be good down at the bazaar.  I\'ll get him resupplied right away!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (257, 'enUS', 'Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (258, 'enUS', 'I should have a few extra coins from this sale.  Maybe I\'ll buy myself some lunch...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (259, 'enUS', 'Hey, Harlan.  Here\'s a load of knitted cloth for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (260, 'enUS', 'Oomph!  Here\'s another load of supplies, Harlan.  It must be selling fast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (261, 'enUS', 'Ah, much appreciated, Corbett.  We\'ll get these on the racks immediately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (262, 'enUS', 'Ah yes, and promptly delivered.  As always, it\'s a pleasure doing business with you, Corbett.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (263, 'enUS', 'Well, I\'m off then.  Take care, Harlan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (264, 'enUS', 'Glad to see you\'re doing so well, Harlan.  And I hope to see you again soon...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (265, 'enUS', 'Lord Mayor!  Our scouts report strange activity to the west.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (266, 'enUS', 'Mayor Ello!  Our graveyard scouts return with ominous reports...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (267, 'enUS', 'Hideous bellows and growls are heard coming from that old hermit\'s shack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (268, 'enUS', 'Wolves head this way, as if fleeing from...something.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (269, 'enUS', 'Yes, Lord Mayor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (270, 'enUS', 'This is grim news, and I fear it is linked to the dealings $n had with the Embalmer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (271, 'enUS', 'Watcher, inform Althea that a dire threat is marshalling against our town.  We must prepare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (272, 'enUS', 'Althea, there are reports of strange happenings at the graveyard.  Ello believes it portends an attack on Darkshire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (273, 'enUS', '', 'This doesn\'t bode well.  Send scouts to gather more intelligence, and stay on alert.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (275, 'enUS', 'Yes, commander!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (276, 'enUS', 'Beware!  Beware!  A threat lurks in the wild!  Night Watchers, be on the alert!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (277, 'enUS', 'DARKSHIRE...I HUNGER!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (278, 'enUS', 'ROARRRRR!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (279, 'enUS', '', '...Nine...Ten...Ready or not.  Here I come!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (280, 'enUS', '', 'Hey! You better not have gone too far!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (281, 'enUS', '', 'Mom said not to climb up there any more.  I\'m gonna tell!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (282, 'enUS', 'Thank you, $n.  May the Light bless you, $Gbrother:sister;, and guide your path.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (283, 'enUS', 'Now for that snack...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (284, 'enUS', 'Good day, Elling!  Hullo Elaine!  Let me have a wheel of bleu cheese, eh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (285, 'enUS', 'Hullo, Trias clan!  A ball of your smoked mozzarella, if you please!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (286, 'enUS', '', 'Good day, Corbett.  Here\'s your cheese, fresh made this morning!  And how are things at your shop?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (287, 'enUS', '', 'Hi Corbett!  Here, you go!  I trust business is faring well at your clothier shop...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (288, 'enUS', 'See you later, Corbett, and tell the missus hello!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (289, 'enUS', 'Take care, and send our regards to your family.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (290, 'enUS', 'Thank you kindly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (291, 'enUS', 'Thanks for the cheese!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (292, 'enUS', 'I should get back before Rema starts to worry...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (293, 'enUS', 'Time to get back to the shop...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (294, 'enUS', 'I\'m back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (295, 'enUS', 'Yes ma\'am, business is brisk!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (296, 'enUS', 'Testing 1', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (297, 'enUS', 'Testing 2', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (298, 'enUS', 'Testing 3', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (299, 'enUS', 'I\'m out of the building again.  Now I\'ll move one more waypoint...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (300, 'enUS', 'I\'m moving within the building', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (301, 'enUS', 'I made it to the last waypoint.  Yay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (302, 'enUS', '', 'Mom, Jesse climbed the wind tower again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (303, 'enUS', 'You\'d better not tattle on me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (304, 'enUS', 'I can\'t come down.  I am scared.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (305, 'enUS', 'Whoa...Whoa..Aaaaahhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (306, 'enUS', 'Work you dogs! Put your back into it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (307, 'enUS', '', 'You get down from there right now!  I don\'t want you climbing that thing.  It\'s not safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (308, 'enUS', '', 'See what I mean?  Get inside right now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (310, 'enUS', 'This will take a while to heat up, so its time for a drink! What do you say Tongus?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (311, 'enUS', 'PICK ME!!!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (312, 'enUS', 'Attack!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (313, 'enUS', 'Get in position lads! The beasties are upon us. Wait for my signal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (314, 'enUS', 'These lands shall be cleansed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (316, 'enUS', 'These undead atrocities will be destroyed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (317, 'enUS', 'The Scarlet Crusade shall not fail in its mission!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (318, 'enUS', 'Stormy seas out there.  The sea spirits must be restless....hic!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (319, 'enUS', 'I saw a Sea Giant once.  It\'s not something I would care to see again.  Burp!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (320, 'enUS', 'I\'d go out and clear out some of those gnolls, but this leg has seen better days and I am afraid I\'d just wind up dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (321, 'enUS', 'Watch your step out there.  Many an adventure seeker has not returned from the marsh.  hic!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (322, 'enUS', 'It\'s time for my meditation, leave me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (323, 'enUS', 'There you are. What news from Westfall?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (324, 'enUS', 'VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (325, 'enUS', 'Very well. I will return then.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (326, 'enUS', 'Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (327, 'enUS', '*sigh* It\'s about time to check on the cemetery again-- I\'ll be back in a few minutes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (328, 'enUS', 'We really should fix this fence soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (329, 'enUS', 'Vrok said the sights are off on Grumlar\'s gun. I suppose I should check it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (330, 'enUS', 'Hmph! I didn\'t even hit the blasted target! That thick skulled dwarf was probably using the gun as a mace again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (331, 'enUS', 'Hmmmm... That one clipped it\'s shoulder. Grumlar was right, the sights are slightly off. Easy enough for me to fix though.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (332, 'enUS', 'Almost perfect shot! It\'s a bit off but I can see where Grumlar would miss with it. He couldn\'t hit the broad side of a barn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (333, 'enUS', 'Bullseye! That blasted dwarf doesn\'t know what he is talking about. Only thing wrong with Grumlar\'s gun is Grumlar\'s aim.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (339, 'enUS', 'Only $3658E shopping minutes before Christmas!  But wait, is it Christmas?  Well then, there are only $3659E minutes left to party!  TEST TEST DO NOT LOCALIZE', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (340, 'enUS', 'Warg, are you about ready to eat? I am gonna get it started.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (341, 'enUS', '', 'Khara hums an old dwarven tune while she cooks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (342, 'enUS', 'Hon, food is ready. Come wash up and eat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (343, 'enUS', 'Mmmm, that was good. However, time to get back to work!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (344, 'enUS', 'Ahhh, just in time! My stomach was rumblin\' loud.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (345, 'enUS', 'MMmmMMmmm. Smells delicious Khara.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (346, 'enUS', 'Burp! Hon, that was really good. Not only do you know how to catch a fish but you sure do know how to cook one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (347, 'enUS', 'Whew! I think I ate too much. Need to loosen my belt a few notches.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (348, 'enUS', 'Thanks for the great meal darlin\' but I need to get back out front.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (349, 'enUS', 'Another glorious day of fishin\' is ahead of me, Khara. I best get movin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (350, 'enUS', 'What a day for fishin\'. They are sure to be bitin\' today!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (352, 'enUS', 'Warg looks over the water appraising what his luck might be today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (353, 'enUS', 'Welp the fish ain\'t bitin\' no more so I might as start gutting and cleaning what I did catch today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (355, 'enUS', 'Best be on my way. Khara will be expecting me soon. See ya tomorrow X.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (356, 'enUS', 'Look at all those ripples. Lots of activity in the water so I should catch a mess of fish today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (357, 'enUS', 'Hmmmm, only a little bit of activity on the surface but the sun is in the right place. Might turn out to be a fine day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (358, 'enUS', 'Not looking too good today. Sun is high and bright early and no activity on the water.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (359, 'enUS', 'Hmph! What a sorry day for fishin\' this is going to be. Ah well, a bad day fishin\' is better then a good day workin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (360, 'enUS', 'Berte! Evalyn! It\'s getting dark. Come inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (361, 'enUS', 'You can play more tomorrow. Now come on inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (362, 'enUS', 'Alright, alright. You two can go play just stay in town. No sneaking off to bug Warg and Khara down by the water.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (363, 'enUS', 'Hah! Daryl, you should have seen the look on your face! You half jumped out of your skin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (365, 'enUS', 'They have you good on that one, lad!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (366, 'enUS', 'Oh, shut up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (367, 'enUS', 'Awww Mom! It\'s hardly dark yet! Can we play for a bit longer please?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (368, 'enUS', 'Ok...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (370, 'enUS', 'We must be vigilant to eradicate this plague!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (371, 'enUS', 'Keep up the good work.  This scourge will be cleansed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (372, 'enUS', 'The Scarlet Crusade will scour these lands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (373, 'enUS', 'Let none with the foul taint of plague live!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (374, 'enUS', 'Mom! Can we go play please please please? Can we huh? Can we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (375, 'enUS', 'The kids jump up and down with youthful energy. (PH emote)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (376, 'enUS', 'Wheeee! Thanks Mom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (378, 'enUS', 'Thanks Mrs. Brightbeard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (379, 'enUS', 'Gettin\' a bit nippy out here. Think I will go warm myself by the fire and grab a pint or twelve.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (380, 'enUS', 'Nothing like a roaring fire on a cool night with good friends and dwarven ale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (381, 'enUS', 'Brock. Torren. I am going inside for an ale and to warm myself by the fire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (382, 'enUS', 'Mmmmm, an ale by the fire does sound good. Sure, I will join ya Jern.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (383, 'enUS', 'Here! Here! I\'ll drink to that!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (384, 'enUS', 'What? Did someone say ale? Don\'t start pouring the pints without ol\' Torren!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (385, 'enUS', 'I\'ll drink to anything!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (386, 'enUS', 'Noma should have dinner ready soon. Might as well head in.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (387, 'enUS', 'My, that Black Claw stout packs quite a punch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (388, 'enUS', 'I feel... dizzy.  It\'s so hot... in... here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (389, 'enUS', 'Where\'s the rest of the guys?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (390, 'enUS', 'Every bodies coming...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (391, 'enUS', 'He really couldn\'t help it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (393, 'enUS', 'No one lives forever...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (394, 'enUS', '', 'Gimlok!  Gimlok! Get in here this instant!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (395, 'enUS', '', 'Where has the boy gotten to now?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (396, 'enUS', '', 'Gimlok, don\'t you go and get your clothes all dirty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (397, 'enUS', 'Huuup...Huuup...Bleeeeehh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (398, 'enUS', 'I\'ll be a guard someday!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (399, 'enUS', 'Remember when Danath gathered all the mercenaries of Stormwind together and we marched to fight at Nethergarde?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (400, 'enUS', 'I tell ye, I don\'t miss the Great War at all. I remember when we fought at Darrowmere. All night in the fog, lying in a muddy trench.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (401, 'enUS', 'I miss the dwarven ale we used to get at that inn in Lordaeron. Remember that fight we started there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (402, 'enUS', 'Less than a hundred of us, and over a thousand orcs. Only a handful of us managed to walk away from that one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (403, 'enUS', 'The best had to have been the look on Perenolde\'s face when our army comes marchin\' right up to his front door. What a battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (405, 'enUS', 'Battling that band of Twilight Hammer in the Morass, I could think of better places for a war.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (406, 'enUS', 'Ahh yes, now I remember, your wedding night wasn\'t it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (407, 'enUS', 'Ahh the Glustewelt twins!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (408, 'enUS', 'I still have the scars from that night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (409, 'enUS', 'Broke both me legs that night. How could I forget?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (410, 'enUS', 'I woke up in a bed in Northshire three weeks later. Don\'t remember a damn thing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (411, 'enUS', 'Wasn\'t that the night we had to pick up my thumb and carry it in your smoke pouch?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (413, 'enUS', 'I have a piece of iron in my back that will remind me of that night for the rest of my days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (414, 'enUS', 'Course I remember that night. Two inches to the left and you\'d be drinking with that elf, Morris. That was my best shirt, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (415, 'enUS', 'Was that the third or fourth time you nearly got me gutted trying one of your crazy stunts?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (416, 'enUS', 'Aye and thanks for letting me carry my own hand back to the priests that night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (417, 'enUS', 'Hehe, wimp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (418, 'enUS', 'You screamed like a little girl, funniest thing I ever saw.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (419, 'enUS', 'It all worked out in the end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (420, 'enUS', 'Let\'s not even begin comparing battle scars, my friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (421, 'enUS', 'Never seen anyone move so fast in my whole life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (422, 'enUS', 'You are constantly surprising me with what a person can live through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (423, 'enUS', 'Well, you\'re still here. I\'d say that is something worth drinking to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (424, 'enUS', 'You can thank me anytime for making your life more interesting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (425, 'enUS', 'I... I... don\'t... feel... right...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (426, 'enUS', '%s waves to $n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (427, 'enUS', 'You have disturbed my rest, $c.  Now face my wrath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (428, 'enUS', 'My mind... my flesh... I\'m... rotting...!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (429, 'enUS', 'Let me have another one, Langston.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (430, 'enUS', 'Best ale in Stormwind, Langston. Thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (431, 'enUS', 'Hey Reese, give me an\' Christoph another round.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (432, 'enUS', 'A warm tavern and a cold ale. What more could we ask for?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (433, 'enUS', '%s raises the gun to his shoulder, aims it carefully then fires a shot at the target.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (434, 'enUS', 'Die in the name of Ragnaros!', '', 26124);
