INSERT INTO `broadcast_text_locale` VALUES (477, 'enUS', 'Am I carrying any letters addressed to $n? Why, no!  Looks like it\'s time for you to die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (478, 'enUS', 'I\'ll deliver you, weak $C, to the afterlife!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (479, 'enUS', 'I have a special message for $n.  And it says you must die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (480, 'enUS', 'Here\'s a singing telegram for $n: Roses are red, violets are blue, I will kill any $R I see, including you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (481, 'enUS', 'Let\'s get to the others, and keep an eye open for those wolves outside...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (482, 'enUS', 'Be careful, $n.  Those wolves like to hide among the trees.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (483, 'enUS', 'We\'re almost there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (484, 'enUS', 'We made it!  Thanks, $n.  I couldn\'t have gotten here without you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (485, 'enUS', 'Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (486, 'enUS', 'I\'ve been spotted!  $n incoming! Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (487, 'enUS', 'Die you worthless $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (488, 'enUS', 'Need a little help here.  That $n saw me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (489, 'enUS', '$n coming in fast!  Prepare to fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (490, 'enUS', 'That $n wants me dead!  Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (491, 'enUS', 'What was that?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (492, 'enUS', 'Did you hear something?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (493, 'enUS', 'I smell a $R.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (494, 'enUS', 'Help! We\'re being attacked! Dark Irons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (496, 'enUS', '$n coming this way fast!  Prepare for attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (497, 'enUS', 'We are under siege!  To arms!  Kill this $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (498, 'enUS', 'Ah, here at last! It\'s going to feel so good to get rid of these barrels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (499, 'enUS', '%s roars a challenge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (500, 'enUS', '', 'I shall return in time..', 26124);
INSERT INTO `broadcast_text_locale` VALUES (502, 'enUS', 'The Sons of Arugal will rise against all who challenge the power of the Moonrage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (503, 'enUS', 'Let the legend of Stalvan rest!', 'Let the legend of Stalvan rest!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (504, 'enUS', 'Who is this mere $r that meddles with that which is past?  May the legend of Stalvan die along with you!', 'Who is this mere $r that meddles with that which is past?  May the legend of Stalvan die along with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (505, 'enUS', 'Toil not in matters of the past, $n!', 'Toil not in matters of the past, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (506, 'enUS', '', '%s lets out a high pitched screech, calling for help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (507, 'enUS', 'Tilloa is that you?  Why no!  It\'s just some dirty $r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (508, 'enUS', 'Who dares disturb me?  Die $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (509, 'enUS', 'I shall spill your blood, $C!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (510, 'enUS', 'Help! I\'ve only one hand to defend myself with.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (511, 'enUS', 'Send them on! I\'m not afraid of some scrawny beasts!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (512, 'enUS', 'Get him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (513, 'enUS', '%s chants words of power...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (514, 'enUS', 'No!  It can\'t be...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (515, 'enUS', 'Incredible!  I know he who wrought these enchantments...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (516, 'enUS', 'This is unforseen.  The being who enchanted this is known to me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (517, 'enUS', '$n, I have the information you seek...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (518, 'enUS', 'My minions...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (519, 'enUS', 'The time for conquest approaches quickly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (520, 'enUS', 'Soon, your thirst for destruction will be quenched.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (521, 'enUS', 'The rage burning within you will soon find fuel in our enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (522, 'enUS', 'I come with news of our conflict with the Rogue Undead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (523, 'enUS', 'The rebels of Lordaeron are small and weak, and know not the joy of serving our Lich King.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (525, 'enUS', 'The Dark Lady\'s followers are blind to think they can stand against our might.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (526, 'enUS', 'We grow strong, while our foes are surrounded by foes, and weakened by constant attacks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (527, 'enUS', 'Our Lord commands us to make ready for war!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (528, 'enUS', 'Prepare yourselves, for we will soon launch an assault against they who called themselves \"The Forsaken.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (529, 'enUS', 'Make ready for battle, my creations.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (530, 'enUS', 'We are the arm of His will.  And we will crush His foes into grave dust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (531, 'enUS', 'These undead rabble, and their dead-elf queen, will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (532, 'enUS', 'We will drive our foes from Silverpine, then bring the tides of war to their very capital!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (533, 'enUS', 'Death and Praise for the Lich King!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (534, 'enUS', '', 'It\'s good to see you again, Erland.  What is your report?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (535, 'enUS', 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (536, 'enUS', 'If I am excused, then I\'d like to check on Quinn...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (537, 'enUS', 'Hello, Quinn.  How are you faring?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (538, 'enUS', 'Try to take better care of yourself, Quinn.  You were lucky this time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (539, 'enUS', 'I\'ve been better.  Ivar the Foul got the better of me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (540, 'enUS', 'No two-bit traitor will stop the powder from getting to Ironband!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (541, 'enUS', 'Beware!  A $n is upon us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (542, 'enUS', '', 'Be ready, $n. I hear the council returning. Prepare to ambush!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (543, 'enUS', 'A $n attacks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (544, 'enUS', 'Beware!  I am under attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (545, 'enUS', '', 'Well done. A blow to Arugal no doubt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (546, 'enUS', 'The Stonewrought Dam has been saved!  Three cheers for $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (548, 'enUS', '', 'All is lost!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (549, 'enUS', 'Make Way!  Make Way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (551, 'enUS', '%s leaps from the first catapult to the second!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (552, 'enUS', 'I\'m cursed!  First my ship sinks, and now...I\'m out of mead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (553, 'enUS', 'Curse you, Halyndor!  You led us to our doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (554, 'enUS', 'Don\'t mind me, or my smell...because I sure don\'t!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (556, 'enUS', 'Ah, beautiful Menethil Harbor, with the best mead this side of Ironforge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (557, 'enUS', 'Oh, my brothers!  May you find rest in your watery graves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (558, 'enUS', '%s looks like he\'s going to be sick!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (559, 'enUS', '%s swaggers heavily.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (560, 'enUS', '%s mumbles incoherent words.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (561, 'enUS', '%s raises his mug to his eye, and frowns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (562, 'enUS', '%s growls and looks around him aggressively', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (563, 'enUS', '%s begins translating ...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (564, 'enUS', '$n, the translation is finished!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (566, 'enUS', 'This is a dangerous line of business!$B$BMiran just left with a delivery. He should be back in a few minutes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (567, 'enUS', 'They thought I was weak.  Haha!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (568, 'enUS', 'I am the head of the family.  Me.  ME!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (569, 'enUS', 'I\'m cold, lord.  When will you keep your promise?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (570, 'enUS', 'My family hates me.  Frightened idiots, all of them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (571, 'enUS', 'Father?  Are you proud of me now? HAHAH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (572, 'enUS', 'Mother, oh mother.  You should have listened to me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (573, 'enUS', 'Thurman my brother, who\'s laughing now?  Hah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (574, 'enUS', 'Here to visit the family?  Die, fool!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (575, 'enUS', 'The Agamand Mills is held by the Scourge, $C.  Join us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (576, 'enUS', 'I raise my brew and hope to be rid of the likes of you!  Cheers, you no good scoundrel, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (577, 'enUS', '%s cheers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (578, 'enUS', '', 'Stay back vile creatures!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (579, 'enUS', 'Croak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (580, 'enUS', '%s sniffs the elixir then eagerly digs in!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (581, 'enUS', 'Finally they show mercy!  They\'ve given us something to drink!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (582, 'enUS', 'And Shindigger Stout too!  Thank the Light!  I was parched!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (583, 'enUS', 'Your undeath is not the end of your life, but the beginning. It is up to you to decide where your fate lies now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (584, 'enUS', 'You are free of His control. And while you will be judged by lesser beings, at least you have chances that the Scourge do not!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (585, 'enUS', 'Terror, darkness, power? The Forsaken crave not these things; the Forsaken ARE these things.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (586, 'enUS', 'My bones are weak, my eyes see only darkness, and my body feels only pain; but these things are good because my will is my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (587, 'enUS', '', 'Hello, Charys. I have my list, could you get me all of that, especially the last ingredient.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (588, 'enUS', '', 'Mommy? Can I pet Fizzles?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (589, 'enUS', '', 'Sure, Paige. Just be gentle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (590, 'enUS', '', 'Thanks, Charys. C\'mon Paige, sweetie.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (591, 'enUS', '%s starts to chant in an ancient tongue, summoning forth something evil in nature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (592, 'enUS', 'Ah, it is good to be free of my bonds! Have you freed Tog\'thar yet? The humans split us up after our capture.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (593, 'enUS', 'Have you freed Drull yet? I don\'t know where he\'s being held.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (594, 'enUS', 'Go on ahead without me! I will make my own way to Tarren Mill.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (595, 'enUS', 'I will only slow you down, but no pink-skinned humans will keep me from freedom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (596, 'enUS', 'Search this rubble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (597, 'enUS', 'Find me something good, you dogs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (598, 'enUS', 'Over here! Inside the room. Did you find the key to my chains? The human Borhuin has them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (599, 'enUS', 'I returned from the grave to take my revenge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (600, 'enUS', 'Die in the name of the Dark Lady, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (601, 'enUS', 'The undead shall feast on your soul, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (602, 'enUS', 'I have risen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (603, 'enUS', 'Revenge shall be mine at last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (604, 'enUS', 'Southshore shall pay in blood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (605, 'enUS', 'Mmm... I love my delicious Southshore stout.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (606, 'enUS', 'Southshore stout, how do I love thee? Let me count the ways...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (608, 'enUS', 'Protect Foreman Bonds at all costs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (609, 'enUS', 'Guards! Guards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (610, 'enUS', 'You\'re going to pay for this outrage! Guards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (611, 'enUS', 'Get to your posts! Let no invader reach Southshore!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (612, 'enUS', 'Anyone suspicious is to be taken down quickly. Get to your posts as quickly as possible!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (613, 'enUS', 'Protect the Magistrate at all costs. They could come from any direction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (614, 'enUS', 'My trees.... they have withered up and died just like me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (615, 'enUS', 'This orchard is now as barren and lifeless as my rotting shell. Once these trees bloomed with fruit but now there is only rot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (616, 'enUS', 'I spent my whole life caring for these trees. Pruning and nurturing them... only fitting that they have joined me in death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (617, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (618, 'enUS', 'Greetings Citizens!  Soon perhaps the tide will turn in our favor and the plague will end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (619, 'enUS', 'Help me! Help! Assassins in the hills! To arms! To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (620, 'enUS', 'Hear ye! Hear ye! *pant pant* Be warned citizens of Southshore: assassins are heading to our fair town!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (621, 'enUS', 'Guards to your posts! They must be after the Magistrate again. He must be protected! They could come from any direction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (622, 'enUS', 'I\'m so tired *pant pant*', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (623, 'enUS', 'The Southshore Crier grabs the back of his neck and his eyes go wide.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (626, 'enUS', 'Assassins... save... th... Magistraaa...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (627, 'enUS', 'They\'re here! They\'re over here! Help me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (628, 'enUS', 'The assassins are over here! Quickly, before they reach the Magistrate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (629, 'enUS', 'More agents of the Syndicate I\'ll wager! You\'ll never take back Alterac... or Southshore!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (630, 'enUS', 'The deed is done! The Magistrate is dead-- to the shadows, my brethren.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (631, 'enUS', 'The Magistrate has fallen! Make your escape, my brothers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (632, 'enUS', 'Haha! Alterac and Hillsbrad shall come under our rightful rule once again now that the Magistrate has fallen. Retreat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (633, 'enUS', 'The meddlers shall pay for their interference. Retreat to the shadows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (634, 'enUS', 'The Magistrate is too well protected. Retreat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (635, 'enUS', 'You may have succeeded in stopping us this time, but we shall return-- this we assure you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (636, 'enUS', '', '%s takes out a bottle filled with some kind of green liquid and takes a large gulp.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (637, 'enUS', '%s\'s eyes glow red as he lights his dynamite and begins to cackle madly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (638, 'enUS', 'Wahehe! I\'m taking you down with me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (639, 'enUS', 'All right, you lazy bums, time to get up and get started. That bridge isn\'t getting built on its own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (640, 'enUS', 'Ya\'ll get a few minutes to wake up, but let\'s get started soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (641, 'enUS', 'Hi Ho, Hi Ho, and all that... let\'s get a move on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (642, 'enUS', 'Take a break, men! Half hour and then back to work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (643, 'enUS', 'The bridge is looking great. We keep up this pace and it\'ll be done for the first expansion forces to head north.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (645, 'enUS', 'Okay, lunch time\'s over, guys. Let\'s get back to work. If the rest of the day goes this well, then maybe we\'ll cut out early.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (646, 'enUS', 'Good, good. Keep it up and this bridge\'ll be done in no time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (647, 'enUS', 'Call it a day, men!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (648, 'enUS', 'Day\'s wasting. Let\'s get going.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (649, 'enUS', 'Good night, gents. Sleep well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (650, 'enUS', 'Morning, my friends.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (651, 'enUS', 'Ahh... food It\'s \'bout time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (652, 'enUS', 'Tis a good day to live, neh? Hard work and good food-- there are worse things we could be doing on such a beautiful day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (654, 'enUS', '*Dimitri wipes sweat from his forehead and lays down his tools*', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (655, 'enUS', 'Another good day indeed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (656, 'enUS', 'Good night, my friends. Until tomorrow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (657, 'enUS', 'Can\'t we just sleep a while longer?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (658, 'enUS', 'Okay, okay... I\'m up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (659, 'enUS', '\'Bout time. I was hungry an hour ago.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (660, 'enUS', 'Finally! I can\'t wait to get some sleep tonight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (661, 'enUS', 'Ugh! Being on your feet all day is no way to make a living. I should have been a tailor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (662, 'enUS', 'I love mornings! It\'s a great day, isn\'t it? Smell that air?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (664, 'enUS', 'By the Light, I\'m hungry today. Did you all see Darcy today? She looked great.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (665, 'enUS', 'You know, this bridge is kind of large for just the 6 of us to be building. You\'d think more people would\'ve been hired.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (666, 'enUS', 'You guys see her? I didn\'t see Darcy. I wonder where she is tonight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (667, 'enUS', '%s looks up and down the docks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (669, 'enUS', 'Oh well, I\'m sure she\'ll be around later.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (670, 'enUS', 'Night, all. I\'m gonna sit up for a bit. Maybe Darcy\'ll come by.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (671, 'enUS', 'Huh?! Darn it, it\'s lunch time! I overslept!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (672, 'enUS', '%s runs up to the other bridge workers, panting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (674, 'enUS', 'Sorry, ah\'m late.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (675, 'enUS', 'This bridge isn\'t ever going to be finished, is it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (676, 'enUS', 'My back is killing me today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (677, 'enUS', 'Little food\'ll feel pretty good right about now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (678, 'enUS', 'Mmm, good stuff. Back to work once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (679, 'enUS', 'Another day gone by...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (680, 'enUS', 'Night, night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (681, 'enUS', '%s is struck dumb by the Soul Gem!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (682, 'enUS', '%s\'s spirit is freed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (683, 'enUS', '%s lets out a deep sigh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (684, 'enUS', 'I hate this part of my job. Oh Duskwood, be gentle with poor Antonio.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (685, 'enUS', 'Items of antiquity, weapons of war, armor or rarity. Who knows what Antonio will be carrying this week if you do not come look.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (686, 'enUS', 'This week it could be eye of newt, or perhaps a treasure map. You\'ll never know unless you look. What could Antonio have?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (687, 'enUS', 'Adventurers from all over sell things to Antonio-- come look for yourselves what little treasures he has for you this week.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (688, 'enUS', 'Travelers and adventurers make haste to the town circle and take advantage of Antonio\'s wonderful prices! Come one, come all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (689, 'enUS', 'Well met, travelers of Duskwood! My incredible wares are on sale for a short time in Darkshire. Make haste!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (690, 'enUS', 'Leave your battles or errands, and browse my incredible wares! Only in Darkshire for a short time... praise the Light for that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (691, 'enUS', 'Farewell, citizens of Darkshire. Until next time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (692, 'enUS', 'Finally, Redridge. A pleasant change from the dark, cold fog of Duskwood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (693, 'enUS', 'Ha ha! I have Betsy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (694, 'enUS', '', 'Gimme my dolly!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (695, 'enUS', '', 'Betsy! Give me Betsy back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (696, 'enUS', '', 'Don\'t hurt Betsy, you meanie!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (697, 'enUS', '', 'WAAAHHH!!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (698, 'enUS', '', 'I\'m telling Mommy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (699, 'enUS', '', 'Stop pulling her hair out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (700, 'enUS', '', 'You\'re hurting her!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (701, 'enUS', '', 'Stop! You\'ll pull her head off!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (702, 'enUS', 'Hello, hello, Amy. It\'s good to be back in Lakeshire and to see you, milady.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (703, 'enUS', '', 'Hello, Antonio. It\'s good to see you safely made it here again. Let me know if you need anything.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (704, 'enUS', 'Hear ye, hear ye! I\'ll pause to sell my wares for only a short time here in Lakeshire. Come to the town hall with haste!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (705, 'enUS', 'I wonder if your dolly can swim!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (706, 'enUS', 'I have your dolly! Nyah nyah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (707, 'enUS', 'What happens when I do this?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (708, 'enUS', 'If you want her back you\'re gonna have to beg!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (709, 'enUS', 'Crybaby! Crybaby!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (710, 'enUS', 'Baby wants her dolly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (711, 'enUS', 'Oops!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (712, 'enUS', 'I\'ve finally reached Lakeshire-- come one, come all, and take advantage of Antonio Perelli\'s wonderful deals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (713, 'enUS', 'Items powerful enough to help make the journey through Duskwood, Antonio Perelli\'s goods are now for sale here in Lakeshire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (714, 'enUS', 'Good to see you again, Amy. Be safe, luv. I\'m off to Goldshire now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (715, 'enUS', '', 'Take care of yourself, Antonio. I\'ll see you in another week.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (716, 'enUS', 'Hello, my friends. It is good to see you again. Still keeping our borders free I see. Antonio appreciates your hard work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (717, 'enUS', 'You\'re very welcome! I must be on my way... be safe, all of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (718, 'enUS', 'Hello again, Antonio. Thank you again for the hat for my wife. She adored it.', 'Hello again, Antonio. Thank you again for the hat for my wife. She adored it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (719, 'enUS', 'Be safe, Antonio.', 'Be safe, Antonio.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (720, 'enUS', 'Oh, I will, my friend. I will indeed. You, too, be safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (721, 'enUS', 'Wares for sale! Wares for sale! Come browse Antonio\'s wares before I must take my leave, citizens of Goldshire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (722, 'enUS', 'Come one, come all! Antonio\'s wares are for sale. I may not return for a great deal of time, so come one, come all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (723, 'enUS', 'Good people of Goldshire, come take advantage of my incredible prices on rare goods. I shall not return for some time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (724, 'enUS', 'Good bye, Goldshire. I\'ll be back soon enough-- perhaps a week\'s time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (725, 'enUS', 'Home again, home again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (726, 'enUS', 'Known throughout the lands, Antonio Perelli\'s goods now for sale near Sentinel Hill! I must move on soon, so make haste.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (727, 'enUS', 'Long-lasting, well-crafted items for sale near the Hill! Come one, come all! Time is short, so make haste.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (728, 'enUS', 'The time has come for me to once again make my wares available to all who travel near Sentinel Hill! Come one, come all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (729, 'enUS', 'Thank you, all. I shall return again in about a week.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (730, 'enUS', 'Off to Darkshire then, I suppose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (731, 'enUS', 'Mmmh...I SMELL FOOD!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (732, 'enUS', '%s searches through the skulls...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (733, 'enUS', '%s continues searching, placing more skulls in his crate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (734, 'enUS', 'I am done inspecting the skulls, $n, and I am relieved.  Yenniku is not among them...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (735, 'enUS', 'Ah, good $n.  Now let us see what tale these heads tell...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (736, 'enUS', '%s begins chanting...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (737, 'enUS', 'Where in the Outlands are those shipments? For days they keep me waiting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (738, 'enUS', 'Stupidity! I\'m surrounded by stupidity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (739, 'enUS', 'Orcs are smarter than those couriers! Where the blazes are they?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (740, 'enUS', 'There, $n.  You may now speak to the Bloodscalp chief and his witchdoctor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (741, 'enUS', '%s begins to summon in reinforcements!', '%s begins to summon in reinforcements!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (742, 'enUS', '%s presents the Mind\'s Eye and the Singing Crystals before him...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (743, 'enUS', 'I have fashioned Yenniku\'s Soul Gem, $n...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (744, 'enUS', '%s tries opening Maury\'s Clubbed Foot...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (745, 'enUS', 'Aha!  It opened!  I expected that to be harder.  Thanks for your help, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (746, 'enUS', 'Alright I\'m going! Stop yelling!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (747, 'enUS', 'This better be the last one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (749, 'enUS', 'You better back me up on this one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (750, 'enUS', 'Why did I have to get stuck with this repulsive, feeble excuse for a warlock?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (751, 'enUS', 'Make yourself useful and help me out here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (752, 'enUS', 'Just release me already! I\'ve had enough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (753, 'enUS', 'What? You mean you can\'t kill this one by yourself?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (754, 'enUS', 'If you touch me again it\'ll be the last thing you touch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (757, 'enUS', '%s begins to crack and break apart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (758, 'enUS', '%s\'s strength dwindles as chunks of rock break off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (759, 'enUS', '%s is reduced to rubble, but still continues to fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (761, 'enUS', 'How interesting... the Stone possesses great magical potential, but in a purely raw form...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (763, 'enUS', 'AH, A FEAST!  WHO LEFT THIS HERE...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (764, 'enUS', 'SMOTTS!  HEY, SMOTTS!!  COME OUT AND PLAY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (765, 'enUS', '', 'Thank you again for your aid. May the wind always be at your back during your travels.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (766, 'enUS', 'You have completed the quest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (767, 'enUS', 'Here come the beasts!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (768, 'enUS', 'You have failed to protect me from the apes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (769, 'enUS', 'What is happening? What force draws me to the Altar of the Tides!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (770, 'enUS', 'More beasts on the way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (771, 'enUS', 'Dirty beasts! Here they come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (772, 'enUS', 'YOU WERE SUCH GOOD SPORT THE LAST TIME...COME OUT!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (773, 'enUS', 'SMOTTS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (774, 'enUS', 'I PROMISE I WON\'T KILL YOUR CREW THIS TIME.  HAH!  BUT MY BROTHER MIGHT EAT THEM!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (775, 'enUS', 'COME OUT TO JANEIRO\'S POINT.  I\'M WAITING FOR YOU, SMOTTS...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (776, 'enUS', 'DON\'T YOU WANT TO FIGHT, SMOTTS!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (777, 'enUS', 'HAVE YOU RUN OUT OF SHIPS TO SEND?  WHERE\'S YOUR COURAGE?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (778, 'enUS', 'DON\'T LET THIS STATUE BE MY ONLY COMPANY.  COME OUT AND PLAY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (779, 'enUS', '%s tosses down his bottle of poison in desperation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (780, 'enUS', '%s takes out a remote control and tosses down a model siege engine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (781, 'enUS', '%s begins to glow and smoke!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (782, 'enUS', 'Still no sign of the final shipment of explosives.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (783, 'enUS', 'No sign of the final explosives shipment to the west either.  Where are those lollygaggers?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (784, 'enUS', 'This bridge should have been destroyed by now.  How long does it take for those lazy sods to get here from the Highlands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (785, 'enUS', 'Help me! Help me! Don\'t let them eat me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (786, 'enUS', 'Please save me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (787, 'enUS', 'I don\'t want to be a meal for an ogre! Please save me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (788, 'enUS', 'Heeeeeeeeeeeeellllllllllllllllllllllllp!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (789, 'enUS', 'I tire of this nonsense! Prepare to die!', 'I tire of this nonsense! Prepare to die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (790, 'enUS', 'Sure does seem like it\'s been a long time since anyone has relieved me from my post.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (791, 'enUS', 'Well, that\'s what I get for oversleeping!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (792, 'enUS', '', '%s reads Sully\'s letter page by page.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (793, 'enUS', '', 'This isn\'t fair!  My dearest Sully!  Come back to me!  No, $n!  Why did you bring me such news!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (794, 'enUS', 'Hiccup!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (795, 'enUS', 'These shards give me the creeps.  Hey Greeby, did you find more motes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (796, 'enUS', 'I wouldn\'t tell you if I did!  Because as soon as I do, I\'m getting away from these crystals...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (797, 'enUS', 'Yeah, I don\'t want to be here if Myzrael wakes up.  They say she has a mean temper...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (798, 'enUS', 'Easier than I expected... the sigil of Thoradin is whole, once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (799, 'enUS', 'May I bask in your shadow forever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (800, 'enUS', 'Master, your wisdom is matched only by your hugeness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (801, 'enUS', 'If I had a copper for each of your great deeds, I\'d be a rich kobold!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (802, 'enUS', 'You will keep us safe from the evil mistress, yes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (803, 'enUS', 'Oh, praise!  Your vigilance keeps Myzrael deep in the earth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (804, 'enUS', 'My only wish is to serve you, great one, until I die by your foot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (805, 'enUS', 'Your strength is of the mountain.  Your grace is of the winds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (806, 'enUS', 'My feet hurt...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (807, 'enUS', 'Die!  You will not free Myzrael!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (808, 'enUS', 'The Great One will smash you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (809, 'enUS', 'Aren\'t you a little short for your kind?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (810, 'enUS', 'My life means nothing!  Our sacred task is all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (811, 'enUS', 'If you mean to free Myzrael, you will be sorry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (812, 'enUS', 'Stand firm, brothers.  And don\'t worry!  Size is on our side!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (814, 'enUS', 'Take that!  The Drywhiskers will prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (815, 'enUS', 'No!  Leave us!  We must not fail our task!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (816, 'enUS', '', 'Well then, let\'s get this started. The longer we\'re here, the more damage the undead could be doing back in Hillsbrad.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (817, 'enUS', '', 'Alright, this is where we really have to be on our paws. Be ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (818, 'enUS', '', 'Watch my rear! I\'ll see what I can find in all this junk...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (819, 'enUS', '', '%s begins rummaging through the apothecary\'s belongings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (820, 'enUS', '', 'Found something... I think.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (821, 'enUS', '', 'I bet Quae\'ll think this is important. She\'s pretty knowledgeable about these things--no expert, but knowledgeable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (822, 'enUS', '', 'Okay, let\'s get out of here quick quick! Try and keep up. I\'m going to make a break for it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (823, 'enUS', 'You made it?! I\'m surprised Kinelory didn\'t slow you down anymore than that. Kin, here\'s your socks. Give me what you found ins', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (824, 'enUS', '%s studies the circle of stones, looking for signs of stress.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (825, 'enUS', 'You will never defeat us!', 'You will never defeat us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (826, 'enUS', 'My death matters little... the Hammer will still fall!', 'My death matters little... the Hammer will still fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (827, 'enUS', 'My life for my masters.  Rejoice!', 'My life for my masters.  Rejoice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (828, 'enUS', 'Curse you!', 'Curse you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (829, 'enUS', 'Glory and blood for the Old Gods!', 'Glory and blood for the Old Gods!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (830, 'enUS', 'My lords!  I come to thee!', 'My lords!  I come to thee!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (831, 'enUS', 'Your victory here only speeds your doom!', 'Your victory here only speeds your doom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (832, 'enUS', 'Mommy!', 'Mommy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (833, 'enUS', 'Intruders!  Slay them!', 'Intruders!  Slay them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (834, 'enUS', 'For our Masters!', 'For our Masters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (835, 'enUS', 'Masters, I pledge this $r to you!', 'Masters, I pledge this $r to you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (836, 'enUS', 'Carnage!  May I spill blood in His name!', 'Carnage!  May I spill blood in His name!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (837, 'enUS', 'Embrace the end!', 'Embrace the end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (838, 'enUS', 'Battle!  I can smell your fear, $c!', 'Battle!  I can smell your fear, $c!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (839, 'enUS', 'Death comes!  Rejoice!', 'Death comes!  Rejoice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (840, 'enUS', 'Intruders!  Good, I was getting bored...', 'Intruders!  Good, I was getting bored...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (841, 'enUS', 'Stop!  Foolish $c, we cannot let you summon the creature Myzrael!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (842, 'enUS', '', 'What?  $n, you served me well, but...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (843, 'enUS', '', 'Why have you summoned me so soon?  I haven\'t yet reached my full power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (844, 'enUS', '', 'No matter.  You were a fool to help me, and now you will pay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (845, 'enUS', 'Ok, $n. Follow me to the cave where I\'ll attempt to harness the power of the rune stone into these goggles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (846, 'enUS', 'I discovered this cave on our first day here.  I believe the energy in the stone can be used to our advantage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (847, 'enUS', 'I\'ll begin drawing energy from the stone.  Your job, $n, is to defend me. This place is cursed...trust me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (848, 'enUS', '%s begins tinkering with the goggles before the stone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (849, 'enUS', 'Almost done! Just a little longer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (850, 'enUS', 'I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (851, 'enUS', 'Phew! Glad to be back from that creepy cave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (852, 'enUS', 'Bah! Failed again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (853, 'enUS', '%s is ripped apart as it is banished from this world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (854, 'enUS', 'You\'ve plundered our treasures too long.  Prepare to meet your watery grave!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (855, 'enUS', 'Nothing will stop us!  You will die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (856, 'enUS', '%s frantically whispers something to Shake\'s O\'Breen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (857, 'enUS', 'Wait, $n!!! It looks like we\'re going to need some help here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (858, 'enUS', 'Help!! I told you this place was cursed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (859, 'enUS', 'Help!!! Get these things off me so I can get my work done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (860, 'enUS', '%s carefully studies Sara Balloo\'s note and lets out a long, solemn sigh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (861, 'enUS', 'What is this power that flows through me? Is this the power of shamanism that Tor\'gan spoke of...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (862, 'enUS', 'Amazing! I feel... renewed. My strength returns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (863, 'enUS', 'If we can just hold them now, I am sure we will be in the clear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (864, 'enUS', 'Here we go...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (865, 'enUS', 'I\'ll go secure below.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (866, 'enUS', 'Captain is dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (867, 'enUS', 'We survived!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (868, 'enUS', '%s winks at $n knowingly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (869, 'enUS', '%s nods his head at $n, and sighs thoughtfully.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (870, 'enUS', '%s begins searching through his pack...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (871, 'enUS', 'Ah!  There is my ratchet!  Now let\'s re-attach these scroll fragments.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (872, 'enUS', 'There we are, $n.  The Scroll is fixed!  Hooray!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (873, 'enUS', '', '%s begins to search through the badges of fallen Stromgarde defenders...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (874, 'enUS', '', 'Privates Marcus, Tremane, Osgood, Lampret...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (875, 'enUS', '', 'Privates Fontane, MacDugal, Wilcott...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (876, 'enUS', '', 'Privates Theoric, Kitrin, Blythe, Agmond...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (877, 'enUS', '', 'Privates Horus, Tilling, Rockwell...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (878, 'enUS', '', '...Sampson, Yorick, Chamley...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (879, 'enUS', '', 'Clemens, Bartholemew, Enderman, Wyles...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (880, 'enUS', '', 'Tormil, Gamlor, Shefferd, Cowry...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (881, 'enUS', '', 'Martens, Reblock, Adinay, Nikeson...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (882, 'enUS', '', '%s sighs...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (883, 'enUS', '', 'So many lost.  And so many more we must lose before this battle ends.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (884, 'enUS', '', 'Thank you for these badges, $n.  I will send news to the families of the deceased.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (885, 'enUS', '%s concocts a potion for $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (886, 'enUS', 'Ribbit! No!! This cannot...ribbit...be! You have duped me with...ribbit...your foul trickery! Ribbit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (887, 'enUS', '%s cooks up a batch of spicy hot buzzard wings for $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (888, 'enUS', 'The nightmare of Ursal is over! We are free!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (889, 'enUS', '%s hands the glowing goggles over to Doctor Draxlegauge.', '', 26124);
