INSERT INTO `broadcast_text_locale` VALUES (2525, 'enUS', 'Greetings.  I\'m a gnome mage trainer, and you\'re a mage.', 'Greetings.  I\'m a gnome mage trainer, and you\'re a mage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2526, 'enUS', 'Well met, $c.  You want some advice?  Befriend a mage!  We have a trick or two up our sleeves, we do...', 'Well met, $c.  You want some advice?  Befriend a mage!  We have a trick or two up our sleeves, we do...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2527, 'enUS', 'Greetings.  I\'m an undead mage trainer, and you\'re a mage.', 'Greetings.  I\'m an undead mage trainer, and you\'re a mage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2528, 'enUS', 'Well met, $c.  My advice to you is this: As you travel the world, be wary of magic for it will burn the untrained.', 'Well met, $c.  My advice to you is this: As you travel the world, be wary of magic for it will burn the untrained.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2529, 'enUS', 'Greetings, $c.  Are you here to learn of the hidden arts?', 'Greetings, $c.  Are you here to learn of the hidden arts?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2530, 'enUS', 'Greetin\'s, $c.  I be pityin\' your kind, for de secrets of true magic are beyond ya...', 'Greetin\'s, $c.  I be pityin\' your kind, for de secrets of true magic are beyond ya...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2541, 'enUS', '%s calls for help!', '%s calls for help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2542, 'enUS', 'Greetings.  I\'m a warlock trainer, and you\'re a warlock.', 'Greetings.  I\'m a warlock trainer, and you\'re a warlock.', 26124);
