INSERT INTO `broadcast_text_locale` VALUES (2546, 'enUS', 'With the sanction of Lady Sylvanas, the Deathstalkers\' numbers grow. More blades to strike at our enemies when they least expect it.', 'With the sanction of Lady Sylvanas, the Deathstalkers\' numbers grow. More blades to strike at our enemies when they least expect it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2547, 'enUS', 'Return me to life.', 'Return me to life.', 26124);
