INSERT INTO `broadcast_text_locale` VALUES (2549, 'enUS', 'Here\'s another load for ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2550, 'enUS', '%s sets the load of wood down with a heavy thud.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2551, 'enUS', 'A\'ight, just put it down over there and I will get to it.', '', 26124);
