INSERT INTO `broadcast_text_locale` VALUES (2566, 'enUS', 'Astor? Taken your bloody time getting here, so let\'s not waste any time, shall we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2567, 'enUS', 'Now which one of these did I put it in...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2568, 'enUS', 'Ah, here it is.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2569, 'enUS', 'You will talk eventually.  You might as well spill it now.', 'You will talk eventually.  You might as well spill it now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2570, 'enUS', 'Confess and we shall set you free.', 'Confess and we shall set you free.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2571, 'enUS', 'What?  Oh no, I don\'t care what you have to say.  I just enjoy inflicting pain.', 'What?  Oh no, I don\'t care what you have to say.  I just enjoy inflicting pain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2582, 'enUS', 'I\'m a cloth vendor, and this is my default greeting.', 'I\'m a cloth vendor, and this is my default greeting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2583, 'enUS', 'I would like to buy from you.', 'I would like to buy from you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2584, 'enUS', 'I am an undead cloth vendor, and this is my greeting text.', 'I am an undead cloth vendor, and this is my greeting text.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2585, 'enUS', 'Welcome, and feel free to examine my goods.  Other than the moldy smell, they are in perfect condition.', 'Welcome, and feel free to examine my goods.  Other than the moldy smell, they are in perfect condition.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2586, 'enUS', 'I am interested in tailor training.', 'I am interested in tailor training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2587, 'enUS', 'I am a troll tailor trainer, and this is my default greeting.', 'I am a troll tailor trainer, and this is my default greeting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2588, 'enUS', 'What do you want, $c? I\'m running late, and my employer doesn\'t look kindly on tardiness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2589, 'enUS', 'You\'re Astor Hadren, right?', 'You\'re Astor Hadren, right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2590, 'enUS', 'Yes, I am. What\'s it to you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2591, 'enUS', 'You\'ve got something I need, Astor. And I\'ll be taking it now.', 'You\'ve got something I need, Astor. And I\'ll be taking it now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2592, 'enUS', '', 'Prepare yourself, $n.  I go to open the rift...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2601, 'enUS', 'Argh! What was that? It\'s not supposed to work like this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2602, 'enUS', 'The Shattered Hand will see the Horde to their former glory.', 'The Shattered Hand will see the Horde to their former glory.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2603, 'enUS', 'I would like training.', 'I would like training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2621, 'enUS', 'Mennet said I should ask you for a set of thieves\' tools.', 'Mennet said I should ask you for a set of thieves\' tools.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2622, 'enUS', '', 'Let me go get you a set of tools, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2623, 'enUS', '', 'Ah, here\'s one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2624, 'enUS', '', 'Good luck out there, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2625, 'enUS', 'You carry the taint of the Scourge.  Prepare to enter the Twisting Nether.', 'You carry the taint of the Scourge.  Prepare to enter the Twisting Nether.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2626, 'enUS', 'There is no escape for you.  The Crusade shall destroy all who carry the Scourge\'s taint.', 'There is no escape for you.  The Crusade shall destroy all who carry the Scourge\'s taint.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2627, 'enUS', 'The Light condemns all who harbor evil.  Now you will die!', 'The Light condemns all who harbor evil.  Now you will die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2628, 'enUS', 'The Scarlet Crusade shall smite the wicked and drive evil from these lands!', 'The Scarlet Crusade shall smite the wicked and drive evil from these lands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2629, 'enUS', '', 'Gird yourself, $n, for the demon in this orb is a fel beast.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2630, 'enUS', 'I need another set of tools.', 'I need another set of tools.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2631, 'enUS', 'Hah!  Eternal undeath to the cursed Agamands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2632, 'enUS', 'Thank you, $n.  You have tempered the cold rage in my heart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2633, 'enUS', 'If Gnoarn finds out about this, it\'ll be my hide!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2634, 'enUS', 'Your armor is ready, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2635, 'enUS', 'I have completed your helm, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2641, 'enUS', 'What\'s a young $c like you out visiting an old farmer like me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2642, 'enUS', 'If Therzok\'s sent you, tell him no, I won\'t go back to work for him, no matter how many times he asks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2643, 'enUS', 'I need another set of thieves\' tools.', 'I need another set of thieves\' tools.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2644, 'enUS', '', '%s begins to craft...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2645, 'enUS', '', 'I have finished your leg guards, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2646, 'enUS', 'I am done.  Your armor is ready, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2647, 'enUS', '', 'These are fine pieces, $n.  Let me get started on your robe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2648, 'enUS', '', 'That\'s it!  It\'s all finished, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2649, 'enUS', '', 'Ok, let\'s get to work!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2650, 'enUS', '', 'Aha!  There it is.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2651, 'enUS', '', 'Now where did I put that silver spool...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2652, 'enUS', 'Harroc, get a box of tools for, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2653, 'enUS', 'Right away, Rokar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2654, 'enUS', 'There you go. I need to get back to work before Rokar clouts me again for being lazy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2655, 'enUS', 'Release the hounds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2656, 'enUS', '', 'I just have one thing to check...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2657, 'enUS', '', 'That\'s it!  It worked!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2658, 'enUS', '', 'Thanks for your patience, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2659, 'enUS', '', 'I\'ll just keep this here until we need it...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2660, 'enUS', 'Naralex sleeps again!$b$bLong ago, Naralex journeyed to this cavern with the honorable goal of entering the Emerald Dream and regrowing the Barrens into a lush forest. His focus waned and his thoughts became tainted by serpentine visions.$b$bNow, reptilian beasts seep from his dreams to the land. He must be awoken from the nightmare or he will be forever lost, and a great evil unleashed.$b$bOnly after his corrupted servants, the Fanglords, have been slain can I perform the awakening ritual.\n\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2661, 'enUS', 'The Fanglords are dead! All hope is not lost!$b$bThe awakening ritual is quite complex, however. It cannot be done alone.$b$bI will handle all of the necessary preparations but I will need you to protect me. The nightmares summoned from Naralex\'s journey through the Emerald Dream prove to be quite formidable. Without your help, this mission is doomed.$b$bWhen you are ready to defend me, say the word, and I shall lead the way to Naralex\'s chamber.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2662, 'enUS', 'Let the event begin!', 'Let the event begin!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2663, 'enUS', '', 'Ok, let\'s get this orb made...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2664, 'enUS', '', 'All right.  It\'s made!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2665, 'enUS', 'I shall now begin the robe...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2666, 'enUS', 'Your robe is complete, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2681, 'enUS', '', 'Here, $n.  Take a chest of containment coffers and a cantation of manifestation.  You will need them for your mission.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2682, 'enUS', '', 'Take a chest of containment coffers and a cantation of manifestation, $n.  You will need them for your mission.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2701, 'enUS', '%s sets down the refined wood planks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2702, 'enUS', 'The machine adjusts to face you.$B$BOn the back of the machine is an empty, oddly misshapen slot with an engraving that reads: \"Propirtee o\' Cap\'n Gelkak Gyromast\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2703, 'enUS', 'Turn the key to start the machine.', 'Turn the key to start the machine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2704, 'enUS', 'YARRR! Swabie, what have ye done?! He\'s gone mad! Baton down the hatches! Hoist the mast! ARRRR! Every man for hi\'self!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2705, 'enUS', 'I\'m \"Plucky\" Johnson, and this is my default greeting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2706, 'enUS', 'Hey!  Are you on an errand for Tirth?  If I were you I\'d get as far away from him as I could!  His mind\'s been pickled from too much drink but his magic\'s as strong as ever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2707, 'enUS', 'Tell me the phrase to Tirth\'s lockbox.', 'Tell me the phrase to Tirth\'s lockbox.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2708, 'enUS', '', 'Stand back, and ... hold your breath.  I haven\'t done this in a while.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2709, 'enUS', '', 'It worked!  Amazing!  I guess I haven\'t lost my touch...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2721, 'enUS', 'Sure, I\'ll tell you the phrase.  As long as I don\'t have to go anywhere near Tirth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2722, 'enUS', 'Don\'t tell Tirth I\'m still here.  If he finds out I haven\'t left the Shimmering Flats, I\'ll be running his errands and cleaning his dirty laundry forever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2741, 'enUS', 'Gelkak\'s key slides into the vacant slot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2742, 'enUS', 'Do you mock me while I am unable to defend myself, bound to this ball of iron?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2743, 'enUS', 'How can I free you?', 'How can I free you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2744, 'enUS', 'One of the humans has the key to this infernal contraption. Eston I think they said his name was.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2745, 'enUS', 'Get away from here, $c, or they\'ll put you in chains as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2746, 'enUS', 'Where is the key to this lock?', 'Where is the key to this lock?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2747, 'enUS', 'Marlgen... One of the jailors. He has the key to my bonds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2748, 'enUS', 'Good day citizen.  How may I assist you?', 'Good day citizen.  How may I assist you?\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2749, 'enUS', 'Bank of Stormwind', 'Bank of Stormwind', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2750, 'enUS', 'The bank is located in the Trade District just northwest of the city gates. Here, let me show you on your map.', 'The bank is located in the Trade District just northwest of the city gates. Here, let me show you on your map.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2751, 'enUS', 'I wish the snow would just all melt away. It\'s always getting my robe wet and messing up everything!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2752, 'enUS', 'Stupid snow... Hey, you\'re, $n, aren\'t you? I\'ve just received a message asking for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2753, 'enUS', 'What message?', 'What message?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2754, 'enUS', 'You\'ve been summoned by Lago Blackwrench, warlock master of Ironforge. You\'d do yourself well to report to him immediately. You\'ll find him in the Forlorn Cavern district of the city.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2755, 'enUS', 'Greetings $R, I am Tharnariun Treetender.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2756, 'enUS', 'I require training.', 'I require training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2757, 'enUS', 'The animal looks confused.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2761, 'enUS', 'One moment while I stitch this together...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2762, 'enUS', 'There.  I am finished with your robe, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2763, 'enUS', '%s whispers into $n\'s ear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2764, 'enUS', 'If you ask me, I think the goblins are a shoe in.  We have the tools, and we have the talent!', 'If you ask me, I think the goblins are a shoe in.  We have the tools, and we have the talent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2765, 'enUS', 'If it weren\'t for that cursed, gnomish luck, they\'d never win a race!', 'If it weren\'t for that cursed, gnomish luck, they\'d never win a race!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2766, 'enUS', 'If I were you, I\'d buy a goblin race voucher.  Rooting for the other team is a waste of time.', 'If I were you, I\'d buy a goblin race voucher.  Rooting for the other team is a waste of time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2767, 'enUS', 'My money\'s on the goblins!  And if you have any sense then yours is too!', 'My money\'s on the goblins!  And if you have any sense then yours is too!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2768, 'enUS', 'I heard they were going to try to blow up the gnome\'s car in the next race!  True or not, I\'d stand clear of their pit area.', 'I heard they were going to try to blow up the gnome\'s car in the next race!  True or not, I\'d stand clear of their pit area.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2769, 'enUS', 'Where is Tirth\'s assistant?', 'Where is Tirth\'s assistant?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2773, 'enUS', 'Oh, you mean Johnson?  I heard he\'s in hiding.', 'Oh, you mean Johnson?  I heard he\'s in hiding.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2774, 'enUS', 'If it weren\'t for the goblins\' inconceivable, dumb luck, they\'d crash their car during every race!', 'If it weren\'t for the goblins\' inconceivable, dumb luck, they\'d crash their car during every race!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2775, 'enUS', 'These races are a great opportunity to show our ingenuity!', 'These races are a great opportunity to show our ingenuity!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2776, 'enUS', 'I installed a new device in our car\'s matrix regulometer.  That should increase its velocity quotient by a factor of no less than fifteen!', 'I installed a new device in our car\'s matrix regulometer.  That should increase its velocity quotient by a factor of no less than fifteen!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2777, 'enUS', 'My son could design a faster car than that clunky goblin junk heap! ', 'My son could design a faster car than that clunky goblin junk heap!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2778, 'enUS', 'My Gizmotic Almanacotron says that we should beat the goblins by twenty seconds more than we did last time!', 'My Gizmotic Almanacotron says that we should beat the goblins by twenty seconds more than we did last time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2779, 'enUS', 'Where is Tirth\'s assistant?', 'Where is Tirth\'s assistant?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2780, 'enUS', 'Tirth\'s not himself these days, and neither is his assistant.', 'Tirth\'s not himself these days, and neither is his assistant.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2781, 'enUS', 'Johnson?  I think he\'s around here somewhere...', 'Johnson?  I think he\'s around here somewhere...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2782, 'enUS', 'Hah!  Tirth\'s been looking for him for days, but he doesn\'t want to be found.  He\'s chicken!', 'Hah!  Tirth\'s been looking for him for days, but he doesn\'t want to be found.  He\'s chicken!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2783, 'enUS', 'Bears??', 'Bears??', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2801, 'enUS', 'At last! I am finally free from this cell!$b$bThe Kirin Tor sent me from Dalaran to investigate Arugal\'s progress with the undead here in Silverpine Forest. Little did we know that the wizard had lost his grasp on sanity.$b$bI must report this mess at once! But before I leave, allow me to undo the magical lock on the courtyard door.$b$bYou defeated Rethilgore, perhaps you will survive what lies beyond....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2802, 'enUS', 'Please unlock the courtyard door.', 'Please unlock the courtyard door.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2803, 'enUS', 'At last! Someone to free me from this cell!$b$bHigh Executor Hadrec sent us to gather information on the Keep so that a plan could be formulated to overthrow Arugal once and for all.$b$bBut the old wizard has many tricks up his sleeve and we were detected by a magical ward. I was thrown in this prison. Vincent was not so lucky.$b$bI must return to Hadrec to debrief him at once. But first I will pick the lock to the courtyard door for you. Perhaps you can try your luck against the foes that lurk beyond.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2804, 'enUS', 'The curing cantrip has failed. Unfortunately, the introduction of the serum into this beast\'s blood stream will soon end its life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2805, 'enUS', 'Release the Rabid Thistle Bear for Tharnariun.', 'Release the Rabid Thistle Bear for Tharnariun.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2806, 'enUS', 'Well done. Release the bear into my custody so that I may apply the curing cantrip.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2807, 'enUS', 'I once followed the ways of the Twilight\'s Hammer. Obsessed with reviving the Old Gods, I journeyed deep into this lair to try to draw power from the cursed beast, Aku\'mai!$b$bAfter witnessing the sacrifice of countless innocents, I came to my senses.$b$bI was trapped but now I am free! But so far from my homeland of Khaz Modan!$b$bBrave adventurer, I offer you passage to a very special land in return for slaying Aku\'mai. If you wish, I can port you to the city of Darnassus.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2821, 'enUS', 'Welcome to my Inn, weary traveler. What can I do for you?', 'Welcome to my Inn, weary traveler. What can I do for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2822, 'enUS', 'Make this inn your home.', 'Make this inn your home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2823, 'enUS', 'Let me browse your goods.', 'Let me browse your goods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2824, 'enUS', 'Rest your weary bones for a spell.', 'Rest your weary bones for a spell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2825, 'enUS', 'As the wind on the plains, you are always welcome here.', 'As the wind on the plains, you are always welcome here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2826, 'enUS', 'Fill yer tankard and pull up a chair. We\'ve stories to tell and kegs to empty.', 'Fill yer tankard and pull up a chair. We\'ve stories to tell and kegs to empty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2827, 'enUS', 'Your body looks worn and your spirit weary. Rest yourself beneath our roof and allow Elune to guide your dreams.', 'Your body looks worn and your spirit weary. Rest yourself beneath our roof and allow Elune to guide your dreams.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2828, 'enUS', 'Come in, come in. Just don\'t sit too close to the stove. It hasn\'t exploded yet today, but just to be safe I think you should sit elsewhere.', 'Come in, come in. Just don\'t sit too close to the stove. It hasn\'t exploded yet today, but just to be safe I think you should sit elsewhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2829, 'enUS', '%s begins to bubble!', '%s begins to bubble!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2841, 'enUS', 'Inns are places where you can rest after your adventures.  If you do not stay at an inn such as this, then you will not be fully rested when you venture again into the wilderness.$B$BYou may also acquire a hearthstone at any inn, and later when you are ready to rest you can use this heartstone to bring you back to the inn from which you acquired it.', 'Inns are places where you can rest after your adventures.  If you do not stay at an inn such as this, then you will not be fully rested when you venture again into the wilderness.$B$BYou may also acquire a hearthstone at any inn, and later when you are ready to rest you can use this heartstone to bring you back to the inn from which you acquired it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2842, 'enUS', 'The master has fallen!  Avenge him, my brethren!', 'The master has fallen!  Avenge him, my brethren!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2843, 'enUS', '', 'Hello $r, it is a pleasure to make your acquaintance.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2844, 'enUS', 'My strider requires training.', 'My strider requires training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2845, 'enUS', '', 'Hello, $n. Are you interested in outdoor survival? Sustenance off the flora and fauna of the lands!$B$BYou could learn a thing or two from me, that is, if you\'re interested.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2846, 'enUS', 'Please port me to Darnassus.', 'Please port me to Darnassus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2847, 'enUS', 'Infidels! They must be purified!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2848, 'enUS', 'Aku\'mai is dead! At last, I can leave this wretched place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2849, 'enUS', 'Speak with me to hear my tale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2850, 'enUS', 'Hail, what ya want?', 'Hail, what ya want?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2861, 'enUS', 'Well, well... look what the cat brung in... and how can I help you, $c?', 'Well, well... look what the cat brung in... and how can I help you, $c?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2862, 'enUS', 'I am in need of training, Keryn.', 'I am in need of training, Keryn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2863, 'enUS', 'Flight Master', 'Flight Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2864, 'enUS', 'You can find the gryphon master on the northeast rampart overlooking the Valley of Heroes.', 'You can find the gryphon master on the northeast rampart overlooking the Valley of Heroes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2865, 'enUS', 'What\'s that noise?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2866, 'enUS', 'I smell an intruder...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2867, 'enUS', 'The Brotherhood will not be hampered by insects.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2868, 'enUS', 'Class Trainer', 'Class Trainer', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2869, 'enUS', 'Profession Trainer', 'Profession Trainer', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2870, 'enUS', 'Guild Master & Vendor', 'Guild Master & Vendor', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2871, 'enUS', 'You\'re looking a bit lost.  Can I help you find something?', 'You\'re looking a bit lost.  Can I help you find something?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2872, 'enUS', 'Greetings!', 'Greetings!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2873, 'enUS', 'Yes?', 'Yes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2874, 'enUS', 'Look alive, dogs! We got an operation to run here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2875, 'enUS', 'What do you want?', 'What do you want?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2876, 'enUS', 'Ahhh, you are looking for Dungar Longdrink. He has some of the fastest gryphons this side of Khaz Modan! You can find him up on the ramparts in the Trade District.', 'Ahhh, you are looking for Dungar Longdrink. He has some of the fastest gryphons this side of Khaz Modan! You can find him up on the ramparts in the Trade District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2877, 'enUS', 'Lookin\' to take a gryphon ride, eh? Dungar keeps his birds up on the rampart in the Trade District. Just remember to hold on tight! ', 'Lookin\' to take a gryphon ride, eh? Dungar keeps his birds up on the rampart in the Trade District. Just remember to hold on tight! ', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2878, 'enUS', 'Gryphons, eh? Never really cared for the beasts but to each their own.\n\nYou can find Dungar Longdrink on the rampart in the Trade District.', 'Gryphons, eh? Never really cared for the beasts but to each their own.\n\nYou can find Dungar Longdrink on the rampart in the Trade District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2879, 'enUS', 'You can find Aldwin Laughlin at the Stormwind Visitor\'s Center in the Trade District.', 'You can find Aldwin Laughlin at the Stormwind Visitor\'s Center in the Trade District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2880, 'enUS', 'Gonna make a guild, are ya? Good luck to ya then!\n\nTalk to Aldwin Laughlin in the Stormwind Vistor\'s Center. It\'s in the Trade District right as you come in from the Valley of Heroes.', 'Gonna make a guild, are ya? Good luck to ya then!\n\nTalk to Aldwin Laughlin in the Stormwind Vistor\'s Center. It\'s in the Trade District right as you come in from the Valley of Heroes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2881, 'enUS', 'Ahhh, Stormwind Visitor\'s Center is what you are looking for then. It\'s the first building on the left as you walk through the front gates from the Valley of Heroes.', 'Ahhh, Stormwind Visitor\'s Center is what you are looking for then. It\'s the first building on the left as you walk through the front gates from the Valley of Heroes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2901, 'enUS', 'Which class trainer are you looking for?', 'Which class trainer are you looking for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2902, 'enUS', 'Mage', 'Mage', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2903, 'enUS', 'The magi congregate in the Wizard\'s Sanctum in the Mage Quarter. Quite a tower they have there. Makes our barracks look like peasant hovels.', 'The magi congregate in the Wizard\'s Sanctum in the Mage Quarter. Quite a tower they have there. Makes our barracks look like peasant hovels.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2904, 'enUS', 'Oh, looking for some arcane enlightenment are you? No place better then then Wizard\'s Sanctum in the Mage Quarter.', 'Oh, looking for some arcane enlightenment are you? No place better then then Wizard\'s Sanctum in the Mage Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2905, 'enUS', 'Sounds like you want to talk to Maginor Dumas! You can usually find him in the Wizard\'s Sanctum in the Mage Quarter. Course, you never know when or where those magi will portal off to.', 'Sounds like you want to talk to Maginor Dumas! You can usually find him in the Wizard\'s Sanctum in the Mage Quarter. Course, you never know when or where those magi will portal off to.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2906, 'enUS', 'Rogue', 'Rogue', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2907, 'enUS', 'You want to talk to Osborne then. No finer teacher of the stealth arts than him in Stormwind. Look for him over in Old Town.', 'You want to talk to Osborne then. No finer teacher of the stealth arts than him in Stormwind. Look for him over in Old Town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2908, 'enUS', 'Bah, rogues have done nothing but make our job here in Stormwind harder. I wish I could drag both Osborne and Mathias both out of Old Town and right to the Stockade.', 'Bah, rogues have done nothing but make our job here in Stormwind harder. I wish I could drag both Osborne and Mathias both out of Old Town and right to the Stockade.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2909, 'enUS', 'Old Town is where you should go if you want to find rogues. It\'s in the eastern corner of Stormwind.', 'Old Town is where you should go if you want to find rogues. It\'s in the eastern corner of Stormwind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2910, 'enUS', 'Warrior', 'Warrior', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2911, 'enUS', 'Warriors can usually be found either at the Pig and Whistle Tavern or the Barracks in Old Town. Tell ya though, the tavern is probably a better place to look.', 'Warriors can usually be found either at the Pig and Whistle Tavern or the Barracks in Old Town. Tell ya though, the tavern is probably a better place to look.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2912, 'enUS', 'Looking for some training, eh? I could train you better then anyone in Stormwind but the guard prohibits it. I guess you will have to settle for one of those has beens hanging out around the Barracks or at the Pig and Whistle Tavern in Old Town.', 'Looking for some training, eh? I could train you better then anyone in Stormwind but the guard prohibits it. I guess you will have to settle for one of those has beens hanging out around the Barracks or at the Pig and Whistle Tavern in Old Town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2913, 'enUS', 'Check around Old Town at the Barracks or the Pig and Whistle Tavern. If you go to the tavern, be sure to sample some of the fine brews. Puts the dwarven ales to shame, I tell ya.', 'Check around Old Town at the Barracks or the Pig and Whistle Tavern. If you go to the tavern, be sure to sample some of the fine brews. Puts the dwarven ales to shame, I tell ya.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2914, 'enUS', 'Druid', 'Druid', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2915, 'enUS', 'Well, you can find Theridan or Maldryn by the moonwell in the Park.  Just head to the western area of Stormwind, north of the Mage District, but west of the Cathedral Square.', 'Well, you can find Theridan or Maldryn by the moonwell in the Park.  Just head to the western area of Stormwind, north of the Mage District, but west of the Cathedral Square.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2916, 'enUS', 'The Park is where you want to go if you are looking for druid trainers. Theridan and Maldryn can usually be found by the moonwell in the center of the district.', 'The Park is where you want to go if you are looking for druid trainers. Theridan and Maldryn can usually be found by the moonwell in the center of the district.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2917, 'enUS', 'A child of nature are we? Then you need look no further then the moonwell in the Park.', 'A child of nature are we? Then you need look no further then the moonwell in the Park.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2918, 'enUS', 'Priest', 'Priest', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2919, 'enUS', 'The Cathedral of Light in Cathedral Square is home to the priests of Stormwind. There you will find High Priestess Laurena.', 'The Cathedral of Light in Cathedral Square is home to the priests of Stormwind. There you will find High Priestess Laurena.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2920, 'enUS', 'Laurena is the High Priestess of Stormwind. You can find her in the Catheral of Light in Cathedral Square.', 'Laurena is the High Priestess of Stormwind. You can find her in the Catheral of Light in Cathedral Square.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2921, 'enUS', 'I would suggest looking in the Cathedral of Light in Cathedral Square.', 'I would suggest looking in the Cathedral of Light in Cathedral Square.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2922, 'enUS', 'Paladin', 'Paladin', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2923, 'enUS', 'If you are looking to become a mighty paladin, there is no better teacher then Lord Grayson Shadowbreaker. He can be found in the Cathedral of Light in Cathedral Square.', 'If you are looking to become a mighty paladin, there is no better teacher then Lord Grayson Shadowbreaker. He can be found in the Cathedral of Light in Cathedral Square.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2924, 'enUS', 'Hunter', 'Hunter', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2925, 'enUS', 'Thorfin Stoneshield came to us all the way from Ironforge to bring us his expertise in hunting.  If you don\'t mind breathing the smoke filled air of the Dwarven District then you\'ll find him at his house in the northeast area of the district.', 'Thorfin Stoneshield came to us all the way from Ironforge to bring us his expertise in hunting.  If you don\'t mind breathing the smoke filled air of the Dwarven District then you\'ll find him at his house in the northeast area of the district.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2926, 'enUS', 'Warlock', 'Warlock', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2927, 'enUS', 'What? I don\'t know of any warlock trainers in Stormwind. \n\nAlthough... There have been some sightings of demonic activity over by The Slaughtered Lamb in the Mage Quarter. I suppose you could check there.', 'What? I don\'t know of any warlock trainers in Stormwind. \n\nAlthough... There have been some sightings of demonic activity over by The Slaughtered Lamb in the Mage Quarter. I suppose you could check there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2928, 'enUS', 'What brings you to this part of the world, $n?', 'What brings you to this part of the world, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2929, 'enUS', 'Good day to you, Hogral! I seek training.', 'Good day to you, Hogral! I seek training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2930, 'enUS', 'He grows fatter and more corrupt with each passing day. Ye can smell the stink comin\' off o\' his body. He be dirty, an\' I dun just mean he be needin\' a wash.$B$BEnough babblin\' from Hulfdan. Where did ye come from and how can me help ye, $c?', 'He grows fatter and more corrupt with each passing day. Ye can smell the stink comin\' off o\' his body. He be dirty, an\' I dun just mean he be needin\' a wash.$B$BEnough babblin\' from Hulfdan. Where did ye come from and how can me help ye, $c?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2931, 'enUS', 'Good day, Hulfdan, I am looking for training.', 'Good day, Hulfdan, I am looking for training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2941, 'enUS', 'Which profession trainer are you looking for?', 'Which profession trainer are you looking for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2942, 'enUS', 'Blacksmithing', 'Blacksmithing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2943, 'enUS', 'Engineering', 'Engineering', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2944, 'enUS', 'Mining', 'Mining', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2945, 'enUS', 'Cooking', 'Cooking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2946, 'enUS', 'Lockpicking', 'Lockpicking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2947, 'enUS', 'Leatherworking', 'Leatherworking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2948, 'enUS', 'Skinning', 'Skinning', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2949, 'enUS', 'First Aid', 'First Aid', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2950, 'enUS', 'Herbalism', 'Herbalism', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2951, 'enUS', 'Tailoring', 'Tailoring', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2952, 'enUS', 'Alchemy', 'Alchemy', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2953, 'enUS', 'If you need training in alchemy, there is only one place in Stormwind you need to go... Alchemy Needs. You can find it in the Mage Quarter along the outer rim of shops.', 'If you need training in alchemy, there is only one place in Stormwind you need to go... Alchemy Needs. You can find it in the Mage Quarter along the outer rim of shops.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2954, 'enUS', 'Therum Deepforge is the dwarf you want to talk to. One of the finest blacksmiths around if you ask me. You can find him in the heart of the Dwarven District.', 'Therum Deepforge is the dwarf you want to talk to. One of the finest blacksmiths around if you ask me. You can find him in the heart of the Dwarven District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2955, 'enUS', 'If you are looking to become a great cook, venture over to the Pig and Whistle Tavern in Old Town and talk to Stephen Ryback. That man knows how to cook up a rack of ribs like no other.\n\nMan... now you got me hungry for some of his famous ribs. Don\'t count on him teaching you that recipe though!', 'If you are looking to become a great cook, venture over to the Pig and Whistle Tavern in Old Town and talk to Stephen Ryback. That man knows how to cook up a rack of ribs like no other.\n\nMan... now you got me hungry for some of his famous ribs. Don\'t count on him teaching you that recipe though!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2956, 'enUS', 'Stormwind\'s resident engineer trainer is an ingenious gnome by the name of Lilliam Sparkspindle. He can be found in the back of the Dwarven District.', 'Stormwind\'s resident engineer trainer is an ingenious gnome by the name of Lilliam Sparkspindle. He can be found in the back of the Dwarven District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2957, 'enUS', 'The Cathedral of Light is the center of all the healing practices of Stormwind. There you can find Angela Leifeld to help you learn this craft.', 'The Cathedral of Light is the center of all the healing practices of Stormwind. There you can find Angela Leifeld to help you learn this craft.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2958, 'enUS', 'You can find Tannysa standing outside Alchemy Needs in the Mage Quarter.', 'You can find Tannysa standing outside Alchemy Needs in the Mage Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2959, 'enUS', 'Want to learn leatherworking, eh? Simon Tanner would be able to teach you. He can be found in Old Town at the Protective Hide.', 'Want to learn leatherworking, eh? Simon Tanner would be able to teach you. He can be found in Old Town at the Protective Hide.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2960, 'enUS', 'Don\'t know why you would need a skill like that? Only dishonest folk would want to learn how to pick locks.\n\nI guess if you wanted to learn, the best place to look would be over in Old Town. Probably in one of those back alleys.', 'Don\'t know why you would need a skill like that? Only dishonest folk would want to learn how to pick locks.\n\nI guess if you wanted to learn, the best place to look would be over in Old Town. Probably in one of those back alleys.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2961, 'enUS', 'Gelman Stonehand would be the one to talk to. I think he has a house over in the Dwarven District but I can\'t remember exactly. I would check over there.', 'Gelman Stonehand would be the one to talk to. I think he has a house over in the Dwarven District but I can\'t remember exactly. I would check over there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2962, 'enUS', 'You are gonna want to head over to Old Town and drop by the Protective Hide if you are lookin\' to learn skinning.', 'You are gonna want to head over to Old Town and drop by the Protective Hide if you are lookin\' to learn skinning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2963, 'enUS', 'Duncan\'s Textiles employs one of the finest tailors in Stormwind, Georgio Bolero. Look for the shop over in the Mage Quarter.', 'Duncan\'s Textiles employs one of the finest tailors in Stormwind, Georgio Bolero. Look for the shop over in the Mage Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2964, 'enUS', 'Plucky doesn\'t want Tirth to know he\'s still in the Shimmering Flats, so he turned himself into a chicken!$B$BIf you find Plucky, beckon him to you.  Maybe he\'ll come out of hiding.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2967, 'enUS', 'I hope yer ready to work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2968, 'enUS', 'Welcome to the races!  Enjoy your stay, and don\'t bet all your money in one place!', 'Welcome to the races!  Enjoy your stay, and don\'t bet all your money in one place!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2969, 'enUS', 'Hello, $c!  Welcome to the races at the Shimmering Flats!', 'Hello, $c!  Welcome to the races at the Shimmering Flats!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2970, 'enUS', 'Who has the fastest car?  Who knows?!?', 'Who has the fastest car?  Who knows?!?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2971, 'enUS', 'The goblin racer packs a lot of punch, but the gnomes hid a trick or two in their car...', 'The goblin racer packs a lot of punch, but the gnomes hid a trick or two in their car...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2972, 'enUS', '%s begins to billow smoke and catches on fire! You get the feeling you should RUN!', '%s begins to billow smoke and catches on fire! You get the feeling you should RUN!', 26124);
