INSERT INTO `broadcast_text_locale` VALUES (2976, 'enUS', 'Should you choose to follow me, be on guard. The animals and beasts of the Darkshore will not harm me but they may attempt to harm you, adventurer.', 'Should you choose to follow me, be on guard. The animals and beasts of the Darkshore will not harm me but they may attempt to harm you, adventurer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2977, 'enUS', 'Like a cat dragged in from the rain, you scratch and claw, then do it all over again... I\'ll be back for more...$B$BOh, I didn\'t see you enter, $n! What can I do for you?', 'Like a cat dragged in from the rain, you scratch and claw, then do it all over again... I\'ll be back for more...$B$BOh, I didn\'t see you enter, $n! What can I do for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (2978, 'enUS', 'Hello! I am a Rogue in need of training.', 'Hello! I am a Rogue in need of training.', 26124);
