INSERT INTO `broadcast_text_locale` VALUES (3009, 'enUS', 'Nothing like spending a day down at Crystal Lake fishing. Arnold Leland is the man that taught me. You can usually find him out on the dock in the Canal District just fishing the day away. \n\nOh, and don\'t believe any of the stories he tells you about monsters in the canals either.', 'Nothing like spending a day down at Crystal Lake fishing. Arnold Leland is the man that taught me. You can usually find him out on the dock in the Canal District just fishing the day away. \n\nOh, and don\'t believe any of the stories he tells you about monsters in the canals either.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (3010, 'enUS', 'Lucan Cordell has a small shop on the canal by the Stockade. I am sure he will be able to teach you the art of enchanting.', 'Lucan Cordell has a small shop on the canal by the Stockade. I am sure he will be able to teach you the art of enchanting.', 26124);
