INSERT INTO `broadcast_text_locale` VALUES (5854, 'enUS', '', 'Get that head and it\'s sure to give Daryl a fright. Not that he doesn\'t deserve it, the bloody arrogant fool. But ha! I can\'t wait to see the look on his face when you show it to him.', 26124);
