INSERT INTO `broadcast_text_locale` VALUES (6144, 'enUS', 'The Thorium Brotherhood Fall Catalogue:$B$BInside you will find the hottest deals on all of your adventuring needs! We\'ll even show you where to pick up those difficult to find components and supplies.', '', 26124);
