INSERT INTO `broadcast_text_locale` VALUES (6259, 'enUS', '', 'Ha ha, $n! I won\'t ever forget the look on Daryl\'s face when you showed him that head!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (6260, 'enUS', 'Leave this place!', '', 26124);
