INSERT INTO `broadcast_text_locale` VALUES (6265, 'enUS', '', 'Since we\'ve begun our stay in the crater, Dadanga\'s taken a liking to the large bloodpetal sprouts that grow all over the place here.$B$BI\'m sure she\'d enjoy a snack, if you feel like bringing her some...', 26124);
