INSERT INTO `broadcast_text_locale` VALUES (8458, 'enUS', 'Overflowin\' with rats! Help a pirate out, matey!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8459, 'enUS', 'Do yer part in keepin\' the Deeprun Tram clean!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8460, 'enUS', 'Don\'t crowd me, people!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8461, 'enUS', 'Your own strength feeds me, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8462, 'enUS', 'The little feller be hidin\' in one o\' them boxes, matey. G\'luck!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8463, 'enUS', 'Monty be me name an\' rats be me game.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8464, 'enUS', 'I\'m ready to find the squirrel, Monty.', 'I\'m ready to find the squirrel, Monty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8465, 'enUS', 'Into the box me pretties! Thas it. One by one ye go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8466, 'enUS', 'Winner! We be havin\' a winner here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8467, 'enUS', 'The strongest hides come from those kodo that have laid themselves to rest in this wasteland.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8468, 'enUS', 'Your skills have surpassed mine, young one. Go see Una who has a shop in Thunder Bluff\'s central plateau. She can give you the guidance you seek.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8470, 'enUS', 'I am sorry but I can not train any more apprentices. Seek out Mak in Thunder Bluff. He will be able to teach you the basics.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8471, 'enUS', 'First you must prove yourself to me, then I will train you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8472, 'enUS', 'Now you are ready to learn the finer points of kodo hide tanning. Let us begin...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8473, 'enUS', '', 'Between the light and the dark, there is shadow... and those shadows have powers you have yet to see.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8474, 'enUS', '', 'Between the light and the dark, there is shadow... and those shadows have powers you have yet to see.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8475, 'enUS', 'You\'ll find no finer rat kabob this side of Khaz Modan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8476, 'enUS', 'I wish to make a purchase, Nipsy.', 'I wish to make a purchase, Nipsy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8477, 'enUS', 'You look like you could use some work, $g fella:lady;. You should board the tram and visit my brother, Monty, at the Ironforge Deeprun Tram depot. I\'m sure he has something you could do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8478, 'enUS', 'Watch your back, stranger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8479, 'enUS', 'Ravenholdt Manor does not recognize you, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8480, 'enUS', 'Brash. Master Ravenholdt does not like brash.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8481, 'enUS', 'It is good to see you again, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8482, 'enUS', 'Greetings, $c. Have you spoken with Fahrad yet?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8483, 'enUS', 'The Syndicate is made up of a bunch of hacks - disorganized, unskilled, and lazy... ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8484, 'enUS', 'You have truly grown more cunning, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8485, 'enUS', 'An honor to see you again, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8486, 'enUS', 'I believe Simone was looking for you earlier, $n. Have you spoken with her yet?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8487, 'enUS', '$G Master:Mistress; $n! Master Kang has been asking about you! Perhaps it is your time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8488, 'enUS', 'Your presence steels our nerves, $G Master:Mistress; $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8489, 'enUS', 'Tales of your heroics are heard throughout Ravenholdt Manor, $G Master:Mistress; $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8490, 'enUS', 'I am unworthy of this conversation, $G Master:Mistress;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8491, 'enUS', 'It is my dream to one day serve under your command, $G Master:Mistress; $n. That is, should you ever deem me worthy of such things...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8492, 'enUS', 'The Manor has been abuzz with talk of your arrival, $G Master:Mistress; $n. We are all honored and graced by your presence.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8493, 'enUS', 'You dare set foot inside Ravenholdt Manor, worm?', 'You dare set foot inside Ravenholdt Manor, worm?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8494, 'enUS', 'Making it out of here without a blade in your back or poison in your veins would be an accomplishment.', 'Making it out of here without a blade in your back or poison in your veins would be an accomplishment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8495, 'enUS', 'You\'re shaping up nicely, $n. I\'ve heard good things...', 'You\'re shaping up nicely, $n. I\'ve heard good things...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8496, 'enUS', 'You should talk to Winstone. I hear he\'s looking for thieves with your skill.', 'You should talk to Winstone. I hear he\'s looking for thieves with your skill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8497, 'enUS', 'Marvelous. Simply marvelous. You are an inspiration to us all, $n.', 'Marvelous. Simply marvelous. You are an inspiration to us all, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8498, 'enUS', 'I really envy you, $n. You do amazing work.', 'I really envy you, $n. You do amazing work.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8499, 'enUS', 'Your time has come, $n. Master Kang awaits...', 'Your time has come, $n. Master Kang awaits...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8500, 'enUS', 'And to think, one day you might be the one that the stories of legend are about!', 'And to think, one day you might be the one that the stories of legend are about!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8501, 'enUS', 'I wept like a babe when I heard of your heroics. Master Ravenholdt would do well to have an apprentice like you.', 'I wept like a babe when I heard of your heroics. Master Ravenholdt would do well to have an apprentice like you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8502, 'enUS', 'If I was not bound by duty to Master Ravenholdt, I would follow you to the ends of the world, $G Master:Mistress; $n. You are grace and cunning personified.', 'If I was not bound by duty to Master Ravenholdt, I would follow you to the ends of the world, $G Master:Mistress; $n. You are grace and cunning personified.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8503, 'enUS', '', 'May Elune walk with you, $c.  The Temple of the Moon has come to help the troubled citizens of Auberdine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8504, 'enUS', 'I\'m a test spirit healer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8505, 'enUS', 'Why did you kill me, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8506, 'enUS', 'Shoja\'my is over in front of the inn.  Now move along, I\'m busy.', 'Shoja\'my is over in front of the inn.  Now move along, I\'m busy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8507, 'enUS', 'Pets to take care of?  Just make sure you clean up after it!  Look for Shoja\'my in front of the inn.', 'Pets to take care of?  Just make sure you clean up after it!  Look for Shoja\'my in front of the inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8508, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8509, 'enUS', 'Go to the Valley of Honor and seek out Xon\'cha near the stables.', 'Go to the Valley of Honor and seek out Xon\'cha near the stables.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8510, 'enUS', 'Xon\'cha will stable your extra pets.  Find him in the Valley of Honor at the stables between the arena and the Hunter\'s Hall.  Now begone!', 'Xon\'cha will stable your extra pets.  Find him in the Valley of Honor at the stables between the arena and the Hunter\'s Hall.  Now begone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8511, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8512, 'enUS', 'Only a fool walks into a den of thieves unaware. That fool rarely walks out...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8513, 'enUS', 'Ah yes, it is Seikwa that you must find.  I believe I saw her last standing out in front of the inn.', 'Ah yes, it is Seikwa that you must find.  I believe I saw her last standing out in front of the inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8514, 'enUS', 'Seikwa can take care of your animal friends when you have the need.  Seek her out in front of the inn.', 'Seikwa can take care of your animal friends when you have the need.  Seek her out in front of the inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8515, 'enUS', 'The stable master', 'The stable master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8516, 'enUS', 'It is the assistance of Bulrug that you need.  You will find him outside on the lower rise standing between the inn and the Thunder Bluff Bank.', 'It is the assistance of Bulrug that you need.  You will find him outside on the lower rise standing between the inn and the Thunder Bluff Bank.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8517, 'enUS', 'The concern that you show for the welfare of your animal companions is commendable.  Bulrug, on the lower rise, can help you with that.  Seek him out between the bank and the inn.', 'The concern that you show for the welfare of your animal companions is commendable.  Bulrug, on the lower rise, can help you with that.  Seek him out between the bank and the inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8518, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8519, 'enUS', 'Morganus the stable master can be found out in front of the stables strangely enough.', 'Morganus the stable master can be found out in front of the stables strangely enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8520, 'enUS', 'Not too clever are you?  Hmm, if I were a stable master perhaps I might be in the vicinity of say, the stable?  Look for Morganus over there.', 'Not too clever are you?  Hmm, if I were a stable master perhaps I might be in the vicinity of say, the stable?  Look for Morganus over there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8521, 'enUS', 'The stable master', 'The stable master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8522, 'enUS', 'Anya Maulray at inn on upper ring of Trade Quarter.', 'Anya Maulray at inn on upper ring of Trade Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8523, 'enUS', 'Us want lots of pets.  Anya Maulray help you take care of them.  You find her at inn, upper ring of Trade Quarter.', 'Us want lots of pets.  Anya Maulray help you take care of them.  You find her at inn, upper ring of Trade Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8524, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8525, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8526, 'enUS', 'You can trust any pets you have to be well taken care of by Alassin.  He resides in the Cenarion Enclave where the hunters gather.', 'You can trust any pets you have to be well taken care of by Alassin.  He resides in the Cenarion Enclave where the hunters gather.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8527, 'enUS', 'For many hundreds of years Alassin has cared for the animals that our hunters befriend.  His care of animals is highly sought for by the hunters around here.  If you seek him as well, then look for him in the Cenarion Enclave.', 'For many hundreds of years Alassin has cared for the animals that our hunters befriend.  His care of animals is highly sought for by the hunters around here.  If you seek him as well, then look for him in the Cenarion Enclave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8528, 'enUS', 'We are all pieces of a much larger puzzle, $n. Just as you guide the piece of a puzzle to its destination, so too are we guided by another.$B$BAnd perhaps they who guide us are themselves guided by an even greater force.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8529, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8530, 'enUS', 'Ah, gentle Seriadne.  All animals are safe while in her care.  You can find her just outside the Inn in Dolanaar.', 'Ah, gentle Seriadne.  All animals are safe while in her care.  You can find her just outside the Inn in Dolanaar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8531, 'enUS', 'You seek someone who cares for animals?  Then you should look for Seriadne outside the Inn in Dolanaar.  She is the one that you seek.', 'You seek someone who cares for animals?  Then you should look for Seriadne outside the Inn in Dolanaar.  She is the one that you seek.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8532, 'enUS', 'Good ol\' Erma, she\'s been taking care of the local animals for years now.  I\'m sure she\'d be happy to take care of any you\'d like to put up.  She can be found right outside of the Lion\'s Pride Inn.', 'Good ol\' Erma, she\'s been taking care of the local animals for years now.  I\'m sure she\'d be happy to take care of any you\'d like to put up.  She can be found right outside of the Lion\'s Pride Inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8533, 'enUS', 'Erma does all the caring for animals around here.  She\'s right outside the Lion\'s Pride Inn if you need to speak with her.', 'Erma does all the caring for animals around here.  She\'s right outside the Lion\'s Pride Inn if you need to speak with her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8534, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8535, 'enUS', 'Why did you kill me, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8536, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8537, 'enUS', 'Jenova Stoneshield over in the Dwarven District is the one you\'re looking for.  She tends to hunters\' pets while they seek training from her father.', 'Jenova Stoneshield over in the Dwarven District is the one you\'re looking for.  She tends to hunters\' pets while they seek training from her father.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8538, 'enUS', 'If you need to house your pet while you\'re training another, then you should talk to Jenova Stoneshield over in the Dwarven District.', 'If you need to house your pet while you\'re training another, then you should talk to Jenova Stoneshield over in the Dwarven District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8539, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8540, 'enUS', 'Well $glad : lass;, ye can find Shelby Stoneflint right outside the Thunderbrew Distillery in Kharanos.  She\'ll take good care of any pets ye might have.', 'Well $glad : lass;, ye can find Shelby Stoneflint right outside the Thunderbrew Distillery in Kharanos.  She\'ll take good care of any pets ye might have.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8541, 'enUS', 'Shelby Stoneflint houses a good deal of pets for the local hunters.  She can be found over by the Thunderbrew Distillery if ye need her to look after a pet.', 'Shelby Stoneflint houses a good deal of pets for the local hunters.  She can be found over by the Thunderbrew Distillery if ye need her to look after a pet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8542, 'enUS', 'Stable Master', 'Stable Master', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8543, 'enUS', 'Taking care of animals is something that Ulbrek Firehand takes quite seriously.  He usually hangs out with the hunters at the Hall of Arms if ye want him to look after a pet or two.', 'Taking care of animals is something that Ulbrek Firehand takes quite seriously.  He usually hangs out with the hunters at the Hall of Arms if ye want him to look after a pet or two.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8544, 'enUS', 'Over in the Hall of Arms you can find our resident stable master, Ulbrek Firehand.  He\'ll make sure any pets ye have are well fed and rightly cared for.', 'Over in the Hall of Arms you can find our resident stable master, Ulbrek Firehand.  He\'ll make sure any pets ye have are well fed and rightly cared for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8545, 'enUS', 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8546, 'enUS', 'Help! Guards! It\'s going to step on me!', 'Help! Guards! It\'s going to step on me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8547, 'enUS', 'I got something for you, $n. Looks important.', 'I got something for you, $n. Looks important.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8548, 'enUS', 'May I have it, please.', 'May I have it, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8549, 'enUS', 'Welcome to SI:7 headquarters, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8550, 'enUS', '$n, this came for you this morning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8551, 'enUS', 'Messengers from Ravenholdt delivered this letter to me this morning. It is addressed to you, $n.', 'Messengers from Ravenholdt delivered this letter to me this morning. It is addressed to you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8552, 'enUS', 'Take heart!  Pain goes away and $gladies love men with scars:men love women with scars;!', 'Take heart!  Pain goes away and $gladies love men with scars:men love women with scars;!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8553, 'enUS', 'I require aid.', 'I require aid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8554, 'enUS', 'Show strength to your enemy, and we will prevail!', 'Show strength to your enemy, and we will prevail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8555, 'enUS', 'This will be a tough fight, $n. Follow me closely!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8556, 'enUS', 'This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8557, 'enUS', 'Bah, it\'s not here.  I wonder where I left it last...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8558, 'enUS', 'You have my deepest gratitude. I thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8559, 'enUS', 'Now where did I put that companion document?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8560, 'enUS', 'Quite the riveting tale.  Now I know why there was so much dust on that book.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8561, 'enUS', 'So many bookshelves, so little time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8562, 'enUS', 'I should talk to Dewey about some sort of notation system to keep these books better organized.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8563, 'enUS', 'A book by a goblin on gnomish military victories... and all the pages are blank.  Figures.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8564, 'enUS', 'I am going to patrol the area for a while longer and ensure that things are truly safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8565, 'enUS', 'Please return to Zoram\'gar and report our success to the Warsong Runner.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8566, 'enUS', 'Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8567, 'enUS', 'I sense that the great beast draws near...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8568, 'enUS', 'Look, just at the water\'s surface!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8569, 'enUS', 'We have done it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8570, 'enUS', '', 'You seek to lure me from my clutch? You shall pay for your insolence!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8571, 'enUS', 'Yes, $c? Are you reporting in?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8572, 'enUS', 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8573, 'enUS', 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH, YOU CREATURES OF HATE! YOUR MASTER CALLS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8574, 'enUS', 'Crush them!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8575, 'enUS', 'RUN THEM THROUGH, SOLDIERS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8576, 'enUS', 'Push them back to their base! Let not a single Alliance soldier remain living on this battlefield!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8577, 'enUS', 'Rally around your Lieutenants and Commanders! Protect our bases!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8578, 'enUS', 'Major Domo Enabled.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8579, 'enUS', 'Who controls the Coldtooth Mine?', 'Who controls the Coldtooth Mine?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8580, 'enUS', 'The Coldtooth Mine is owned by the Kobolds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8581, 'enUS', 'The Coldtooth Mine is owned by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8582, 'enUS', 'The Coldtooth Mine is owned by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8583, 'enUS', 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8584, 'enUS', 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8585, 'enUS', 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Kobolds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8586, 'enUS', 'Who controls the Irondeep Mine?', 'Who controls the Irondeep Mine?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8587, 'enUS', 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8588, 'enUS', 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8589, 'enUS', 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Troggs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8590, 'enUS', 'This monument is dedicated to all those who have fallen in the protection of Stormwind. Our people have weathered unbelievable hardships to retain their freedoms and to control their own destinies. It is here we remember every sacrifice our citizens have made during the first war that riddled our people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8591, 'enUS', 'The Irondeep Mine is owned by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8592, 'enUS', 'The Irondeep Mine is owned by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8593, 'enUS', 'The Irondeep Mine is owned by the Troggs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8594, 'enUS', 'The Alliance has taken Coldtooth Mine!  Its supplies will now be used for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8595, 'enUS', 'The Alliance has taken the Irondeep Mine!  Its supplies will now be used for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8596, 'enUS', 'The Horde has taken the Coldtooth Mine!  Its supplies will now be used for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8597, 'enUS', 'The Horde has taken the Irondeep Mine!  Its supplies will now be used for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8599, 'enUS', 'The troggs are retaking the Irondeep Mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8600, 'enUS', 'The kobolds are retaking the Coldtooth Mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8601, 'enUS', 'In Alterac Valley, battles rage and equipment is in short supply.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8602, 'enUS', 'I\'ve reached my supply needs for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8603, 'enUS', 'Hook me up!', 'Hook me up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8604, 'enUS', '', 'Cover me, soldier! I have to make it back to our base and get the fleet prepped for an aerial assault!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8605, 'enUS', '', 'WOOT! Made it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8606, 'enUS', '', 'There\'s no time to waste, trooper. I must get back to base immediately! Cover me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8607, 'enUS', '', 'Let\'s get down to business!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8608, 'enUS', 'Guse? Jeztor? Are they... flat spin?? Seas of Southshore??', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8609, 'enUS', 'I need to get back to my ride. Search and destroy imminent! Cover me! On my six! GO GO GO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8610, 'enUS', 'I come for you, puny Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8611, 'enUS', 'Good work, soldier. Now we light the fires and spite their ire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8612, 'enUS', '%s let\'s loose a menacing hoot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8613, 'enUS', '', 'Soldiers of Frostwolf, come to my aid! The time has come to unleash the fury of the elements upon them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8614, 'enUS', 'I\'ve reached my supply needs for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8615, 'enUS', 'This expedition is a terrible drain on military supplies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8616, 'enUS', 'WHO DARES SUMMON LOKHOLAR?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8617, 'enUS', 'The blood of a thousand Alliance soldiers I shall spill... None shall stand against the might of the Ice Lord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8618, 'enUS', 'I drink in your suffering, mortal. Let your essence congeal with Lokholar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8619, 'enUS', '%s\'s death wail has stirred the nearby silithid hive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8620, 'enUS', '', '%s\'s death wail has stirred the nearby silithid hive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8621, 'enUS', '', 'The blood of our enemies shall be spilt upon the battlefield. The great elementals shall answer our call and crush all that stand in our way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8622, 'enUS', '', '%s cackles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8623, 'enUS', '', 'The Ice Lord will consume the entire Stormpike army!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8624, 'enUS', 'Answer cannot be seen through the twisting nether.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8625, 'enUS', '', 'Watch as the blood seeps into the snow, children. The elemental will be bound to us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8626, 'enUS', '', 'It is done! The Ice Lord has arrived! Bow to the might of the Horde, fools!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8627, 'enUS', 'Forget about it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8628, 'enUS', 'It is not worth answering.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8629, 'enUS', 'Is that some kind of a joke?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8630, 'enUS', 'Do not summon me again unless you have a real question to ask, ok?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8631, 'enUS', '%s is enraged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8632, 'enUS', '', 'Channel your energies into the Altar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8633, 'enUS', 'It\'s just so horrible. Our three top guns were shot down over Alliance territory during an air assault Nobody knows if they\'re dead or alive!$B$BThis is a terrible blow to our air strike capabilities.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8634, 'enUS', 'I need a ride out of here, Martin.', 'I need a ride out of here, Martin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8635, 'enUS', '', 'Good work, soldier. I need to get back to base camp! Will you cover me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8636, 'enUS', 'I got your back, Guse.', 'I got your back, Guse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8637, 'enUS', '', 'About damn time someone came. What took you so long? Ah, nevermind. Cover me, soldier. I gotta make it back to base!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8638, 'enUS', 'You can count on me, Jeztor.', 'You can count on me, Jeztor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8639, 'enUS', '', 'You do not belong in these gardens.  Your body shall nourish our lovely creations!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8640, 'enUS', '', 'Nothing must befoul the gardens! You must be destroyed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8641, 'enUS', 'How much more blood is needed, Commander?', 'How much more blood is needed, Commander?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8642, 'enUS', '', 'There is much work left to be done, $n! Continue to strike down our enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8643, 'enUS', 'We must be close, Commander. I can feel the elements stirring.', 'We must be close, Commander. I can feel the elements stirring.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8644, 'enUS', '', 'Yes, $n. The elements are stirring. Soon, we will unleash their might upon our enemies. They shall reel from the blows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8645, 'enUS', 'I sense a great power among us, Commander. Is the spell ready?', 'I sense a great power among us, Commander. Is the spell ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8646, 'enUS', '', 'I have made contact, $n. It is Lokholar the Ice Lord. He has deemed our cause worthy - our earth bound sacrifice sufficient. He comes soon... prepare yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8647, 'enUS', 'I need a few good wing men to get me out of here safely and back to base.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8648, 'enUS', 'I will be your wingman!', 'I will be your wingman!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8649, 'enUS', 'Leave this sacred place or perish!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8650, 'enUS', 'For the Legion! For Kil\'Jaeden!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8651, 'enUS', 'Kazzak is supreme!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8652, 'enUS', 'Holds mistletoe over $g his : her; head', 'Holds mistletoe over $g his : her; head', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8653, 'enUS', 'The pages of the journal seem to be filled with information about trades, buyers  and other miscellaneous business related events.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8654, 'enUS', '', 'Never look the Commander directly in the eyes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8655, 'enUS', '', 'We are under direct orders from Cairne, $r. Do not interfere with the Commander\'s task.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8656, 'enUS', '', 'The Shaman Commander is not to be trifled with, $r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8657, 'enUS', '', 'Soon, Lokholar shall rise... Soon... Very, very soon...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8658, 'enUS', 'You continue to thumb through the journal looking for any information that might help in your search for the stolen chest.$B$BTowards the final pages of the book, in red pencil, you see a notation from James along the side of the page. It says, \"Old home near Stone Cairn Lake... gnolls will work perfectly as bodyguards.\"$B$BThere is no reason to suggest this is where the chest is, but there doesn\'t seem to be any other clues.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8659, 'enUS', '', 'The fleet is ready, $n. You must take this beacon and place it at the eastern crater of the central battlezone. Defend the beacon from attackers until the fleet is able to lock on to its coordinates!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8660, 'enUS', '** CONFIRMS TARGET LOCK. WAR RIDER UNIT INCOMING.  AERIAL ASSAULT IMMINENT. **', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8661, 'enUS', '** PLANTED. CONFIRMING WAR RIDER LOCK. STAND BY. T-MINUS 60 SECONDS. **', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8662, 'enUS', '** SERVOS DESTROYED. UNIT TARGETTING CORRUPT. SHUT DOWN IMMINENT. **', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8663, 'enUS', '%s lets loose a shrill cry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8664, 'enUS', 'I am Hydraxis, Duke of the Waterlords.  You are not an elemental and so cannot understand our struggle, but perhaps we can strike a deal that will benefit us both...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8665, 'enUS', '$n, it is good to see you.  Our war with the other elemental lords yet rages, but your efforts help ensure our victory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8666, 'enUS', 'I require a vial of aqual quintessence, Hydraxis, for I go to the Molten Core to extinguish a rune of the Firelords.', 'I require a vial of aqual quintessence, Hydraxis, for I go to the Molten Core to extinguish a rune of the Firelords.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8667, 'enUS', 'I\'ll get that beacon to the crater safely, Guse. You can count on me!', 'I\'ll get that beacon to the crater safely, Guse. You can count on me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8668, 'enUS', '', 'The fleet is ready, $n. You must take this beacon and place it at the western crater of the central battlezone. Defend the beacon from attackers until the fleet is able to lock on to its coordinates!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8669, 'enUS', 'Consider it done, Jeztor. Give me a beacon.', 'Consider it done, Jeztor. Give me a beacon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8670, 'enUS', 'The time has come to prove your worth as a wing man, $n. Take this beacon and plant it dead center of the Snowfall graveyard. Defend it until my fleet arrives.$B$B<Mulverick salutes.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8671, 'enUS', 'I\'m ready to plant the beacon, Mulv. Give me a beacon.', 'I\'m ready to plant the beacon, Mulv. Give me a beacon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8672, 'enUS', 'Yeee hawww! Mulverick\'s fleet is ready to rock. All able bodied Horde soldiers need request a beacon and try their damndest to plant it so that my War Riders can home in for the aerial assault.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8673, 'enUS', '', 'War Riders are now ready! Soldiers of the Horde, I need your assistance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8674, 'enUS', '', 'Jeztor\'s Riders are ready! Soldiers of the Horde, report for duty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8675, 'enUS', '%s springs forth to defend its hive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8676, 'enUS', '%s springs forth to defend its hive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8677, 'enUS', 'The Earth Mother watch over us; under cover of the night as in the day. Let this fire illuminate both our bodies and spirits and remind us of our honored ancestors, who are ever present and guiding our way.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8678, 'enUS', 'I wish to learn my leatherworking focus.', 'I wish to learn my leatherworking focus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8679, 'enUS', 'Show these savages no mercy, soldiers! They\'ll give none to you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8680, 'enUS', 'Withdraw your troops, brother, and I promise you a swift second death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8681, 'enUS', 'Rally around your Lieutenants and Commanders, soldiers! Keep the savages at bay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8682, 'enUS', 'Show our enemies your resolve, soldiers! Crush them underfoot! Under steel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8683, 'enUS', 'FER THE KING! Push \'em back, soldiers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8684, 'enUS', 'Surrender an\' we\'ll let ye live, heathens! Jus\' put down yer arms an\' march!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8685, 'enUS', 'Sending ground assaults takes huge amounts of supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8686, 'enUS', '$n, what are you waiting for?  Find Field Marshal Teravaine in the Field of Strife and give him the assault orders.  Once he gets the orders, he and his troops will charge into the Horde and take no prisoners!', '$n, what are you waiting for?  Find Field Marshal in the Field of Strife and give him the assault orders.  Once he gets the orders, he and his troops will charge into the Horde and take no prisoners!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8687, 'enUS', 'Give me the orders!  I will deliver them to Teravaine in the Field of Strife!', 'Give me the orders!  I will deliver them to Teravaine in the Field of Strife!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8688, 'enUS', 'The Eye sees all within its sphere of influence, $c. What is it that you wish to see?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8689, 'enUS', 'The orders are given!  Move out, and show them what we\'re made of!', 'The orders are given!  Move out, and show them what we\'re made of!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8691, 'enUS', 'Thank you, $n.  I\'ll mobilize my people right now!', 'Thank you, $n.  I\'ll mobilize my people right now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8692, 'enUS', 'I wish to see my enemies, Ryson. Show them to me.', 'I wish to see my enemies, Ryson. Show them to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8693, 'enUS', 'Take it! Return to me should the Eye\'s power falter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8694, 'enUS', 'If you\'re looking for information that the Cenarion Circle might have archived somewhere, then I\'m the one to talk to.  Then again, you need to know what information you\'re looking for first before I can really help you.$B$BSee how that works?  Brilliant!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8695, 'enUS', 'Don\'t listen to the hearts of the world - the answer lies within.  You know the truth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8696, 'enUS', 'The past lives in these wastes, $c.  We must be careful to not awaken it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8697, 'enUS', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8698, 'enUS', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8699, 'enUS', 'You have only just begun to see the Eye\'s power, $c. If you wish to facilitate the abilities of the Eye, then there is a task that you must complete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8700, 'enUS', 'What is this task?', 'What is this task?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8701, 'enUS', 'Take this beacon and advance upon the enemy\'s base. Make your way to the outside of their main stronghold and place the beacon in the center of the courtyard. Defend the beacon from attackers until the Eye has locked on the beacon\'s coordinates.$B$BOnce this is done, all activity outside the enemy\'s base will be viewable from here.$B$BWill you take on this task?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8702, 'enUS', 'Absolutely. Give me a beacon, Ryson.', 'Absolutely. Give me a beacon, Ryson.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8703, 'enUS', '$n, you are ready for the ultimate task.  The fiery runes etched on the floor of the molten core are conduits to the realm of fire, and their presence is both an affront to us and a source of power for the Firelords.  They must be extinguished!$B$BTo extinguish a rune, take a vial of aqual quintessence.  Pour it on the rune and its flame will go out, rendering it useless and weakening our enemy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8704, 'enUS', 'Welcome to the Undercity Guild Registry. What do you want?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8705, 'enUS', 'Welcome to the Cenarion Circle, $c.  How may I assist you?', 'Welcome to the Cenarion Circle, $c.  How may I assist you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8706, 'enUS', 'I need a Scrying Scope to view the Globe of Scrying, Ryson.', 'I need a Scrying Scope to view the Globe of Scrying, Ryson.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8707, 'enUS', 'Speak with Hydraxis.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8708, 'enUS', 'You have no business with me, little one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8709, 'enUS', 'Speak not your vulgar language to me!  Only Hydraxis can stand it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8710, 'enUS', 'Greetings, little one.  You have business with the Waterlords?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8711, 'enUS', 'Tales of your exploits spread.  Your name is not unknown to us, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8712, 'enUS', 'You have made our enemies your own.  I will gladly share waters with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8713, 'enUS', 'Perhaps you will tell me of your deeds, $n.  I would be honored to know them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8714, 'enUS', 'Our lord Hydraxis speaks highly of you.  My water is your water, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8715, 'enUS', 'When you next go to the Molten Core, kill a Firelord for me, will you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8716, 'enUS', 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8717, 'enUS', 'You look like the type I should watch my coin purse around. Something I can help you with?', 'You look like the type I should watch my coin purse around. Something I can help you with?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8718, 'enUS', 'Upgrade to seasoned units!', 'Upgrade to seasoned units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8719, 'enUS', 'Upgrade to veteran units!', 'Upgrade to veteran units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8720, 'enUS', 'I see that you have found the eye.$B$B<Master Ryson grins.>$B$BWell, give it to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8721, 'enUS', 'Take it! This thing is probably more trouble than its worth.', 'Take it! This thing is probably more trouble than its worth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8722, 'enUS', 'The All Seeing Eye must be recovered!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8723, 'enUS', 'Upgrade to champion units!', 'Upgrade to champion units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8724, 'enUS', 'Lets get back to your base!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8725, 'enUS', 'I am greatly indebted to you, $c. Perhaps I can help your cause.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8726, 'enUS', 'I was searching for materials to build my latest and greatest invention, the portable remote control shredder unit, when these trolls clubbed me and dragged me into the depths of their cave.$B$BReturn me to safety and I will lend my aid to your cause, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8727, 'enUS', 'Let\'s go, goblin!', 'Let\'s go, goblin!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8728, 'enUS', 'Upgrade to seasoned units!', 'Upgrade to seasoned units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8729, 'enUS', 'Upgrade to veteran units!', 'Upgrade to veteran units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8730, 'enUS', 'Upgrade to champion units!', 'Upgrade to champion units!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8731, 'enUS', '', 'This land is tainted. Ivus must be called!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8732, 'enUS', '', 'Stormpike soldiers, aid and protect us! The Forest Lord has granted us his protection. The gateway must now be opened!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8733, 'enUS', '', 'Come, brothers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8734, 'enUS', '', 'Onward!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8735, 'enUS', '', 'We must focus our thoughts upon the Circle of Calling if the Forest Lord is to come! We must hurry! Concentrate your energies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8736, 'enUS', 'Wicked, wicked, mortals! The forest weeps. The elements recoil at the destruction. Ivus must purge you from this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8737, 'enUS', 'I come to raze your bases, Frost Wolf clan. Ivus punishes you for your treachery!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8738, 'enUS', 'Ancients, rise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8739, 'enUS', 'Your base is forfeit, mortals! From its ashes shall rise life!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8740, 'enUS', 'Your base is forfeit, puny mortals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8741, 'enUS', 'Our seasoned units are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8742, 'enUS', 'Our veteran units are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8743, 'enUS', 'Our champion units are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8744, 'enUS', 'Seasoned units are entering the battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8745, 'enUS', 'Veteran units are entering the battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8746, 'enUS', 'Champion units are entering the battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8747, 'enUS', 'We have been sent here from Darnassus to aid the Stormpike in their time of need.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8748, 'enUS', 'The Archdruid believes that Ivus the Forest Lord will come once he is made aware of the destructive forces within this valley.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8749, 'enUS', 'It is not for us to judge these atrocities. Ivus will pass judgment once he arrives.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8750, 'enUS', '', 'My faith guides my actions, $r. I know that Ivus will come.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8751, 'enUS', 'Our seasoned units are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8752, 'enUS', 'Our veterans are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8753, 'enUS', 'Our champion units are leaving the field.  We need more supplies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8754, 'enUS', 'Seasoned units are taking to the field!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8755, 'enUS', 'Our veterans are entering the field!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8756, 'enUS', 'Our champion units are equipped!  Beware, alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8757, 'enUS', 'Have you made contact with Ivus, Archdruid?', 'Have you made contact with Ivus, Archdruid?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8758, 'enUS', '', 'I have been lost in the Dream. I sense his lifeforce but my powers have not yet grown strong enough to make contact.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8759, 'enUS', 'The air is abuzz with energy, Archdruid. What is the status of the Forest Lord?', 'The air is abuzz with energy, Archdruid. What is the status of the Forest Lord?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8760, 'enUS', '', 'Yes. $n. I have located the Forest Lord. We must redouble our efforts! More crystals are needed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8761, 'enUS', 'The forest shakes and shudders! The winds howl! Does the Forest Lord approach?', 'The forest shakes and shudders! The winds howl! Does the Forest Lord approach?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8762, 'enUS', '', 'The Forest Lord is aware of the destruction in this valley, $n. Soon we must make our way to the Circle of Calling. Prepare yourself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8763, 'enUS', 'I was shot down by that reckless fool, Mulverick. I fear that if I do not make it back to base, all will be lost! Mulverick and his squad of War Riders must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8764, 'enUS', 'I got your back, Ichman. GO GO GO!', 'I got your back, Ichman. GO GO GO!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8765, 'enUS', 'I can\'t believe it... I was doing a routine recon mission over the central DMZ when all hell broke loose. I saw the Horde Wing Commander, Mulverick, take out both Ichman and Slidore! Anger took hold of me and I broke from my wing man and drove that show-off, Mulverick, down. Unfortunately, he clipped my gryphon before he crashed, forcing me down as well. I ended up captured by these savages.$B$BI have to get back to base! Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8766, 'enUS', 'I got you covered, Vipore. Move out!', 'I got you covered, Vipore. Move out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8767, 'enUS', 'I heard their mocking laughter as my gryphon crashed and burned, $c.$B$B\"Slidore, you stink!\" They shouted.$B$BNobody talks to Slidore that way! Help me get out of here so that I can show them who the real stinker is!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8768, 'enUS', 'I got your back, Slidore, but to be honest, you do stink. Take a shower, man.', 'I got your back, Slidore, but to be honest, you do stink. Take a shower, man.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8769, 'enUS', 'Cover me, I\'m making a break for it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8770, 'enUS', 'We\'ve got no time to waste. This is a really long run! Cover me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8771, 'enUS', 'Just get me close enough to our base and I can channel a teleport!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8772, 'enUS', 'This is far enough! Meet me back at base!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8773, 'enUS', 'You\'re going down, Mulv!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8774, 'enUS', '', 'I have orders here for an attack on alliance forces in the middle fields.  I need someone to get them to the assault leader.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8775, 'enUS', '', 'It\'s a cold, bloody battle we wage in Alterac Valley.  It\'s perfect.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8776, 'enUS', 'Yes, I will deliver the orders to Warmaster Garrick!', 'Yes, I will deliver the orders to Warmaster Garrick!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8777, 'enUS', '', 'This forest can be confusing! Be careful out there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8778, 'enUS', 'Season\'s savings to you and yours!', 'Season\'s savings to you and yours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8779, 'enUS', 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8780, 'enUS', '\'Tis the season for great bargains!  Fah la la la la!', '\'Tis the season for great bargains!  Fah la la la la!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8781, 'enUS', 'Have a wondrous Feast of Winter Veil!  Start it off right with treats from Smokywood Pastures!', 'Have a wondrous Feast of Winter Veil!  Start it off right with treats from Smokywood Pastures!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8782, 'enUS', 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8783, 'enUS', 'Don\'t let the jingle of the bells take away from the jingle in your pocket!  Buy Smokywood Pastures treats for your Winter Veil festivities!', 'Don\'t let the jingle of the bells take away from the jingle in your pocket!  Buy Smokywood Pastures treats for your Winter Veil festivities!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8784, 'enUS', 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire.  Mmmm!', 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire.  Mmmm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8785, 'enUS', 'Don\'t forget to stock up on wrapping paper this holiday season!  Nothing says \"Wondrous Winter Veil\" like wrapped armors and weapons for your friends and family.', 'Don\'t forget to stock up on wrapping paper this holiday season!  Nothing says \"Wondrous Winter Veil\" like wrapped armors and weapons for your friends and family.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8786, 'enUS', 'Let me browse your seasonal fare.', 'Let me browse your seasonal fare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8787, 'enUS', 'Double time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8788, 'enUS', '%s arming. T-minus 60 seconds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8789, 'enUS', '%s armed. Aerie Gryphon en route.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8790, 'enUS', '%s is destroyed. Aerial assault unable to confirm strike point. Mission aborted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8791, 'enUS', 'Soldiers of Stormpike, come to my aid! The beacon must be planted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8792, 'enUS', 'Pay attention, soldier! You need to take this beacon to the Snowfall graveyard and plant it! Protect it long enough so that my gryphon can lock on.$B$BIf my gryphon gets a solid strike location, Mulv and his crew will be humiliated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8793, 'enUS', 'I\'m your man, Ich. Give me a beacon!', 'I\'m your man, Ich. Give me a beacon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8794, 'enUS', 'My flight is ready! Soldiers come to my aid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8795, 'enUS', '$n! Finally!$B$BYou must take this beacon and plant it in the eastern crater of the central battle grounds. My flight will attack when the beacon beams its coordinates!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8796, 'enUS', 'Give me the beacon, Slidore! Also, you still stink!', 'Give me the beacon, Slidore! Also, you still stink!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8797, 'enUS', 'Their spirits are restored! The gryphons are ready to strike! Come, soldiers! Come to my side!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8798, 'enUS', 'The gryphons are ready to strike, $n! You must take this beacon and plant it in the western crater of the central battlegrounds.$B$BDo this and the battle is ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8799, 'enUS', 'I am ready to rock, Vipore! Give me a beacon.', 'I am ready to rock, Vipore! Give me a beacon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8800, 'enUS', 'We now hold the wisdom of the Winterax, $n. This power can be yours for a short time should you so desire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8801, 'enUS', 'Grant me the wisdom of Winterax!', 'Grant me the wisdom of Winterax!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8802, 'enUS', 'Our units are not upgraded, and I don\'t have enough supplies to upgrade them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8803, 'enUS', 'Our units are upgraded to Seasoned, but I don\'t have enough supplies to upgrade them to Veteran.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8804, 'enUS', 'Our units are upgraded to Veteran, but I don\'t have enough supplies to upgrade them to Champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8805, 'enUS', 'I have enough supplies to upgrade  our troops to seasoned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8806, 'enUS', 'I have enough supplies to upgrade our troops to veterans.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8807, 'enUS', 'I have enough supplies to upgrade our troops to champions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8808, 'enUS', 'Our units are upgraded to Champions, but I can still collect more supplies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8809, 'enUS', 'I cannot store any more supplies.  I have all I can handle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8810, 'enUS', 'Here to learn how to wield a new weapon, or just to waste my time?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8811, 'enUS', 'I\'d like some weapon training', 'I\'d like some weapon training', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8812, 'enUS', 'What can other weapon masters teach?', 'What can other weapon masters teach?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8813, 'enUS', 'Tell me what weapon you want to learn or go away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8814, 'enUS', 'Who controls the Stormpike Graveyard?', 'Who controls the Stormpike Graveyard?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8817, 'enUS', 'The Stormpike Graveyard is cut into the mountains, across the bridge east of Dun Baldar.$B$BThe Alliance currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8818, 'enUS', 'The Stormpike Graveyard is cut into the mountains, across the bridge east of Dun Baldar.$B$BThe Horde currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8820, 'enUS', 'Who controls the Snowfall Graveyard?', 'Who controls the Snowfall Graveyard?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8821, 'enUS', 'The Snowfall Graveyard lies high atop a hill in the Field of Strife, east of Winterax Hold.$B$BThe Alliance currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8823, 'enUS', 'The Snowfall Graveyard lies high atop a hill in the Field of Strife, east of Winterax Hold.$B$BThe Horde currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8824, 'enUS', 'Who controls the Frostwolf Graveyard?', 'Who controls the Frostwolf Graveyard?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8825, 'enUS', 'The Frostwolf Graveyard is along the side of the road, not far north of Frostwolf Village.$B$BThe Alliance currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8826, 'enUS', 'The Frostwolf Graveyard is along the side of the road, not far north of Frostwolf Village.$B$BThe Horde currently controls it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8830, 'enUS', 'The Fostwolf Graveyard is owned by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8831, 'enUS', 'The Fostwolf Graveyard is owned by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8832, 'enUS', 'The Snowfall Graveyard is owned by the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8833, 'enUS', 'The Snowfall Graveyard is owned by the Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8834, 'enUS', 'A wondrous Winter Veil to you... uh... little $G boy : girl;.  Good children get special, um, discounts at any local Smokywood Pastures vendor.  Yum.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8835, 'enUS', 'I have a pretty good idea what ol\' Greatfather Winter\'s gonna be doing when this shift is over.  I got a bottle of Winter Veil cheer right here in my bag, and I intend to get to the bottom of it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8836, 'enUS', 'No... you can\'t sit on my lap.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8837, 'enUS', 'Yeah, sure, go ahead and tell ol\' Greatfather Winter what you\'d like for this Winter Veil.$B$BHo ho hurry up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8838, 'enUS', 'The Stormpike clan bows to no one, especially the horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8839, 'enUS', 'Your attacks are weak!  Go practice on some rabbits and come back when you\'re stronger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8840, 'enUS', 'Is that the best you can do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8841, 'enUS', 'It\'ll take more than you rabble to bring me down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8842, 'enUS', 'We will not be swayed from our mission!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8843, 'enUS', 'We, the Alliance, will prevail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8844, 'enUS', 'You cannot defeat the Frostwolf clan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8845, 'enUS', 'Your spirits are weak, and your blows are weaker!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8846, 'enUS', 'Your attacks are slowed by the cold, I think!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8847, 'enUS', 'If you will not leave Alterac Valley on your own, then the Frostwolves will force you out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8848, 'enUS', 'You are no match for the strength of the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8849, 'enUS', 'Today, you will meet your ancestors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8850, 'enUS', '%s\'s wings thunder in the distance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8851, 'enUS', 'Tell me about the Glowing Shard.', 'Tell me about the Glowing Shard.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8852, 'enUS', 'Mine! Mine! Mine!  Gizlock is the ruler of this domain!  You shall never reveal my presence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8853, 'enUS', 'Dun Baldar took a real beating in the last Horde attack!  We need more supplies in order to rebuild and make repairs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8854, 'enUS', 'They say an army travels on its stomach, and that\'s the truth.  For any offensive to succeed, your troops have to be well-fed and well supplied.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8855, 'enUS', 'Some say that strength and bravery are most needed for an army\'s success.  And it\'s true!  But an army also needs supplies to keep itself in good shape, and it\'s my job to make sure they have what they need.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8857, 'enUS', 'Frostwolf Keep is razed!  We must have supplies in order to repair and rebuild!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8858, 'enUS', '%s whimpers obediently.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8859, 'enUS', '', 'Greetings, soldier. Are you here to help the cause?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8860, 'enUS', '', 'Well done, soldier. A few more and our stables will be overflowing with mounts for the cavalry to ride into combat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8861, 'enUS', 'Thank you, Stable Master. Please take the animal.', 'Thank you, Stable Master. Please take the animal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8862, 'enUS', 'Heya mon.  You come right place learn how ta wield a new weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8863, 'enUS', 'Ah, $n!  You\'re just the person I was hoping to see!  We have enough supplies to send a ground assault against the Horde!  Field Marshal Teravaine is waiting in the Field of Strife for orders...$B$BYou\'re a brave veteran who\'s proven himself time and again.  Do you want to deliver Teravaine the orders?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8864, 'enUS', 'What you want to learn, mon?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8865, 'enUS', '$n!  What are you doing talking to me?  You should be out fighting the Horde!$B$BI have assault orders for one of our field marshals in the Field of Strife, but I need someone I can count on.$B$BPerhaps if your standing with the Stormpike militia were higher, I could count on you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8866, 'enUS', 'I would be honored to train you in the use of the weapons that I have mastered should you have the ability to learn within you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8867, 'enUS', '$n!  Our supplies are dwindling and until we get more we can\'t launch large ground assaults into the Field of Strife!$B$BSpeak with the Stormpike Quartermaster.  He\'ll tell you what you can do to resupply our assault forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8868, 'enUS', 'If you require more training, I would be happy to tell you who can aid you. What weapon has caught your eye?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8869, 'enUS', 'Death to Stormpike!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8870, 'enUS', 'Unleash the cavalry!', 'Unleash the cavalry!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8871, 'enUS', 'You have done an exceptional job, soldier! The cavalry is ready to ride on your command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8872, 'enUS', 'Awaiting orders from my superiors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8873, 'enUS', 'I suppose you are here to be trained in the use of a new weapon?  Make it fast, I\'m very busy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8874, 'enUS', 'I\'ll tell you where to go, provided you tell me what interests you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8875, 'enUS', 'I wish to stable my pet, Stable Master.', 'I wish to stable my pet, Stable Master.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8876, 'enUS', '$n, it\'s good to see veterans like you in the field!  I\'m sorry to report that our supplies are currently too low to launch any large ground assaults against the Horde.$B$BSpeak with our quartermaster; he has supply missions available.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8877, 'enUS', 'Have you come seeking training in the ways of armed combat?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8878, 'enUS', 'What weapon in particular interests you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8879, 'enUS', '', 'The grace with which I fight is not something I can teach you, but I can teach you the use of various weapons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8880, 'enUS', '', 'I can direct you to other weapon masters if you prefer. What interests you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8881, 'enUS', 'Ye want to be trained in the use of a weapon, do ye?  Well don\'t stand there slack-jawed, speak up $gladdie : lassie;!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8882, 'enUS', 'What weapon were ye lookin\' to learn?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8883, 'enUS', '', 'Well hi there!  Are you here to learn the proper technique of a new weapon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8884, 'enUS', '', 'Aw, don\'t want to train with me? What other weapon catches your eye?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8885, 'enUS', '', 'We need more wolves! Come to the stables and help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8886, 'enUS', '', 'Well done, Horde! We have enough Frostwolves in our stables to provide mounts to all of our wolf riders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8887, 'enUS', 'Wolf riders, to Frost Dagger Pass!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8888, 'enUS', 'RARRRRRR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8889, 'enUS', 'Let me know when it is best to ride. You must be our eyes and ears on the battlefront.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8890, 'enUS', 'The time has come, brothers. We must not let the enemy advance through Frost Dagger Pass. Some of us will surely perish, but would any of you shy away from this duty? This honor? Onward! For the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8891, 'enUS', 'For the Warchief!', 'For the Warchief!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8892, 'enUS', 'Myolor, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 'Myolor, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8893, 'enUS', 'Myolor, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith!', 'Myolor, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8894, 'enUS', 'Krathok, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 'Krathok, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8895, 'enUS', 'Krathok, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith.', 'Krathok, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8896, 'enUS', 'Patrol Frost Dagger Pass. Let none through! Die with honor...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8897, 'enUS', 'The Kolkar invaders are retreating!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8898, 'enUS', 'We have gathered enough hides from the wolves. Soon, the cavalry will be ready to ride! Speak to me again when we have stabled enough rams.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8899, 'enUS', '', 'The stables are full again! Our cavalry is soon to ride!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8900, 'enUS', '', 'The stable is empty! We must provide our cavalry with suitable mounts. Stormpike Stables requests your assistance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8901, 'enUS', 'Death to Frostwolf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8902, 'enUS', 'We ride on your command!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8903, 'enUS', 'YAW! Er, to the front lines with you!', 'YAW! Er, to the front lines with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8904, 'enUS', 'Awaiting orders from my superiors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8905, 'enUS', 'Ram Riders, REGULATE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8906, 'enUS', 'Lads and ladies. Today can be your greatest day, for today, you fight for the glory of Stormpike! Ride unto the field and grant your enemies no mercy! Strike the savages down and let their Warchief sort em out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8907, 'enUS', 'Patrol the front line, soldiers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8908, 'enUS', 'For the King!', 'For the King!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8909, 'enUS', '', 'The indigenous rams of Alterac are vital to the functionality of our cavalry!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8910, 'enUS', '', 'Just a few more and our stables will be overflowing with mounts. Release it into my custody, soldier. I\'ll take it from here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8911, 'enUS', 'With pleasure. These things stink!', 'With pleasure. These things stink!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8912, 'enUS', 'I wish to stable my pet.', 'I wish to stable my pet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8913, 'enUS', 'All right, Reavers!  The orders are here!  To battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8914, 'enUS', 'I\'ll perform these with pleasure, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8915, 'enUS', '%s emerges from the plant, allowing it to heal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8916, 'enUS', '%s attacks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8917, 'enUS', 'Well met, $n. We must be brief because there is much to do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8918, 'enUS', 'May Cenarius guide you, $c.$B$BI am here in this corrupt land with my sisters on a mission of great importance to the Cenarion Circle. But only those who are powerful enough I ask speak to me about such matters--to send a lesser being would be folly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8919, 'enUS', '', 'My sisters and I have come down from the Stonetalon Mountains to put to rest a tragedy from ages past. Unfortunately, we are not powerful enough to accomplish our goals on our own and now seek mortals such as yourself to aid us.$B$BIf you are powerful enough, then we have much to discuss, $c. If you are not, then, for your safety, I will leave the details of my mission out of our conversation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8920, 'enUS', '', '$n, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8921, 'enUS', 'I greet you, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8922, 'enUS', 'Free! Free from my bonds at last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8923, 'enUS', '$n, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8924, 'enUS', 'I need no home to know I still have a place in this world, $c. The tribes will see... all of them. They fight amongst one another, and for what? Scraps left over by vultures... lands infested by the Legion... bah!$B$BThe centaur should be proud of their heritage--it is just as noble as any Night elf\'s or orc\'s--it is time we started acting like it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8925, 'enUS', 'At last, I am free of the corruption that once bound me to this place...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8926, 'enUS', 'Thank you for what you have done, $n.$B$BKeep the scepter safe; may it aid you in your toil against the evils that have invaded this place...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8927, 'enUS', 'How has this come to be, $n? Do you think that an item such as the Scepter of Celebras is trivial?$B$BI can help you this time, but do not be careless with this valuable item again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8928, 'enUS', 'Celebras, I lost my Scepter...', 'Celebras, I lost my Scepter...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8929, 'enUS', 'I carry both parts of the scepter.', 'I carry both parts of the scepter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8930, 'enUS', 'My scepter... After so many years... You have recovered it! You shall truly be the savior of this place...$B$BPlease listen to me carefully, $n. The head and the rod of the scepter may only be reunited into one at the Stone of Maraudon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8931, 'enUS', 'Please show me the Stone of Maraudon.', 'Please show me the Stone of Maraudon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8932, 'enUS', 'Please explain how the scepter works.', 'Please explain how the scepter works.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8933, 'enUS', 'Between the colors, a path.$B$BBlink across in light.$B$BFrom the water, you emerge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8934, 'enUS', 'I don\'t quite understand...', 'I don\'t quite understand...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8935, 'enUS', 'Between the orange and the purple passageways near the doors to Maraudon, you will find a third path.$B$BThe scepter you hold in your hands is the key.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8936, 'enUS', 'No, really, I still don\'t understand...', 'No, really, I still don\'t understand...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8937, 'enUS', 'I grow weary...$B$BFrom here, you can see the waterfalls, $n...The scepter grants you the power to travel to this very place, should you ever desire to enter through the front gates of Maraudon again.$B$BNear the entrance to Maraudon, you will find a passage that goes down beneath the caverns. Use the scepter at the ancient ritual site to create a portal, and you will find yourself here once more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8938, 'enUS', 'Please tell me more about Maraudon.', 'Please tell me more about Maraudon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8939, 'enUS', 'Long ago, my dryad sisters and I entered these caves to free the tortured spirit of my uncle, Zaetar. However, the corruption that had already spread through these caverns soon overcame us, and we too fell.$B$BWe were destined to wander these passages; the toxins poisoning our minds...$B$BAlthough my spirit may now be at rest, I know that I have failed in my mission. It is a great sadness I must bear...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8940, 'enUS', 'Please tell me more about Zaetar.', 'Please tell me more about Zaetar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8941, 'enUS', 'Ah, Zaetar... <Celebras looks away.>$B$BMy father, Remulos, tried to stop him...$B$BZaetar was headstrong, and when he fell for an earthen elemental princess, my father knew that their union was cursed, but Zaetar refused to listen.$B$BThe centaur are the result of his mistake. They were born misshapen and hateful; my uncle Zaetar was killed by his own kin...$B$BThe evil princess, Theradras, still guards his remains here in the crystal caverns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8942, 'enUS', 'Those goblins should be ashamed of themselves.  Bah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8943, 'enUS', 'Spending coin is NOT the reason for the Feast of Winter Veil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8944, 'enUS', 'I cannot believe those greedy goblins are allowed to hawk their wares while spoiling the spirit of this season.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8945, 'enUS', 'Vile goblins... anything for a gold coin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8946, 'enUS', 'Could I have a copy of \"The Feast of Winter Veil\" to read?', 'Could I have a copy of \"The Feast of Winter Veil\" to read?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8947, 'enUS', 'Perhaps we can attempt this later. Please speak to me again if you wish to do so.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8948, 'enUS', 'Together, the two parts shall become one, once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8949, 'enUS', 'Shal myrinan ishnu daldorah...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8950, 'enUS', 'Read this tome I have placed before you, and speak the words aloud.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8951, 'enUS', '%s begins to channel his energy, focusing on the stone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8952, 'enUS', 'You wish to learn of the stone? Follow me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8953, 'enUS', 'For so long I have drifted in my cursed form. You have freed me... Your hard work shall be repaid.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8954, 'enUS', 'Please do as I instruct you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8955, 'enUS', 'My scepter will once again become whole!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8956, 'enUS', 'Welcome to the Explorers\' League.  If you need information, and it is in book form, then I might be able to help you out.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8957, 'enUS', 'How can I find the two parts of your scepter?', 'How can I find the two parts of your scepter?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8958, 'enUS', 'As the toxins lurking within Maraudon seeped into my mind, I began to lose my grip on reality. Worried that it would be used for evil, my dryad sisters secretly took the scepter from me, breaking it into two parts and hiding it within the caverns.$B$BSoon they fell victim to the madness as well, and the two parts of the scepter, left unguarded, came to be in the possession of Vyletongue, himself. The satyr entrusted one part to Noxxion, an evil elemental, and the other he still has to this day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8959, 'enUS', 'Why didn\'t Lord Vyletongue unite both parts of the scepter into one?', 'Why didn\'t Lord Vyletongue unite both parts of the scepter into one?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8960, 'enUS', 'Without knowledge of the incantations required to unite the two parts, Lord Vyletongue was unable to use the scepter. However, now that I am free of the curse of Vyletongue\'s corruption, I will be able to assist you, if you\'d like to bring me the two parts -- both the head and the rod of the scepter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8961, 'enUS', 'Tell me more about the history of Remulos and Zaetar.', 'Tell me more about the history of Remulos and Zaetar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8962, 'enUS', 'You ask of the Sons of Cenarius, Remulos and Zaetar.$B$BMy father, Remulos, was strong and beautiful, and my uncle, Zaetar, very cunning and slight of build. While they were respected quite equally, Zaetar always felt as if he could never quite compare to the glory and attention he thought Remulos had.$B$BAfter my sisters and I were born, Zaetar, blinded by jealousy, set out to outdo his brother... He was the creator of his own end. The sadness it brings me has never lessened, even as time passes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8963, 'enUS', 'Long have my spirit and remains been kept captive here within Maraudon, young $c. The time has given me nothing but ample opportunities to reflect on my mistakes and realize that my jealousy of Remulos was foolish. He attempted to warn and persuade me that my relations with Theradras would bear nothing pure--that only destruction would come from chaos. My brother was right.$B$BBut that does not mean since you have freed me, I am free of penance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8964, 'enUS', 'Please, go on.', 'Please, go on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8965, 'enUS', 'Though exiled as a heretic, my beliefs still ring true--that alone drives me onward. But for my destiny to be complete, I need the aid of an outsider... one not of any centaur tribe.$B$BThe task requires someone to enter the holy halls of Maraudon--a temple my people protect, and only the most devout are ever allowed to enter.$B$BIf you agree to aid me, then you need only nod. I will pass you a parchment with my instructions on it. Read it over and consider my words.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8966, 'enUS', 'I will aid you. Give me the instructions.', 'I will aid you. Give me the instructions.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8967, 'enUS', '%s bows low before you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8968, 'enUS', 'Thank you, good $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8969, 'enUS', 'Greetings, $n. I am Doctor Gregory Victor, head trauma surgeon of Horde Trauma.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8970, 'enUS', 'It is good to see you again, Doctor $n. I have been following your progress and I dare say that it is time you learned some of the more advanced bandaging techniques.$B$BReturn to me from time to time, as your skill advances, and I shall teach you all that I know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8971, 'enUS', 'Teach me how to create and apply a Heavy Mageweave Bandage, Doctor.', 'Teach me how to create and apply a Heavy Mageweave Bandage, Doctor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8972, 'enUS', 'Teach me how to create and apply a Runecloth Bandage, Doctor.\n', 'Teach me how to create and apply a Runecloth Bandage, Doctor.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8973, 'enUS', 'Teach me how to create and apply a Heavy Runecloth Bandage, Doctor.\n', 'Teach me how to create and apply a Heavy Runecloth Bandage, Doctor.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8974, 'enUS', 'Greetings, $n. I am Doctor Gustaf VanHowzen, head trauma surgeon of Alliance Trauma.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8975, 'enUS', 'Great!  Let\'s keep those supplies coming, people!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8976, 'enUS', 'Thanks for the supplies, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8977, 'enUS', 'Thanks a lot, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8978, 'enUS', 'We\'re making progress!  Bring more supplies to repair Dun Baldar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8979, 'enUS', 'We have enough supplies!  Our captain has retaken position in the Field of Strife!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8980, 'enUS', 'Now we\'re making progress!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8981, 'enUS', 'Thank you for the armor scraps, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8982, 'enUS', 'Our cache of equipment grows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8983, 'enUS', 'Thank you for the supplies, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8984, 'enUS', 'Very good.  Our supply cache grows...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8985, 'enUS', 'Keep them coming!  We need more supplies in order to rebuild...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8986, 'enUS', 'We can now rebuild!  For the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8987, 'enUS', '', 'Hail, $n!  It is good you came to see me.  We are prepared to launch a ground assuault through the Field of Strife!  Once Warmaster Garrick gets his orders, he and his reavers will cut a swath through the Alliance!$B$BDo you want to deliver those orders, $n?  Garrick is waiting in the Field of Strife...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8988, 'enUS', '', 'Why are you here, $n?  Is there lead in your boots?$B$BYou have the assault orders - Warmaster Garrick needs them in the Field of Strife before he can make his assault!  Go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8989, 'enUS', '', 'It\'s good to have veterans like you in Alterac Valley, $n.  Our supplies are too low to launch a ground assault through Alterac Valley... speak with our quartermaster - he\'ll tell you how to increase our supply base.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8990, 'enUS', '', 'Hail, $c!  Our supply level is high, and we are ready to launch a ground assault through the Field of Strife.  Perhaps if your renown was greater, I would trust you to deliver the assault orders.$B$BSo get out there and fight the Alliance!  Show me you\'re a trusted and capable member of the Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8991, 'enUS', '', 'You!  $n!  What are you doing wandering around the village like this?  There are Alliance foes in Alterac!  We must drive them out!$B$BOur supplies are too low to launch a large ground assault, so speak with our quatermaster.  He\'ll tell you how you can increase our supply base.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8993, 'enUS', '', 'Even the bravest warrior must be well supplied if he is to face his foes with strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8994, 'enUS', 'I just gave kill credit to $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8995, 'enUS', 'Click here to remove res effects and repair all gear', 'Click here to remove res effects and repair all gear', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8996, 'enUS', 'Hi! Clear your res effects here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8997, 'enUS', 'Click here to repair your gear', 'Click here to repair your gear', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8998, 'enUS', 'We have enough supplies to launch a ground assault.  If you want to send the order, and you think you qualify, then speak Sergeant Yazra Bloodsnarl.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (8999, 'enUS', 'We have enough supplies to launch a ground assault.  If you want to deliver those orders, and you think you qualify, then speak with Corporal Noreg Stormpike.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9000, 'enUS', 'I am in charge of recruitment for the Frostwolf Clan. As a soldier of Frostwolf, it is your responsibility to check in with me as your rank among the clan rises.$B$BHonor and respect are earned on this frigid field.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9001, 'enUS', 'Warmaster, I have lost my insignia. Could you supply me with a replacement?', 'Warmaster, I have lost my insignia. Could you supply me with a replacement?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9002, 'enUS', 'Hail, $r! I am Lieutenant Haggerdin, officer in charge of recruitment for the Stormpike Guard.$B$BAs you gain prestige and notoriety among the Stormpike, return to me and I shall see about upgrading your insignia. Also, should you ever lose your insignia, I am able to supply a replacement.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9003, 'enUS', 'Lieutenant, I have lost my insignia. Could you please supply me with a replacement?', 'Lieutenant, I have lost my insignia. Could you please supply me with a replacement?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9004, 'enUS', 'I have found both parts of your scepter.', 'I have found both parts of your scepter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (9005, 'enUS', 'Argh, I need help here!', '', 26124);
