INSERT INTO `broadcast_text_locale` VALUES (10123, 'enUS', '', 'Welcome to the Darkmoon Faire, $c!$B$BMy name is Kerri Hicks and I\'d shake your hand but I\'m afraid I\'d break it, because I\'m the strongest woman alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10124, 'enUS', '', 'Hi $n!  Thanks for all the help you\'ve given me!  Right now I don\'t need anything, but who knows what the future will bring?$B$BEnjoy yourself, and remember to tell your friends about the Darkmoon Faire!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10125, 'enUS', 'Come to me and see what the future holds for you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10126, 'enUS', 'Come speak with me, and what once was cloudy shall become crystal clear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10127, 'enUS', 'Fear not!  All fortunes can be discerned for those who know the way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10128, 'enUS', 'The longer you wait, the less future you have for me to foretell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10129, 'enUS', '', 'Cheap beer!  Get your cheap beer right here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10130, 'enUS', '', 'Huzzah for the tipper!  Step right up and get your delicious, cold drinks right here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10131, 'enUS', '', 'Just in... another batch of Darkmoon Special Reserve.  Come get yours before they\'re all gone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10132, 'enUS', '', 'You there!  Yes you!  You look thirsty.  Get over here, I have just what you need!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10133, 'enUS', 'Step right up!  Get your red hot wings right here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10134, 'enUS', 'Darkmoon Dogs fresh off the grill.  Come and get \'em while they\'re hot!', '', 26124);
