INSERT INTO `broadcast_text_locale` VALUES (10136, 'enUS', 'For a limited time only, crunchy frog available exclusively here at the Darkmoon Faire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10137, 'enUS', 'Have a completed Darkmoon deck? Turn it in here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10138, 'enUS', 'Come speak with me if you have any Darkmoon Faire Cards.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10139, 'enUS', 'Darkmoon decks! Turn them in here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10140, 'enUS', 'Have you any Darkmoon Faire cards?  Come speak with me to learn more about them if you dare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10141, 'enUS', '', 'Every item that I have for sale I acquired myself.  You have my personal guarantee of authenticity.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10142, 'enUS', '', 'Darkmoon Faire Cards?  See the Professor here for those.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10143, 'enUS', '', 'Come and take a look at my wares while you still can.  We\'re here all week, and then we\'re off to some far off land.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10144, 'enUS', '', 'Have you any idea what I go through to bring these incomparable valuables to sell?  Come see them all for yourself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10145, 'enUS', 'Hey, someone help me catch this thing!', '', 26124);
