INSERT INTO `broadcast_text_locale` VALUES (10168, 'enUS', '', 'I can\'t find my pet frog, Jubjub.  She loves Dark Iron Ale, it\'s all she ever drinks, but can be so hard to get!  Now I\'m out... and Jubjub must have run away to find more!$B$BI wish she would come back...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10169, 'enUS', '', 'I\'m so happy that Jubjub returned!  I only hope that I don\'t run out of Dark Iron ale soon, or I fear my pet frog might once again escape!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10170, 'enUS', '', 'Hi Jubjub.  I missed you!', 26124);
