INSERT INTO `broadcast_text_locale` VALUES (10294, 'enUS', 'Now to find an unsuspecting Harpy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10295, 'enUS', 'There\'s one! For the Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (10296, 'enUS', 'Take that you mangy Harpy! Hyaa!', '', 26124);
