INSERT INTO `broadcast_text_locale` VALUES (11423, 'enUS', '%s lets loose a massive attack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11424, 'enUS', '%s hears the call to battle and rises to serve its master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11425, 'enUS', '%s casts its massive shadow upon the whole of Silithus.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11426, 'enUS', '%s breaks free of its cocoon beneath Hive\'Ashi.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11427, 'enUS', '$n, Champion of the Bronze Dragonflight, has rung the Scarab Gong. The ancient gates of Ahn\'Qiraj open, revealing the horrors of a forgotten war...', '$n, Champion of the Bronze Dragonflight, has rung the Scarab Gong. The ancient gates of Ahn\'Qiraj open, revealing the horrors of a forgotten war...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11428, 'enUS', 'Slay them all. Leave their husks for the master to consume...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11429, 'enUS', 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will cease.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11430, 'enUS', 'Presents for everyone! Father Winter\'s put gifts under the tree for all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11431, 'enUS', 'Do you think you\'ve won? So easily? The legion has only just begun its march. The armies of C\'Thun will raze this world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11432, 'enUS', 'Massive Qiraji resonating crystals break through the earthen crust of Kalimdor. Colossal creatures made of obsidian stone breach the freshly broken land.', 'Massive Qiraji resonating crystals break through the earthen crust of Kalimdor. Colossal creatures made of obsidian stone breach the freshly broken land.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11433, 'enUS', 'Breathe your last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11434, 'enUS', 'Warriors, Captains, continue the fight! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11435, 'enUS', 'Impudent fool! I will kill you myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11436, 'enUS', 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11437, 'enUS', 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11438, 'enUS', 'Fear is for the enemy! Fear and death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11439, 'enUS', 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11440, 'enUS', 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11441, 'enUS', '%s senses your fear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11442, 'enUS', 'You will be judged for defiling these sacred grounds!  The laws of the Ancients will not be challenged!  Trespassers will be annihilated!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11443, 'enUS', 'I sentence you to death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11444, 'enUS', 'I serve to the last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11445, 'enUS', 'Are you so eager to die? I will be happy to accommodate you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11446, 'enUS', 'Let your death serve as an example!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11447, 'enUS', 'You only delay the inevitable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11448, 'enUS', 'senses your fear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11449, 'enUS', 'Sands of the desert, rise and block out the sun!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11450, 'enUS', 'You are terminated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11451, 'enUS', 'I...have...failed. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11452, 'enUS', 'My brother, no!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11453, 'enUS', 'You will not escape death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11454, 'enUS', 'Vek\'lor, I feel your pain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11455, 'enUS', 'Your fate is sealed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11456, 'enUS', 'Starting at 6PM, they will show fireworks from the highest bluff!', 'Starting at 6PM, they will show fireworks from the highest bluff!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11457, 'enUS', 'The kegs are filled with strong brew!  Drink, friend, and celebrate!', 'The kegs are filled with strong brew!  Drink, friend, and celebrate!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11458, 'enUS', 'I heard that, after the fireworks show, goblets will be brought to the tables.  Fine toasts we shall raise!', 'I heard that, after the fireworks show, goblets will be brought to the tables.  Fine toasts we shall raise!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11459, 'enUS', 'Hail, $c!  Welcome to the celebration!', 'Hail, $c!  Welcome to the celebration!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11460, 'enUS', 'They\'re going to show fireworks all night long.  From 6PM until dawn, the sky will blaze over Booty Bay!', 'They\'re going to show fireworks all night long.  From 6PM until dawn, the sky will blaze over Booty Bay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11461, 'enUS', 'Wow!  These kegs are filled with powerful stuff!  They lift me right out of my boots!', 'Wow!  These kegs are filled with powerful stuff!  They lift me right out of my boots!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11462, 'enUS', 'Stick around after the firework show is over.  Find a goblet and raise it to toast your hardy friends!', 'Stick around after the firework show is over.  Find a goblet and raise it to toast your hardy friends!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11463, 'enUS', 'All right!  Party time!', 'All right!  Party time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11464, 'enUS', 'Times like these get me all sentimental...', 'Times like these get me all sentimental...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11465, 'enUS', 'I sure could use a kiss about now.  Know where I can find one?', 'I sure could use a kiss about now.  Know where I can find one?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11466, 'enUS', 'Hah!  This tauren wants a kiss!', 'Hah!  This tauren wants a kiss!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11467, 'enUS', 'A happy Winter Veil to you, friend!', 'A happy Winter Veil to you, friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11468, 'enUS', 'Season\'s greetings, and peace to you!', 'Season\'s greetings, and peace to you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11469, 'enUS', 'I wish you a happy holiday, friend!', 'I wish you a happy holiday, friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11470, 'enUS', 'Ah, mistletoe...', 'Ah, mistletoe...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11471, 'enUS', 'Kneel before me, mortal! Kneel before Zod!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11472, 'enUS', 'Why ... won\'t you ... just dieeeee?', '', 26124);
