INSERT INTO `broadcast_text_locale` VALUES (11475, 'enUS', 'Burn in hate, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11476, 'enUS', '%s is weakened!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11477, 'enUS', 'I lied...', '', 26124);
