INSERT INTO `broadcast_text_locale` VALUES (11479, 'enUS', '', 'Mighty fine weather we\'re having here, wouldn\'t you say?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11480, 'enUS', 'Oh Umi, please teach me again how to create a Tranquil Yeti. I miss it so.', 'Oh Umi, please teach me again how to create a Tranquil Yeti. I miss it so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11481, 'enUS', '', 'Well, alright then $n. I can tell that you\'ve seen the error of your ways and rejoined the engineering fold.$B$BHave fun with your yeti!!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11482, 'enUS', '', 'Why is it that there\'s never a gyrochronatom lying around when you need one?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11483, 'enUS', '', 'I\'ll get the last laugh... they\'ll see! Oh, hi $c, I didn\'t see you there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11484, 'enUS', '', 'Invention, my dear $c, is two percent bottled inspiration, four percent solar exposure, six percent chance of thunderstorms clearing by mid-afternoon, and ninety-three percent Rumplesnicker.$B$BWait, did that add to one hundred? I\'ve got my notes around here somewhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11485, 'enUS', 'We\'ve gotta find out what the Qiraji are feeding those monsters! Look at the size of \'em!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11486, 'enUS', 'Studying and understanding the Colossus will give us a needed advantage over the Qiraji.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11487, 'enUS', '', 'The secrets behind the power of the Colossus could lead to all sorts of innovations in science!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11488, 'enUS', 'Where is Elder Graveborn?', 'Where is Elder Graveborn?', 26124);
