INSERT INTO `broadcast_text_locale` VALUES (11498, 'enUS', 'Elder Obsidian wanders near the Sepulcher in Silverpine Forest.', 'Elder Obsidian wanders near the Sepulcher in Silverpine Forest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11499, 'enUS', 'Where is Elder Snowcrown?', 'Where is Elder Snowcrown?', 26124);
