INSERT INTO `broadcast_text_locale` VALUES (11501, 'enUS', 'Where is Elder Highpeak?', 'Where is Elder Highpeak?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11502, 'enUS', 'Elder Highpeak watches over the Hinterlands from a hill near its center.', 'Elder Highpeak watches over the Hinterlands from a hill near its center.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11503, 'enUS', 'Where is Elder Windrun?', 'Where is Elder Windrun?', 26124);
