INSERT INTO `broadcast_text_locale` VALUES (11557, 'enUS', 'Elder Skygleam can be found near the Ravencrest Monument in Azshara.', 'Elder Skygleam can be found near the Ravencrest Monument in Azshara.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11558, 'enUS', 'Where is Elder Bladeleaf?', 'Where is Elder Bladeleaf?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11559, 'enUS', 'The village of Dolanaar atop Teldrassil is home to Elder Bladeleaf.', 'The village of Dolanaar atop Teldrassil is home to Elder Bladeleaf.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11560, 'enUS', 'My work here is done. I\'ll be sure to let Mar\'alith and the others at Cenarion Hold know that you helped me out a little. Be careful in here, you don\'t have me holdin\' your hand past this point.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11561, 'enUS', 'Where is Elder Starweave?', 'Where is Elder Starweave?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11562, 'enUS', 'Elder Starweave can be found on the beach near Lor\'danel in Darkshore.', 'Elder Starweave can be found on the beach near Lor\'danel in Darkshore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11563, 'enUS', '%s\'s eyes glow red for a brief moment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11564, 'enUS', 'Is that the best you can do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11565, 'enUS', 'Where is Elder Nightwind?', 'Where is Elder Nightwind?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11566, 'enUS', 'Elder Nightwind passes the days at the shrine in Jaedenar in Felwood.', 'Elder Nightwind passes the days at the shrine in Jaedenar in Felwood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11567, 'enUS', 'Where is Elder Riversong?', 'Where is Elder Riversong?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11568, 'enUS', 'The village of Astranaar in Ashenvale is Elder Riversong\'s current home.', 'The village of Astranaar in Ashenvale is Elder Riversong\'s current home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11570, 'enUS', 'Where is Elder Stonespire?', 'Where is Elder Stonespire?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11571, 'enUS', 'Last I knew, Elder Stonespire was at Everlook in Winterspring.', 'Last I knew, Elder Stonespire was at Everlook in Winterspring.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11572, 'enUS', 'Where is Elder Brightspear?', 'Where is Elder Brightspear?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11573, 'enUS', 'Elder Brightspear can be found wandering amongst the ruins of Kel\'Theril in Winterspring.', 'Elder Brightspear can be found wandering amongst the ruins of Kel\'Theril in Winterspring.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11574, 'enUS', 'Where is Elder Bloodhoof?', 'Where is Elder Bloodhoof?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11575, 'enUS', 'Elder Bloodhoof can be found at Bloodhoof Village in Mulgore.', 'Elder Bloodhoof can be found at Bloodhoof Village in Mulgore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11576, 'enUS', 'Where is Elder Windtotem?', 'Where is Elder Windtotem?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11577, 'enUS', 'Elder Windtotem is keeping company with the goblins of Ratchet on the coast of the Barrens.', 'Elder Windtotem is keeping company with the goblins of Ratchet on the coast of the Barrens.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11578, 'enUS', 'Where is Elder High Mountain?', 'Where is Elder High Mountain?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11579, 'enUS', 'Elder High Mountain is at Vendetta Point in Southern Barrens.', 'Elder High Mountain is at Vendetta Point in Southern Barrens.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11580, 'enUS', 'Where is Elder Moonwarden?', 'Where is Elder Moonwarden?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11581, 'enUS', 'Elder Moonwarden is observing the Crossroads in Northern Barrens.', 'Elder Moonwarden is observing the Crossroads in Northern Barrens.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11582, 'enUS', 'Where is Elder Wheathoof?', 'Where is Elder Wheathoof?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11583, 'enUS', 'Elder Wheathoof is enjoying the vistas above Mulgore at Thunder Bluff.', 'Elder Wheathoof is enjoying the vistas above Mulgore at Thunder Bluff.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11584, 'enUS', 'Where is Elder Darkhorn?', 'Where is Elder Darkhorn?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11585, 'enUS', 'Elder Darkhorn can be found among the red rocks of Orgrimmar.', 'Elder Darkhorn can be found among the red rocks of Orgrimmar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11586, 'enUS', 'Where is Elder Morningdew?', 'Where is Elder Morningdew?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11587, 'enUS', 'Elder Morningdew has taken to watching the commotion aboard Fizzle & Pozzik\'s Speedbarge in Thousand Needles.', 'Elder Morningdew has taken to watching the commotion aboard Fizzle & Pozzik\'s Speedbarge in Thousand Needles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11588, 'enUS', 'Where is Elder Runetotem?', 'Where is Elder Runetotem?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11589, 'enUS', 'Last I heard, Elder Runetotem was traveling to Razor Hill in Durotar.', 'Last I heard, Elder Runetotem was traveling to Razor Hill in Durotar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11590, 'enUS', 'Where is Elder Grimtotem?', 'Where is Elder Grimtotem?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11591, 'enUS', 'You\'ll find Elder Grimtotem at the Lariss Pavilion in Feralas.', 'You\'ll find Elder Grimtotem at the Lariss Pavilion in Feralas.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11592, 'enUS', 'Where is Elder Mistwalker?', 'Where is Elder Mistwalker?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11593, 'enUS', 'Elder Mistwalker enjoys watching the struggles at the Maul in Feralas.', 'Elder Mistwalker enjoys watching the struggles at the Maul in Feralas.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11594, 'enUS', 'Where is Elder Skyseer?', 'Where is Elder Skyseer?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11595, 'enUS', 'Go to the Freewind Post in Thousand Needles and you will find Elder Skyseer.', 'Go to the Freewind Post in Thousand Needles and you will find Elder Skyseer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11596, 'enUS', 'Where is Elder Splitrock?', 'Where is Elder Splitrock?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11597, 'enUS', 'I think Elder Splitrock has traveled to Maraudon in Desolace.', 'I think Elder Splitrock has traveled to Maraudon in Desolace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11598, 'enUS', 'Where is Elder Dreamseer?', 'Where is Elder Dreamseer?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11599, 'enUS', 'Elder Dreamseer resides in Gadgetzan in Tanaris Desert.', 'Elder Dreamseer resides in Gadgetzan in Tanaris Desert.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11600, 'enUS', 'Where is Elder Ragetotem?', 'Where is Elder Ragetotem?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11601, 'enUS', 'Elder Ragetotem spends her days at the Valley of the Watchers in Tanaris Desert.', 'Elder Ragetotem spends her days at the Valley of the Watchers in Tanaris Desert.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11602, 'enUS', 'Where is Elder Wildmane?', 'Where is Elder Wildmane?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11603, 'enUS', 'Zul\'Farrak in Tanaris Desert is Elder Wildmane\'s home.', 'Zul\'Farrak in Tanaris Desert is Elder Wildmane\'s home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11604, 'enUS', 'Where is Elder Thunderhorn?', 'Where is Elder Thunderhorn?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11605, 'enUS', 'For reasons I cannot understand, Elder Thunderhorn has gone to the Slithering Scar in Un\'Goro Crater.', 'For reasons I cannot understand, Elder Thunderhorn has gone to the Slithering Scar in Un\'Goro Crater.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11606, 'enUS', 'Where is Elder Bladesing?', 'Where is Elder Bladesing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11607, 'enUS', 'If you travel to Cenarion Hold in Silithus, you will find Elder Bladesing.', 'If you travel to Cenarion Hold in Silithus, you will find Elder Bladesing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11608, 'enUS', 'Where is Elder Primestone?', 'Where is Elder Primestone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11609, 'enUS', 'Elder Primestone has spent many years in the Crystal Vale in Silithus.', 'Elder Primestone has spent many years in the Crystal Vale in Silithus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11610, 'enUS', 'To honor your elders is to honor your culture and your people, $c.  For this, the coins of ancestry serve as a tangible means to recognize those who choose to follow a venerable path of respect.$B$BDuring this Lunar Festival, you may have the opportunity to honor many elders.  As you do so, you will acquire the coins of ancestry.  Bring me these coins, and I will reward your diligence with a selection of unique items.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11611, 'enUS', 'Qiraji War Party spotted!', 'Qiraji War Party spotted!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11612, 'enUS', 'Sandstorm coming from the south, take co...That\'s no sandstorm! INCOMING QIRAJI ARMY!', 'Sandstorm coming from the south, take co...That\'s no sandstorm! INCOMING QIRAJI ARMY!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11613, 'enUS', 'Invaders approaching from the south!', 'Invaders approaching from the south!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11614, 'enUS', 'Crush them all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11615, 'enUS', 'The Might of Kalimdor will halt their advances!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11616, 'enUS', 'Soldiers of Kalimdor, defend the base!', 'Soldiers of Kalimdor, defend the base!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11617, 'enUS', 'I\'d like a new invitation to the Lunar Festival.', 'I\'d like a new invitation to the Lunar Festival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11618, 'enUS', '', 'Greetings, $n.  We\'ve travelled from Moonglade to spread the spirit of the Lunar Festival far and wide.$B$BWon\'t you join us in our celebration?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11619, 'enUS', 'The final battle for Kalimdor is upon us. The supreme commander of the Might of Kalimdor orders his troops to march upon Ahn\'Qiraj and destroy all in their path.', 'The final battle for Kalimdor is upon us. The supreme commander of the Might of Kalimdor orders his troops to march upon Ahn\'Qiraj and destroy all in their path.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11620, 'enUS', '%s stands before the Might of Kalimdor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11621, 'enUS', 'I am Saurfang. Brother of Broxigar. \n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11622, 'enUS', 'You know me to be the Supreme Commander of the Might of Kalimdor.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11623, 'enUS', '%s pauses and looks across the sea of soldiers that stand nervously before him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11624, 'enUS', 'An orc - a true orc warrior - wishes for one thing: To die in the glory of battle against a hated enemy.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11625, 'enUS', 'Some of you have not fought in battles. Peace has been with us for many years. Many years we sat idle but many years we battled.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11626, 'enUS', 'In those years - where strife filled the land as Legion and Scourge sacked our homes, killed our families - these insects dwelled beneath us. Beneath our homes - waiting.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11627, 'enUS', 'Waiting to crush the life from our little ones. To slay all in their path. This they do for their god.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11628, 'enUS', 'And for our gods?\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11629, 'enUS', 'We defend. We stand. We show that as one. United. We destroy. Their god will fall.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11630, 'enUS', 'To die today, on this field of battle, is to die an orcish death. To die today is to die for our little ones. Our old ones. Our ... loved ones.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11631, 'enUS', 'Would any of you deny yourselves such a death? Such an honor?\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11632, 'enUS', 'FOR KALIMDOR!', 'FOR KALIMDOR!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11633, 'enUS', 'FOR HONOR!\n', 'FOR HONOR!\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11634, 'enUS', 'FER THE KING!', 'FER THE KING!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11635, 'enUS', 'FER MAGNI!', 'FER MAGNI!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11636, 'enUS', 'FER KHAZ\'MODAN!', 'FER KHAZ\'MODAN!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11637, 'enUS', 'FER IRONFORGE!', 'FER IRONFORGE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11638, 'enUS', 'I am with you until I gasp my last dying breath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11639, 'enUS', '', '%s says a prayer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11640, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11641, 'enUS', 'For the Warchief!', 'For the Warchief!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11642, 'enUS', 'FOR THE HORDE!', 'FOR THE HORDE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11643, 'enUS', 'For Broxigar!', 'For Broxigar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11644, 'enUS', 'For Kalimdor!', 'For Kalimdor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11645, 'enUS', 'FOR THE HORDE! FOR CAIRNE! FOR THUNDER BLUFF!', 'FOR THE HORDE! FOR CAIRNE! FOR THUNDER BLUFF!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11646, 'enUS', 'ONWARD! TO AHN\'QIRAJ!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11647, 'enUS', 'Our fates lie ahead...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11648, 'enUS', 'Face the Might of Kalimdor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11649, 'enUS', 'The Might of Kalimdor is victorious! The last of the Qiraji forces are defeated, retreating back to the ancient city-fortress of Ahn\'Qiraj. The Qiraji Resonating Crystals littering Kalimdor vanish as quickly as they appeared, leaving behind nothing but sundered earth.', 'The Might of Kalimdor is victorious! The last of the Qiraji forces are defeated, retreating back to the ancient city-fortress of Ahn\'Qiraj. The Qiraji Resonating Crystals littering Kalimdor vanish as quickly as they appeared, leaving behind nothing but sundered earth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11650, 'enUS', 'We\'re on orders from on high to destroy Rajaxx. Terminate with extreme prejudice if you want to get technical.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11651, 'enUS', 'The battle is won! Watch as they flee to the safety of their precious temple. Soon all will be razed... Their leaders destroyed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11652, 'enUS', 'We stand together with the Alliance, $r. An evil, so old that the world has forgotten it, seeks to engulf us all; first on Kalimdor and then the rest of Azeroth. It is driven by a hatred of all things born of the great Shapers: The eternal Titans.$B$BWe are all that stand in the way of C\'Thun\'s armies. We will strike first and bring down the forces of Ahn\'Qiraj. Let all of Azeroth quake at the combined might of the Horde and Alliance.$B$BThe war effort needs you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11653, 'enUS', 'Where is Elder Meadowrun?', 'Where is Elder Meadowrun?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11654, 'enUS', 'I last saw Elder Meadowrun at the Weeping Cave in the Western Plaguelands.', 'I last saw Elder Meadowrun at the Weeping Cave in the Western Plaguelands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11656, 'enUS', 'Where is Elder Starglade?', 'Where is Elder Starglade?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11657, 'enUS', 'You\'ll find Elder Starglade outside the gates to Zul\'Gurub in Stranglethorn Vale.', 'You\'ll find Elder Starglade outside the gates to Zul\'Gurub in Stranglethorn Vale.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11658, 'enUS', '\"Great job collecting resources for the war,\" they said, \"Have a promotion,\" they said.  What they really meant is \"You\'re stationed outside a bughive in Silithus with a bunch of sweaty dwarves, Carnes!\"$B$BThanks for the promotion, Captain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11659, 'enUS', '', 'I, like my brother, honor those who honor their ancestors.  It brings me great joy to share my crafting secrets to disciples of the Lunar Festival!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11660, 'enUS', 'Run if you want cowards!  It will do you no good!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11661, 'enUS', 'I, like many others, came to aid my fellow druids when the war started.$B$BNow that the Gates of Ahn\'Qiraj are open, I\'ve realized that much work still remains to be done.  I\'ve decided to stay in Silithus and do my best to help the cause, $n.  ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11662, 'enUS', 'As deadly as the silithid seem, they are but tame creatures compared to their Qiraji overlords.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11663, 'enUS', 'This war\'s not over, it\'s only just begun!  We have brave men and women fighting our enemy inside Ahn\'Qiraj day and night, $n.  It won\'t be long before the Qiraji city falls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11664, 'enUS', '', 'Our steady progress in our fight against the Qiraji can be attributed to the diverse help we received from nearly every corner of Kalimdor.$B$BYou need more than just soldiers to fight a war, $n.  Many of the war\'s unsung heroes are the traders and craftsmen that keep our forces supplied with the resources they need.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11665, 'enUS', 'They know how to throw a party here in Moonglade.  Who would have thought so?', 'They know how to throw a party here in Moonglade.  Who would have thought so?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11666, 'enUS', 'Try these fireworks launchers.  They\'re a blast!', 'Try these fireworks launchers.  They\'re a blast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11667, 'enUS', 'This beam of moonlight leads to Stormwind.  Use your invitation when you\'re ready.', 'This beam of moonlight leads to Stormwind.  Use your invitation when you\'re ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11668, 'enUS', 'Don\'t try to teleport to one of your enemies\' cities.  It won\'t work - we may all be friendly here, but back home old grudges remain...', 'Don\'t try to teleport to one of your enemies\' cities.  It won\'t work - we may all be friendly here, but back home old grudges remain...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11669, 'enUS', 'The people in Nighthaven sure know how to party!', 'The people in Nighthaven sure know how to party!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11670, 'enUS', 'These fireworks sure make a nice BOOM, don\'t you think?', 'These fireworks sure make a nice BOOM, don\'t you think?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11672, 'enUS', 'This moonlight will send you to Ironforge.  But who\'d want to leave this party?', 'This moonlight will send you to Ironforge.  But who\'d want to leave this party?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11673, 'enUS', 'The lunar celebration this year is quite magnificent!', 'The lunar celebration this year is quite magnificent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11674, 'enUS', 'They say these fireworks use the power of the moon!', 'They say these fireworks use the power of the moon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11675, 'enUS', 'This beam of greater moonlight will send you to Darnassus.', 'This beam of greater moonlight will send you to Darnassus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11676, 'enUS', 'If I didnt know better, I\'d say this party was organized by orcs!', 'If I didnt know better, I\'d say this party was organized by orcs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11677, 'enUS', 'Did you see?  That last firework almost caught that dwarf\'s shirt on fire!  Hah!', 'Did you see?  That last firework almost caught that dwarf\'s shirt on fire!  Hah!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11678, 'enUS', 'This moon beam will send you to Orgrimmar.  Use your invitation when you want to go.', 'This moon beam will send you to Orgrimmar.  Use your invitation when you want to go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11679, 'enUS', 'There\'s nothing like a little Lunar celebration to warm your bones!', 'There\'s nothing like a little Lunar celebration to warm your bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11680, 'enUS', 'You have to be careful lighting fireworks when you can\'t feel your fingers...', 'You have to be careful lighting fireworks when you can\'t feel your fingers...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11681, 'enUS', 'This beam of moonlight leads to the Undercity.  Retire when ready.', 'This beam of moonlight leads to the Undercity.  Retire when ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11682, 'enUS', 'I love the Lunar Festival!  It\'s a great place to let your hooves kick up dust!', 'I love the Lunar Festival!  It\'s a great place to let your hooves kick up dust!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11683, 'enUS', 'I don\'t know how these fireworks work, but they sure are pretty!', 'I don\'t know how these fireworks work, but they sure are pretty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11684, 'enUS', 'This moonlight will take you to Thunder Bluff.  Use it when you want to leave the festival.', 'This moonlight will take you to Thunder Bluff.  Use it when you want to leave the festival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11685, 'enUS', 'They\'re going to show fireworks all night long.  From 6PM until dawn, the sky will blaze over Elune\'ara!', 'They\'re going to show fireworks all night long.  From 6PM until dawn, the sky will blaze over Elune\'ara!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11686, 'enUS', 'They put these lanterns out to commemorate the closing of the Lunar Festival.', 'They put these lanterns out to commemorate the closing of the Lunar Festival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11687, 'enUS', 'It is the last night of the Lunar Festival.  May fortune be with you!', 'It is the last night of the Lunar Festival.  May fortune be with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11688, 'enUS', 'May your elders bless you, $n, and bring you luck in the coming year.', 'May your elders bless you, $n, and bring you luck in the coming year.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11689, 'enUS', 'How can I summon Omen?', 'How can I summon Omen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11690, 'enUS', 'Omen sleeps in the depths of Lake Elune\'ara, but may be summoned at the southeast banks near the Stormrage Barrow Dens.  There, you will find special cluster launchers...$B$BUse your rockets in the launchers and Omen\'s minions will come.  Fire more rockets, nearly three dozen, or fire Elune\'s candles at his minions, and soon he will awaken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11691, 'enUS', 'I wish to purchase another Elune\'s Candle.', 'I wish to purchase another Elune\'s Candle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11692, 'enUS', '%s shares his powers with his brethren.', '%s shares his powers with his brethren.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11693, 'enUS', '%s goes into a rage!', '%s goes into a rage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11694, 'enUS', '%s goes into a berserker rage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11695, 'enUS', '%s is frozen solid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11696, 'enUS', 'Long have we toiled to see our land returned to its former glory, $c. Ever shall we remain vigilant against those who have invaded and defiled Quel\'thalas. We will not rest until they are driven from our sight.$B$BLet none exist who stand in the way of the sin\'dorei!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11697, 'enUS', '', 'It\'s all a mess, $c, and it\'s all my fault! I was warned to get out of dock while there was still time, but did I listen? No.$B$BWhat am I going to do now? Those sickly blood elves, the Wretched, have made off with my magical goods, and ate them I think. I\'m pretty sure that means they can\'t be retrieved. And the Grimscale murlocs pirated away the rest of my goods in the dead of night! Furthermore, to kick an honest merchant when she\'s down, my crew abandoned ship!$B$BI think I\'m going to cry.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11698, 'enUS', '', 'Hello, $c. These are troubling times indeed if this is what is to become of our beloved Quel\'Thalas.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11700, 'enUS', 'The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11701, 'enUS', 'The Wretched are a constant thorn in my side, $n.  When they\'re not being a danger to themselves by tapping unstable arcane sources to satisfy their cravings, they\'re cooking up half-brained schemes against our respectable society.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11702, 'enUS', 'Only flesh and bone. Mortals are such easy prey...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11703, 'enUS', 'and Bone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11704, 'enUS', 'Mortals are...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11705, 'enUS', 'such easy pray.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11706, 'enUS', 'Where are your manners, brother. Let us properly welcome our guests.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11707, 'enUS', 'There will be pain...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11708, 'enUS', 'Oh so much pain...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11709, 'enUS', 'Come, little ones.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11710, 'enUS', 'The feast of souls begins now...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11711, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11713, 'enUS', 'Good riddance... now none shall be able to repeat my mistakes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11714, 'enUS', 'I hope that Loralthalis is alright, $c. Maybe I should go up to Duskwither Spire myself? Maybe I should have done that in the first place?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11715, 'enUS', 'A man can only live with himself and his shame for so long, $c, before he must take action to undo the mistakes he\'s made.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11716, 'enUS', 'A mess, that\'s what this is!  All of these creatures sprung up out of nowhere, and are running around out of control!  And where\'ve the Magister, and his apprentices, run off to?  Leaving me here all alone without as much as a, \"Hello there Wyllithen, would you like to come with?\"$B$BThe nerve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11717, 'enUS', 'These arcane sanctums of yours are quite... interesting Mr. Caidanis.  I appreciate the information you have given me today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11718, 'enUS', 'It\'s been a pleasure, Mr. Anvilward.  I must say your enthusiastic interest in our operations is most... unexpected.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11720, 'enUS', 'The walls have been breached!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11721, 'enUS', '', 'Attack!  Don\'t let them reach the gates!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11722, 'enUS', 'Generic Text2!', 'Generic Text2', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11723, 'enUS', 'Here, I\'d like to give you this token of my love.', 'Here, I\'d like to give you this token of my love.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11724, 'enUS', 'We\'ve been so wrapped up in this war, some of us forgot to make time for love.', 'We\'ve been so wrapped up in this war, some of us forgot to make time for love.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11725, 'enUS', 'What are those crates for?', 'What are those crates for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11726, 'enUS', 'I ordered a few shipments of new perfumes and colognes from the city. After I get them unpacked, check back and I\'ll have them for sale.', 'I ordered a few shipments of new perfumes and colognes from the city. After I get them unpacked, check back and I\'ll have them for sale.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11727, 'enUS', 'You do not belong here!  Ancients, rise against these intruders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11728, 'enUS', '', 'That was quite a fight.  Let us catch our breath for a bit.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11729, 'enUS', 'Hey, $gmister:miss;.  You look like a $C with discerning tastes in wine.  I have a favor to ask, and I think I can make you an offer you can\'t refuse...forget about it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11730, 'enUS', 'What are you looking at, $glad : lass;?  Never seen a dwarf before, then?$B$BYou\'d best be on yer way if you don\'t want any trouble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11731, 'enUS', 'I need a moment of your time, sir.', 'I need a moment of your time, sir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11732, 'enUS', 'Ah, you must be the $glad : lass; assigned to show me around the Eversong Woods.  Took your sweet time getting here, didn\'t you?$B$BVery well, off we go.  Let us not waste one more minute.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11733, 'enUS', 'Why... yes, of course.  I\'ve something to show you right inside this building, Mr. Anvilward.', 'Why... yes, of course.  I\'ve something to show you right inside this building, Mr. Anvilward.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11734, 'enUS', 'Very well.  Let\'s see what you have to show me, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11735, 'enUS', 'What manner of trick is this, $r?  If you seek to ambush me, I warn you I will not go down quietly!  ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11736, 'enUS', 'We\'ve been so wrapped up in this war, some of us forgot to make time for love.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11737, 'enUS', '', 'We\'ve been so wrapped up in this war, some of us forgot to make time for love.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11738, 'enUS', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11739, 'enUS', '', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11740, 'enUS', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11741, 'enUS', 'Greetings, $c. I trust that you bring me good news concerning the Worldwide Invitational event? If you are, then surely that means you were given a secret code to tell me. In return for your code I will give you a gift of one of the panda cubs that I take care of.$B$BJust whisper it in my ear when you are ready.', 'Greetings, $c. I trust that you bring me good news concerning the Worldwide Invitational event? If you are, then surely that means you were given a secret code to tell me. In return for your code I will give you a gift of one of the panda cubs that I take care of.$B$BJust whisper it in my ear when you are ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11742, 'enUS', 'I would like to whisper my secret code to you to receive my Panda Cub pet.', 'I would like to whisper my secret code to you to receive my Panda Cub pet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11743, 'enUS', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 26124);
