INSERT INTO `broadcast_text_locale` VALUES (11745, 'enUS', 'We\'ve been so wrapped up in this war, some of us forgot to make time for love.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11746, 'enUS', '', 'I don\'t know what\'s come over me. My heart feels as hot as new steel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11747, 'enUS', 'I don\'t know what\'s come over me. My heart feels as hot as new steel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11748, 'enUS', '', 'You work with machines for so long, sometimes you forget about real hearts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11749, 'enUS', 'You work with machines for so long, sometimes you forget about real hearts.', 'You work with machines for so long, sometimes you forget about real hearts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11750, 'enUS', 'You work with machines for so long, sometimes you forget about real hearts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (11751, 'enUS', '', 'When you have lived as long as I, it becomes easy to forget about love for years. It is always good to be reminded.', 26124);
