INSERT INTO `broadcast_text_locale` VALUES (11800, 'enUS', 'What?! How did you sneak past my jail...er, minion out there? Well, I suppose it\'s for the best. Sales have been a little slow lately.$B$BHow may I fulfill your infernal needs, $c?', '', 26124);
