INSERT INTO `broadcast_text_locale` VALUES (13844, 'enUS', 'You\'ll ruin everything!', 'You\'ll ruin everything!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13845, 'enUS', 'Off with you!', 'Off with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13846, 'enUS', 'We do not wish to be disturbed!', 'We do not wish to be disturbed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13847, 'enUS', 'Where am I? What--', 'Where am I? What--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13848, 'enUS', 'What is happening?', 'What is happening?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13849, 'enUS', 'I mustn\'t be afraid....', 'I mustn\'t be afraid....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13850, 'enUS', '%s whimpers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13851, 'enUS', 'Begone, outsider!', 'Begone, outsider!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13852, 'enUS', 'You\'ll not ruin the celebration!', 'You\'ll not ruin the celebration!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13853, 'enUS', 'We are besieged!', 'We are besieged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13854, 'enUS', 'You should never have come here!', 'You should never have come here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13855, 'enUS', 'Now you will pay the ultimate price!', 'Now you will pay the ultimate price!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13856, 'enUS', 'The fates have turned against me!', 'The fates have turned against me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13857, 'enUS', 'My time is done.', 'My time is done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13858, 'enUS', 'No, not now! Not like this!', 'No, not now! Not like this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13859, 'enUS', 'Open your senses, little one. Can you not feel it - the pain, the suffering this tree endured when it was ripped from the nourishing earth? We will heal it and give it peace, though the tree will never grow again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13860, 'enUS', 'It is done, little one. Now, we must focus our efforts on preventing such destruction from claiming any more victims.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13861, 'enUS', 'I\'ll chop you into minced meat!', 'I\'ll chop you into minced meat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13862, 'enUS', 'I\'ll gut you like a fish!', 'I\'ll gut you like a fish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13863, 'enUS', 'I\'ll carve the meat from your bones!', 'I\'ll carve the meat from your bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13864, 'enUS', 'I have served to the last....', 'I have served to the last....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13865, 'enUS', 'This isn\'t the end; it can\'t be!', 'This isn\'t the end; it can\'t be!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13866, 'enUS', 'I tried, master. I tried.', 'I tried, master. I tried.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13867, 'enUS', '%s urges you to follow him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13868, 'enUS', '%s points to the totem of Coo.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13869, 'enUS', '%s motions for you to follow him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13870, 'enUS', '[Furbolg] Ritk kin\'chikx azul azure summit...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13871, 'enUS', '[Furbolg] Coo xip fly... Jump ilos river. Find Tikti.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13872, 'enUS', '%s points across the river.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13873, 'enUS', '[Furbolg] Far you mixik tak come. Gaze upon the kitch\'kal river. Follow. Ilog to Yor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13874, 'enUS', '%s asks that you follow him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13875, 'enUS', '[Furbolg] Go... Search yitix\'kil bottom river. South!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13876, 'enUS', 'Impudent outsiders!', 'Impudent outsiders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13877, 'enUS', 'Meddling fools! You will pay with your lives!', 'Meddling fools! You will pay with your lives!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13878, 'enUS', 'Wait--no! No!', 'Wait--no! No!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13879, 'enUS', 'You\'ll never make it out alive....', 'You\'ll never make it out alive....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13880, 'enUS', '', 'Come play with me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13881, 'enUS', '', 'You WILL be mine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13882, 'enUS', '', 'Come here, pretty. You have what I need!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13883, 'enUS', '', 'So I said, \"Yeah, but that\'ll cost you extra.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13884, 'enUS', '', 'Five seconds. I\'m not kidding!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13885, 'enUS', '', 'He asked if the imp could join in--can you believe it? Actually, it wasn\'t half bad....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13886, 'enUS', '', 'They fall asleep after. Me, I fall asleep during....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13887, 'enUS', '', 'Come any closer, and I\'ll scream.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13888, 'enUS', '', 'I want to show you a different side of me....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13889, 'enUS', '', 'It was fun while it lasted....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13890, 'enUS', '', '<sigh> It\'s always over too soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13891, 'enUS', '', 'I want you to be with me... forever and ever.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13892, 'enUS', '', 'Shhh... I have a little secret I\'ve been keeping.\n', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13893, 'enUS', '', 'Come play with me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13894, 'enUS', '', 'Come play with me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13895, 'enUS', '', 'I\'ve been very, very naughty....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13896, 'enUS', '', 'Enough foreplay. Let\'s get down to business.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13897, 'enUS', '', 'Just when things were getting interesting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13898, 'enUS', '', 'We could have had so much fun!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13899, 'enUS', '', 'Hmm... it\'s not in this stack. Maybe I left it on the shelf.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13900, 'enUS', '', '%s leafs through the pages of a journal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13901, 'enUS', '[Furbolg] Come, $n. Let us leave the water together, purified.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13902, 'enUS', '', 'Aha! Here\'s the sketch I was looking for...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13903, 'enUS', '[Furbolg] We go now, together. We will seek Vark.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13904, 'enUS', '', 'Here, Master Nemos, this is the drawing I was talking about.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13905, 'enUS', '', '%s offers the journal to Architect Nemos.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13906, 'enUS', '%s accepts the book from Apprentice Boulian and glances at the indicated page.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13907, 'enUS', '[Furbolg] Follow me to Vark.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13908, 'enUS', 'Ah, yes, I see what you were talking about now. Thank you, Boulian.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13909, 'enUS', 'It\'s a great assignment, yeah, but \"all looking and no touching\" gets old after a while.', 'It\'s a great assignment, yeah, but \"all looking and no touching\" gets old after a while.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13910, 'enUS', 'I think she winked at me.', 'I think she winked at me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13911, 'enUS', 'This beats patrolling the Opera Hall. Theater people scare me.', 'This beats patrolling the Opera Hall. Theater people scare me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13912, 'enUS', 'Another sold-out crowd.', 'Another sold-out crowd.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13913, 'enUS', 'Haven\'t I seen tonight\'s show before? I could have sworn...', 'Haven\'t I seen tonight\'s show before? I could have sworn...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13914, 'enUS', 'Urgent business! Yes, but what? And how did I end up here...?', 'Urgent business! Yes, but what? And how did I end up here...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13915, 'enUS', 'Tickets aren\'t easy to come by, I hear.', 'Tickets aren\'t easy to come by, I hear.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13916, 'enUS', 'You must not interfere!', 'You must not interfere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13917, 'enUS', 'What are you doing? Be off!', 'What are you doing? Be off!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13918, 'enUS', 'I\'ll miss the show!', 'I\'ll miss the show!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13919, 'enUS', 'Yes, I think I remember now....', 'Yes, I think I remember now....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13920, 'enUS', 'Clear the area!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13921, 'enUS', 'Where\'s your ticket?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13922, 'enUS', 'The show is for guests only!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13923, 'enUS', 'You should not have come here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13924, 'enUS', 'You\'ll be joining the other guests now....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13925, 'enUS', 'You cannot stop us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13926, 'enUS', 'Your death will soon follow....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13927, 'enUS', '[Furbolg] Hurry, $n. Vark awaits you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13928, 'enUS', 'You\'re not allowed here!', 'You\'re not allowed here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13929, 'enUS', 'You\'re not a guest!', 'You\'re not a guest!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13930, 'enUS', 'Am I released from my duties?', 'Am I released from my duties?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13931, 'enUS', 'At last, my work is done.', 'At last, my work is done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13932, 'enUS', 'Step lively; the master is counting on us!', 'Step lively; the master is counting on us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13933, 'enUS', 'More paint for the Oz set--yes, I\'ll have to remember....', 'More paint for the Oz set--yes, I\'ll have to remember....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13934, 'enUS', 'How many set changes? Same as last time, I suppose.', 'How many set changes? Same as last time, I suppose.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13935, 'enUS', 'And I MUST have a carafe of sweet nectar in my dressing room. It\'s right there in my contract!', 'And I MUST have a carafe of sweet nectar in my dressing room. It\'s right there in my contract!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13936, 'enUS', 'I told him he would simply have to contact my agent.', 'I told him he would simply have to contact my agent.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13937, 'enUS', 'No, no, that was under the OLD guild rules....', 'No, no, that was under the OLD guild rules....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13938, 'enUS', 'I will not be upstaged by amateurs!', 'I will not be upstaged by amateurs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13939, 'enUS', 'The show must go on!', 'The show must go on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13940, 'enUS', 'It\'s so cold, so dark.... I\'m not overacting, am I?', 'It\'s so cold, so dark.... I\'m not overacting, am I?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13941, 'enUS', 'Make sure my name is spelled correctly on the tombst--', 'Make sure my name is spelled correctly on the tombst--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13942, 'enUS', 'Your insolence will not be tolerated!', 'Your insolence will not be tolerated!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13943, 'enUS', 'You were not invited!', 'You were not invited!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13944, 'enUS', 'It\'s all... coming back to me....', 'It\'s all... coming back to me....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13945, 'enUS', 'Is it really over?', 'Is it really over?', 26124);
