INSERT INTO `broadcast_text_locale` VALUES (13948, 'enUS', 'Free... at last.', 'Free... at last.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13949, 'enUS', 'Time to move on.', 'Time to move on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13950, 'enUS', '[Furbolg] Bear witness to the savage acts of our adversaries, $n. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13951, 'enUS', 'Thieves! Stop them!', '', 26124);
