INSERT INTO `broadcast_text_locale` VALUES (13954, 'enUS', 'The same fate awaits all who would steal from the master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13955, 'enUS', 'Death is far too merciful for your kind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13956, 'enUS', 'You will not make it out alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13957, 'enUS', 'Others will take my place!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13958, 'enUS', 'This... changes nothing. Eternal damnation awaits you!\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13959, 'enUS', '', 'Welcome to Forest Song, $c. If there is anything I can do for you, please let me know.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13960, 'enUS', 'No! We were here first!', 'No! We were here first!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13961, 'enUS', 'The secrets of the library are ours and ours alone!', 'The secrets of the library are ours and ours alone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13962, 'enUS', 'A treasure trove of knowledge, ripe for the taking!', 'A treasure trove of knowledge, ripe for the taking!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13963, 'enUS', 'Powers beyond comprehension, all at our fingertips...', 'Powers beyond comprehension, all at our fingertips...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13964, 'enUS', 'Beware... the upper reaches.', 'Beware... the upper reaches.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13965, 'enUS', 'Turn back while you can.', 'Turn back while you can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13966, 'enUS', 'I wish to browse your wares.', 'I wish to browse your wares.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13967, 'enUS', 'The secrets of this place are not intended for the likes of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13968, 'enUS', 'Amateurs! You are far beyond your depth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13969, 'enUS', 'The tower is no place for the uninitiated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13970, 'enUS', 'You never should have stepped foot in this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13971, 'enUS', 'Your bravado will be rewarded... with death.\n', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13972, 'enUS', 'You will succeed only in damning yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13973, 'enUS', 'Turn back while you can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13974, 'enUS', '%s gnaws at the bars of its cage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13975, 'enUS', '%s rattles its cage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13976, 'enUS', '%s calls for help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13977, 'enUS', '%s growls at its captors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13978, 'enUS', '[Furbolg] The Stillpine furbolgs will not soon forget your bravery!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13979, 'enUS', '[Furbolg] Those remaining at Stillpine Hold will welcome you as a hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13980, 'enUS', '[Furbolg] The prophecy is true!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13981, 'enUS', 'You have faced many challenges.... Pity they were all in vain! Soon your people will kneel to MY lord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13982, 'enUS', 'I\'ve failed... peace is impossible.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13983, 'enUS', 'What in the Light\'s name...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13984, 'enUS', 'Fel orcs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13985, 'enUS', 'The cycle of bloodshed is unending... Is there nothing I can do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13986, 'enUS', 'Do not return, draenei scum.  Next time we won\'t spare your life, unarmed or not!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13987, 'enUS', '', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13988, 'enUS', 'You can die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13989, 'enUS', 'Yes sir!  I will not fail the Fel Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13990, 'enUS', '%s signals a charge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13991, 'enUS', 'What have you done, Makuru?!  These are not our ways!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13992, 'enUS', 'I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13993, 'enUS', 'Don\'t be afraid; we\'re here to aid you. We\'re grateful for the defense you\'ve granted us within our former home, and as a measure of thanks, we\'ve come to help you return quickly to the Plaguelands should you need it. My compatriot can explain the process further; you\'ll find him where the arcane energies are strongest within this city.', 'Don\'t be afraid; we\'re here to aid you. We\'re grateful for the defense you\'ve granted us within our former home, and as a measure of thanks, we\'ve come to help you return quickly to the Plaguelands should you need it. My compatriot can explain the process further; you\'ll find him where the arcane energies are strongest within this city.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13994, 'enUS', 'I am truly sorry, $c, but as your spirit has never before experienced the horrors that lie in wait within the burning wreck of Stratholme, our magic cannot carry you to the city.', 'I am truly sorry, $c, but as your spirit has never before experienced the horrors that lie in wait within the burning wreck of Stratholme, our magic cannot carry you to the city.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13995, 'enUS', 'I can see in your eyes you\'re no stranger to the tragedy within Stratholme. If you wish to end the miserable existence of the poor souls trapped therein, step into the circle. It leads directly to the gates of the burning city itself.', 'I can see in your eyes you\'re no stranger to the tragedy within Stratholme. If you wish to end the miserable existence of the poor souls trapped therein, step into the circle. It leads directly to the gates of the burning city itself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13996, 'enUS', 'The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13997, 'enUS', 'No!  Not... Sedai!  The orcs must pay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13998, 'enUS', '[Furbolg] Behold the prophecy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (13999, 'enUS', 'Do you smell that?  Fresh meat has somehow breached our citadel.  Be wary of any intruders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14000, 'enUS', 'Intruders!  Hold them off until I can release the warhounds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14001, 'enUS', 'You\'re too late, now feel the wrath of my warhounds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14002, 'enUS', '<The footman stand resolute, muscles tense.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14003, 'enUS', 'Stop it with this Light nonsense.  The swamp is where we need to go!  We belong there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14004, 'enUS', 'Bah!  Suit yourselves.  We\'re leaving!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14005, 'enUS', 'I... I want to hear about the Light.  I think... I remember...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14006, 'enUS', 'Welcome, my brethren!  You\'ve been freed from demons.  Embrace the teachings of Velen!  Embrace the light!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14007, 'enUS', 'As we attempt to gain the help of the peoples of this world against the Burning Legion, we must also learn to live peaceably amongst them.$B$BPlease keep this in mind as your journey takes you throughout Azeroth.  May the Light guide you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14008, 'enUS', 'Control Human Footman', 'Control Human Footman', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14009, 'enUS', '[Furbolg] Spirits grant us vision!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14010, 'enUS', 'I am ready to be flown down to the Exodar.', 'I am ready to be flown down to the Exodar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14011, 'enUS', 'What brings you all the way up here, little $r?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14012, 'enUS', 'Are you ready, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14013, 'enUS', '', 'Greetings, $c.  I get so few visitors up here.  To what do I owe the pleasure?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14014, 'enUS', '', 'Susurrus is expecting you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14015, 'enUS', 'You are brave to swim down to my realm, land-walker.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14016, 'enUS', '', 'I have been expecting you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14017, 'enUS', 'I wish to learn more about blacksmithing.', 'I wish to learn more about blacksmithing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14018, 'enUS', 'Be careful out in the desert, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14019, 'enUS', 'The sound of footsteps shuffling echo throughout the cave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14020, 'enUS', 'What\'s that $T scrub : girlie;?  You want your mommy?  Well too bad.  Maybe you should pay a visit to the Thrallmar officer\'s club to get her back?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14021, 'enUS', 'Do you know where you are?  This is Honor Hold, sweetheart!  Love it or get out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14022, 'enUS', 'You stay away from crystals! Die now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14023, 'enUS', 'This isn\'t a field trip, maggot!  You\'re in the Outland now!  Shape up or take a stroll back through the portal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14024, 'enUS', 'I have neither the time, nor the inclination, to explain things to you, scumbag!  Now do you have something important to say, or are you just here to ask me out for a drink, princess?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14025, 'enUS', 'What is your major malfunction?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14026, 'enUS', 'I\'ve seen you prancing around.  Why don\'t you do us all a favor and just drop your gear off at the armory?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14027, 'enUS', 'Who in name of Kurdran let you through the Dark Portal?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14028, 'enUS', 'Well, well, well, what have we here?  A $T civilian : civilian;?  Isn\'t that precious?  You\'ll get no special treatment from me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14029, 'enUS', '%s roars!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14030, 'enUS', '', 'These pictographs tell a story. This figure at the beginning, almost looks like one of the creatures we encountered at Azure Watch...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14031, 'enUS', '', 'No, this can\'t be... It says this creature willingly became a servant of the Legion. He transforms into a satyr and receives the Legion\'s \"blessing.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14032, 'enUS', '', 'We must defeat these satyr, $n. We will not suffer the Legion\'s presence in these lands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14033, 'enUS', 'Keep your voice down, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14034, 'enUS', 'If you have something to say, say it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14035, 'enUS', 'You\'re a $T civilian : civilian;?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14036, 'enUS', 'I\'m busy, make it fast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14037, 'enUS', '[Furbolg] There is currently an assault in progress. We must wait before unleashing another!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14038, 'enUS', 'Stillpine raiders, the time to strike is at hand! Charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14039, 'enUS', 'Have you spoken with the high priestess yet?  She\'s very eager to hear the tale of the pilgrimage from your point of view.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14040, 'enUS', 'How fares your search for the Mark?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14041, 'enUS', 'This is a great day, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14042, 'enUS', 'I cannot thank you enough for making my pilgrimage possible!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14043, 'enUS', 'Me not like this one...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14044, 'enUS', 'Hey, you numbskull!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14045, 'enUS', 'We kill his friend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14046, 'enUS', 'Smash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14047, 'enUS', 'Why don\'t you let me do the talking?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14048, 'enUS', 'If you nice me let you live.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14049, 'enUS', '[Furbolg] What could it mean?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14050, 'enUS', 'About time the guild sent some new blood our way! Too bad you came all this way for nothing. We\'re not allowed to put on a show for our guests, can you believe it? Moroes has forbidden us from starting a new show, and my backers are going nuts! Something needs to be done about Moroes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14051, 'enUS', 'Finally, everything is in place. Are you ready for your big stage debut?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14052, 'enUS', 'I\'m not an actor.', 'I\'m not an actor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14053, 'enUS', 'Don\'t worry, you\'ll be fine. You look like a natural!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14054, 'enUS', 'Ok, I\'ll give it a try, then.', 'Ok, I\'ll give it a try, then.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14055, 'enUS', 'Intruders are upon us!  Help me, brothers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14056, 'enUS', 'Splendid. I\'m going to get the audience ready. Break a leg!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14057, 'enUS', 'Good evening ladies and gentlemen, welcome to this evening\'s presentation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14058, 'enUS', 'Acteon, where is the best area to hunt for moongraze stags?', 'Acteon, where is the best area to hunt for moongraze stags?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14059, 'enUS', 'Moongraze stags can be found grazing south and east of Azure Watch.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14060, 'enUS', 'Acteon, where is the best place to hunt moongraze bucks?', 'Acteon, where is the best place to hunt moongraze bucks?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14061, 'enUS', 'Just north of Azure Watch is where you will have the best luck finding moongraze bucks.$B$BHappy hunting, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14062, 'enUS', 'Where can I find more infected nightstalker runts, Exarch?', 'Where can I find more infected nightstalker runts, Exarch?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14063, 'enUS', 'The infected nightstalker runts seem to be coming from the north. If I were you, that\'s where I would start my hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14064, 'enUS', '%s roars in pain! ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14065, 'enUS', 'Magtoor, thank you for allowing me to stay here with you.  I\'m sure that there is much that we can learn from each other.  And I\'m eager to learn all that I can of the Lost Ones to the east.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14066, 'enUS', 'Great Tzerak, in the name of Zevrax, we summon you forth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14067, 'enUS', 'Cast down the enemies of the Burning Legion, rain down destruction upon those who would oppose us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14068, 'enUS', 'Of course, my friend.  You are welcome to stay with us for as long as you like.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14069, 'enUS', 'Long have we craved your return! Command us in the destruction of the Legion\'s greatest foes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14070, 'enUS', 'Fools! Weaklings! Tzerak has no need of your assistance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14071, 'enUS', 'Wow, you totally bombed out there. Let\'s think of that as a rehearsal, shall we? Ready to go out again?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14072, 'enUS', '%s throws his head back and laughs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14073, 'enUS', 'Entering Phase 3 <PH>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14074, 'enUS', 'The wolf\'s going down.', 'The wolf\'s going down.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14075, 'enUS', 'Why do you traffic with the Horde?  Their foul ways are beneath you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14076, 'enUS', 'A-ha! Found one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14077, 'enUS', 'Another one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14078, 'enUS', 'Oh that\'s a big one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14079, 'enUS', 'I wonder if you can really hear the ocean in these things.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14080, 'enUS', 'It\'s ironic, I hate the ocean but I love the beach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14081, 'enUS', 'We have no choice.  If we fail to honor the presence of the Horde, then surely they will crush us from nearby Stonard!  I\'m afraid that not even you and your friends would be able to stop them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14082, 'enUS', 'Yes Master, all goes along as planned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14083, 'enUS', '%s places the shell in his pack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14084, 'enUS', '%s holds the shell up to his ear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14085, 'enUS', 'I must find a way to put an end to the evil that Cersei Dusksinger is committing against our lost brothers and sisters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14086, 'enUS', '%s examines the shell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14087, 'enUS', 'Be careful, Avuun.  One of the merchants that I trade with in Stonard has heard her raving about some horrible disease she\'s trying to perfect.  They say it\'s for use against the draenei!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14088, 'enUS', 'Anchorite, we are honored that you are here, but I am curious as to what you truly believe you can accomplish?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14089, 'enUS', 'Magtoor, I have faith that there is a way to bring the Lost Ones back from the brink.  By studying them, I will find a way to cure the fel energies that have consumed them.  And in so doing, I will be able to reverse the damage to the Broken as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14090, 'enUS', 'And now, I cut you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14091, 'enUS', 'DIE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14092, 'enUS', 'Can\'t you see that I\'m busy?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14093, 'enUS', 'It\'s over, Spark. The admiral knows it was you who betrayed the Alliance. Now you\'re either going to cooperate with me and tell me everything that you know or we\'re going to engage in some fisticuff.', 'It\'s over, Spark. The admiral knows it was you who betrayed the Alliance. Now you\'re either going to cooperate with me and tell me everything that you know or we\'re going to engage in some fisticuff.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14094, 'enUS', '', 'Hahaha!  That fool will never find out what I\'m up to!  Or, if he does, it\'ll be too late for the draenei!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14095, 'enUS', '', 'These orcs are fools!  So much power nearby just waiting to be taken, and all they do is sit on their hands and watch while others plunder the riches!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14096, 'enUS', '', 'I cannot wait to see the results of this latest batch!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14097, 'enUS', '', 'Avuun dares to oppose me?!  I will crush him!  When I am done with the draenei, they\'ll wish that they were Lost Ones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14098, 'enUS', '', 'Oh, would you please stop checking your nails and spanking yourself, for one moment?  Really, no one cares!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14099, 'enUS', '', 'Are you hungry?  I\'m hungry.  I just can\'t stand the thought of cured ham steak from Thultash again.  If only we could get some of that succulent roasted quail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14101, 'enUS', '', 'I need some fresh air, not that there\'s any to be had in this swamp!  I\'m going out for a bit.  Tidy up a bit while I\'m gone, would you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14102, 'enUS', '', 'Must you follow me everywhere? <sigh>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14103, 'enUS', '', 'Look at her down there!  Just who does she think she is?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14104, 'enUS', 'Hmph, no doubt she believes herself to be the new mistress of Stonard.  Perhaps we should offer to help her succubi tidy up her messes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14105, 'enUS', 'Her methods are cowardly.  She reminds me of the Forsaken apothecaries.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14106, 'enUS', '', 'You have something to say to my face?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14107, 'enUS', '', 'I thought not!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14108, 'enUS', 'Putting the bottles away, %s sighs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14109, 'enUS', '%s stares at you in sheer wonderment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14110, 'enUS', 'I run now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14111, 'enUS', 'How did I let myself get stationed here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14112, 'enUS', 'Uther Lightbringer brought only darkness to Quel\'Thalas.  Let us see if we can bring some darkness to his tomb!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14113, 'enUS', '', '%s inspects the contents of the crates.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14114, 'enUS', 'What is taking so long?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14115, 'enUS', 'At last I will have my revenge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14116, 'enUS', 'The brazier fires upon the helpless orc! [PH]', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14117, 'enUS', 'Rough audience, but you\'ll win them over. Ready for another shot?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14118, 'enUS', 'Welcome ladies and gentlemen, to this evening\'s presentation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14119, 'enUS', 'The romantic plays are really tough, but you\'ll do better this time. You have TALENT. Ready?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14120, 'enUS', 'I\'ll nail it!', 'I\'ll nail it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14121, 'enUS', 'I\'ve never been more ready.', 'I\'ve never been more ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14122, 'enUS', 'Welcome ladies and gentlemen, to this evening\'s presentation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14123, 'enUS', '[Furbolg] Mil\'ork kix ilfin o oomtor...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14124, 'enUS', '[Furbolg] Poorov\'tarkh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14125, 'enUS', '[Furbolg] Yar vartslaf?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14126, 'enUS', '[Furbolg] Boro gomsho!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14127, 'enUS', 'You feeble dogs are still weak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14128, 'enUS', 'Beg and tremble before my power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14129, 'enUS', 'They call you the Fel Horde\'s elite? Yet, you still tremble in my presence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14130, 'enUS', 'Beg for your pitiful life!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14131, 'enUS', 'You are nothing to me. ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14132, 'enUS', 'Run, coward, run!   ', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14133, 'enUS', 'Flee, peon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14134, 'enUS', 'Fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14135, 'enUS', 'That\'s enough. Now back off!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14136, 'enUS', 'It hurt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (14137, 'enUS', 'Augh! No more hurt!', '', 26124);
