INSERT INTO `broadcast_text_locale` VALUES (21699, 'enUS', 'Valiant effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21700, 'enUS', 'Please. Show Razia your weak little concoctions if you must. Return to me when you can handle more... potent fare.', 'Please. Show Razia your weak little concoctions if you must. Return to me when you can handle more... potent fare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21701, 'enUS', 'A glorious kill!', '', 26124);
