INSERT INTO `broadcast_text_locale` VALUES (21706, 'enUS', 'I know you by your stench, little $n.  Here, in my perch, is where your bones shall finally rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21707, 'enUS', 'You wish to kill me? You first!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21709, 'enUS', 'The Forsaken brew such intriguing and strong concoctions, don\'t you think? It comes from an inability to poison oneself with failures, I believe. Luckily for you, Doctor Herbert Halsey in the Undercity has already done the necessary research; all that remains is for you to visit him.', 'The Forsaken brew such intriguing and strong concoctions, don\'t you think? It comes from an inability to poison oneself with failures, I believe. Luckily for you, Doctor Herbert Halsey in the Undercity has already done the necessary research; all that remains is for you to visit him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21710, 'enUS', 'I tire of interruptions from the insignificant.  Your time has come, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21711, 'enUS', '', 'Flee or die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21712, 'enUS', '', 'My work is done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21713, 'enUS', '', 'As it should be.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21714, 'enUS', 'The Skyguard shall pay for your temerity, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21715, 'enUS', '', 'Destiny... awaits.', 26124);
