INSERT INTO `broadcast_text_locale` VALUES (21777, 'enUS', '', 'I wasn\'t finished.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (21778, 'enUS', 'Their magics are meaningless to you, $n!', '', 26124);
